<?phpnamespace App\Http\Controllers;use App\Mail\SendingMailToCustomer;use App\Mail\SendingPhoneToMe;use Illuminate\Http\Request;use Illuminate\Support\Facades\Mail;use App\Mail\SendingMailToMe;use Illuminate\Support\Facades\Validator;class MailController extends Controller{    public function sendMail(Request $request){        $validator = Validator::make($request->all(), [            'name' => 'required',            'email'=>'required|email',        ],[            'name.required' =>  mb_convert_encoding('Mời Quý Khách Nhập Tên Liên Hệ', 'UTF-8', 'UTF-8'),            'email.required' =>  mb_convert_encoding('Mời Quý Khách Nhập Email', 'UTF-8', 'UTF-8'),            'email.email' =>  mb_convert_encoding('Mời Quý Khách Nhập Nhập Đúng Định Dạng Email', 'UTF-8', 'UTF-8'),        ]);        if ($validator->fails()) {            return response()->json([                'success' => false,                'validator'=>$validator->errors()            ]);        }        Mail::send(new SendingMailToMe());        Mail::send(new SendingMailToCustomer());        return response()->json([            'success' => true        ]);    }}