INSERT INTO `posts` (`id`, `title`, `path`, `description`, `content`, `image`, `post_type`, `is_active`, `category_item_id`, `user_id`, `created_at`, `updated_at`, `seo_id`) VALUES
(1, 'Giới Thiệu', 'gioi-thieu', NULL, NULL, 'images/uploads/images/gt_1.jpg', 0, 1, NULL, 1, '2018-11-26 02:42:50', '2018-11-26 02:44:53', NULL),
(2, 'Dịch Vụ Kế Toán Trọn Gói', 'dich-vu-ke-toan-tron-goi', '<p><strong>Dịch vụ kế toán trọn gói uy tín chuyên nghiệp giá rẻ</strong></p>\r\n\r\n<p><em>Dịch vụ kế toán trọn gói, một phải pháp hoàn hảo cho doanh nghiệp với chi phí chỉ từ&nbsp;<strong>500.000-1.500.000 VNĐ</strong>. Mọi khó khăn, vướng mắc sẽ được giải quyết một cách triệt để nhất.</em></p>', '<h1><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"color:#003399;\"><span style=\"font-size:18px;\"><strong>DỊCH VỤ KẾ TOÁN </strong></span></span><span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><strong>TRỌN GÓI</strong></span></span></span></h1>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/ketoantrongoi/img20161115095622-768x576_560552.jpg\" style=\"width: 68%; height: 68%;\" /></p>\r\n\r\n<p><strong>Dịch vụ kế toán trọn gói uy tín chuyên nghiệp giá rẻ</strong></p>\r\n\r\n<p><em>Dịch vụ kế toán trọn gói, một phải pháp hoàn hảo cho doanh nghiệp với chi phí chỉ từ.</em></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:#ff0000;\"><span style=\"font-size:22px;\"><em>&nbsp;</em><strong>500.000-1.500.000 VNĐ</strong>. </span></span></p>\r\n\r\n<p><em>Mọi khó khăn, vướng mắc sẽ được giải quyết một cách triệt để nhất.</em></p>\r\n\r\n<h1><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><strong>Lý do doanh nghiệp nên lựa chọn dịch vụ kế toán trọn gói</strong></span></span></span></h1>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Doanh nghiệp bạn là công ty mới thành lập, chưa có nhân viên kế toán,kế toán trưởng và đang cần những chuyên viên kế toán có trình độ, năng lực, kinh nghiệm để thực hiện các nghiệp vụ kế toán, chứng từ, làm sổ sách kế toán, kê khai, báo cáo thuế&hellip;Tư vấn kế toán, tư vấn các quy định thuế có liên quan</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Công ty bạn là doanh nghiệp vừa và nhỏ và bạn không cần có kế toán, kế toán trưởng chuyên nghiệp để tiết kiệm chi phí</p>\r\n\r\n<p>Bạn là chủ doanh nghiệp,là người đại diện theo pháp luật cho doanh nghiệp nhưng lại không am hiểu nhiều về kế toán, các quy định về pháp luật thuế thì làm sao bạn có thể kiểm tra, đánh giá được nhân viên kế toán, kế toán trưởng của mình, cũng như công việc, báo cáo mà họ làm liệu đã đúng quy định chưa ?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/ketoantrongoi/ke-toan-tron-goi1_786200.png\" style=\"width: 68%; height: 68%;\" /></p>\r\n\r\n<h2><span style=\"color:#ff0000;\"><span style=\"font-size:18px;\"><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><strong>Công việc chúng tôi làm ở gói dịch vụ kế toán trọn gói</strong></span></span></span></h2>\r\n\r\n<p><span style=\"color:#003399;\"><strong>I/ Kê khai thuế trong dịch vụ kế toán trọn gói:</strong></span></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1/ Khai thuê môn bài hàng năm<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;2/ Lập báo cáo thuế hàng tháng, quý gửi cơ quan thuế<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;3/ Lập báo cáo quyết toán thuế TNDN năm<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;4/ Lập báo cáo quyết toán thuế TNCN năm<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;5/ Lập báo cáo quyết toán các loại thuế liên quan khác<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;6/ Lập báo cáo tài chính<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;7/ Thông báo cho doanh nghiệp số tiên thuế phải nộp khi có phát sinh</p>\r\n\r\n<p><span style=\"color:#003399;\"><strong>II/ Các nghiệp vụ kế toán</strong></span></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;1/ Phân loại, sắp xếp, đóng chứng từ kế toán<br />\r\n&nbsp; &nbsp; &nbsp; 2/ Định khoản các nghiệp vụ kế toán phát sinh<br />\r\n&nbsp; &nbsp; &nbsp; 3/ Kiểm tra các chứng từ đầu vào, đầu ra cho phù hợp với quy định của pháp luật<br />\r\n&nbsp; &nbsp; &nbsp; 4/ Hạch toán và ghi sổ sách các chứng từ<br />\r\n&nbsp; &nbsp; &nbsp; 5/ Lập và in các loại sổ sách kế toán theo qui định<br />\r\n&nbsp; &nbsp; &nbsp; 6/ Đứng tên nhân viên làm kế toán cho doanh nghiệp<br />\r\n&nbsp; &nbsp; &nbsp; 7/ Bổ nhiệm, đứng tên kế toán trưởng cho doanh nghiệp<br />\r\n&nbsp; &nbsp; &nbsp; 8/ Tư vấn, giải trình thuế</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; 9/ Hỗ trợ tư vấn về lao động, bảo hiểm xã hội, bảo hiểm y tế<br />\r\n&nbsp; &nbsp; &nbsp; 10/ Tư vấn kế toán, thuế và hóa đơn chứng từ cho doanh nghiệp trong nội dung và phạm vi công việc thực hiện<br />\r\n&nbsp; &nbsp; &nbsp; 11/ Làm việc với cơ quan thuế khi có yêu cầu<br />\r\n&nbsp; &nbsp; &nbsp; 12/ Làm việc với công ty kiểm toán<br />\r\n&nbsp; &nbsp; &nbsp; 13/ Làm việc và giải trình với cơ quan thuế trong phạm vi công việc đã thực hiện<br />\r\n&nbsp; &nbsp; &nbsp; 14/ Giao nhận chứng từ và nộp các báo cáo<br />\r\n&nbsp; &nbsp; &nbsp; 15/ Nhận các chứng từ từ khách hàng.<br />\r\n&nbsp; &nbsp; &nbsp; 16/Nộp báo cáo thuế cho cơ quan thuế<br />\r\n&nbsp; &nbsp; &nbsp; 17/Báo cáo tình hình sử dụng hóa đơn</p>\r\n\r\n<p><span style=\"color:#003399;\"><strong>Lợi ích của doanh nghiệp khi sử dụng dịch vụ kế toán trọn gói</strong></span></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Có ngay đội ngũ kế toán trưởng, chuyên viên có trình độ, trên 10 năm kinh nghiệm đảm đương về kế toán cho doanh nghiệp với giá chỉ 500.000-1.500.000đ. Bạn biết đấy bây giờ để thuê một kế toán có trình độ như vậy thì mức lương của họ không dưới 4 triệu/tháng chưa tính kế toán trưởng.<br />\r\nKhông chỉ đơn thuần là làm dịch vụ kế toán trọn gói hàng tháng cho doanh nghiệp mà Công ty dịch vụ kế toán&nbsp;<strong>Nguyễn Thắng</strong>&nbsp;còn chịu trách nhiệm pháp lý về những việc mà chúng tôi đã thực hiện cho doanh nghiệp: Giải trình trước cơ quan thuế khi cơ quan thuế xuống kiểm tra ngay cả khi đã chấm dứt hợp đồng với Doanh nghiệp.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Công ty hoàn toàn chịu trách nhiệm với những gì đã làm.&nbsp;Luôn cập nhật kịp thời về luật và chế độ kế toán hiện hành mang lại lợi ích thiết thực cho doanh nghiệp. (Đặc biệt, chúng tôi phục vụ hàng trăm doanh nghiệp nên có nhiều kinh nghiệm thực tế).<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Bằng tất cả sự tận tâm, chuyên nghiệp và thái độ phục vụ tốt nhất, chúng tôi đã và đang là nhà cung cấp dịch vụ uy tín cho hàng trăm khách hàng lớn, nhỏ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/ketoantrongoi/images-4_372981.jpg\" style=\"width: 68%; height: 68%;\" /></p>', NULL, 1, 1, NULL, 1, '2018-11-26 03:07:10', '2018-11-26 04:03:32', NULL),
(3, 'Đăng Ký Hồ Sơ Ban Đầu, Đặt In Và Phát Hành Hóa Đơn', 'dang-ky-ho-so-ban-dau-dat-in-va-phat-hanh-hoa-don', '<p style=\"line-height:1.5;\"><strong><span style=\"font-size:16px;\">ĐĂNG KÝ HỒ SƠ THUẾ BAN ĐẦU, ĐẶT IN VÀ PHÁT HÀNH HÓA ĐƠN</span></strong></p>\r\n\r\n<p style=\"line-height:1.4;\"><span style=\"font-size:16px;\"><em>Sau khi được cấp giấy phép, mã số thuế và con dấu tròn, quý doanh nghiệp phải thiết lập hồ sơ thuế ban đầu nộp lên chi cục thuế quận, huyện nơi đặt trụ sở&nbsp;thành lập công ty.</em></span></p>', '<h1><span style=\"font-size:16px;\"><strong><span style=\"color:#ff0000;\">ĐĂNG KÝ HỒ SƠ THUẾ BAN ĐẦU, ĐẶT IN VÀ PHÁT HÀNH HÓA ĐƠN</span></strong></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Sau khi được cấp giấy phép, mã số thuế và con dấu tròn, quý doanh nghiệp phải thiết lập hồ sơ thuế ban đầu nộp lên chi cục thuế quận, huyện nơi đặt trụ sở&nbsp;thành lập công ty.</em></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><em><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/dangkyhosothuebandau/thiet-lap-ho-so-khai-thue-ban-dau_733636.png\" style=\"width: 320px;\" /></em></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\"><strong><u>THÀNH PHẦN HỒ SƠ:</u></strong><br />\r\n	1. Quyết định bổ nhiệm giám đốc.<br />\r\n	2. Quyết định bổ nhiệm kế toán.<br />\r\n	3. Đăng ký hình thức kế toán, phương pháp khấu hao tài sản cố định.<br />\r\n	4. Giấy tờ hợp lệ về việc sử dụng mặt bằng nơi đặt trụ sở công ty (như: HĐ thuê, mượn nhà, giấy tờ sở hữu nhà&hellip;)<br />\r\n	5. Tờ khai thuế môn bài.<br />\r\n	6. Đăng ký đặt in và phát hành hóa đơn (nếu có nhu cầu sử dụng hóa đơn)</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Liên lạc với chúng tôi bằng cách nào ?</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Hãy liên lạc khi bạn gặp sự cố cần hỗ trợ, mặc dù bạn có thể không sử dụng dịch vụ của chúng tôi nhưng chúng tôi vẫn sẵn sàng tư vấn tận tình như những người bạn thân thiết.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đừng chần chừ nhé.</span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 03:10:03', '2018-11-26 04:11:41', NULL),
(4, 'Dịch Vụ Quyết Toán Thuế', 'dich-vu-quyet-toan-thue', '<h1><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong><em>&nbsp;Quyết toán thuế là công việc bắt buộc của một doanh nghiệp sau một thời gian thành lập với mục đích là truy thu thuế TNDN, thuế GTGT, thuế TNCN của doanh nghiệp tuy nhiên để thực hiện một cách chính xác, kịp thời các nghiệp vụ trên không phải doanh nghiệp nào cũng làm được.</em></span></h1>', '<h1><span style=\"color:#ff0000;\"><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><strong><span style=\"font-size:16px;\">DỊCH VỤ QUYẾT TOÁN THUẾ</span></strong></span></span></h1>\r\n\r\n<h1 style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/ketoantrongoi/img20161115095622-768x576_560552.jpg\" style=\"width: 68%; height: 68%;\" /></span></h1>\r\n\r\n<h1><span style=\"font-size:16px;\"><em>Dịch vụ quyết toán thuế uy tín giá rẻ chuyên nghiệp tại&nbsp;TP.HCM</em></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\">Quyết toán thuế là công việc bắt buộc của một doanh nghiệp sau một thời gian thành lập với mục đích là truy thu thuế TNDN, thuế GTGT, thuế TNCN của doanh nghiệp tuy nhiên để thực hiện một cách chính xác, kịp thời các nghiệp vụ trên không phải doanh nghiệp nào cũng làm được.</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:16px;\">Một số khó khăn doanh nghiệp thường gặp phải khi quyết toán thuế hiện nay</span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1/ Hóa đơn, chứng từ sắp xếp chưa khoa học, mất mát, thiếu sót dẫn tới việc thiếu sự logic giữa các bút toán khi lập các báo cáo tài chính, báo cáo thuế đã nộp</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2/ Sổ sách kế toán, các báo cáo đã lập không trùng khớp với chứng từ thực tế hoặc có nhưng không đúng theo quy định của cơ quan thuế</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3/ Chưa nắm chắc về chuẩn mực, nghiệp vụ kế toán, thiếu nhân sự có trình độ, kinh nghiệm dẫn tới những sai sót trong hệ thống sổ sách, phát sinh nhiều vướng mắc, sai phạm về thuế gây thiệt hại kinh tế cho doanh nghiệp</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4/ Trường hợp cơ quan thuế kiểm tra&nbsp;<a href=\"https://ketoanuytin.com/d%E1%BB%8Bch-v%E1%BB%A5-quyet-toan-thue/\"><strong>quyết toán thuế</strong></a>&nbsp;của doanh nghiệp phát hiện ra việc&nbsp;<a href=\"https://ketoanuytin.com/d%E1%BB%8Bch-v%E1%BB%A5-quyet-toan-thue/\"><strong>khai báo thuế</strong></a>,&nbsp;<a href=\"http://ketoangiare.vn/dich-vu/dich-vu-quyet-toan-thue-24.html\"><strong>quyết toán thuế</strong></a>&nbsp;của doanh nghiệp không chính xác thì sẽ bị xử phạt theo quy định của pháp luật. Nhiều doanh nghiệp đã vi phạm pháp luật do không nắm, không biết các quy định của các luật thuế hoặc hiểu không đúng quy định.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5/ Với &nbsp;10 năm kinh nghiệm và uy tín của một công ty hàng đầu làm về dịch vụ kế toán-thuế chúng tôi thấu hiểu được những khó khăn mà doanh nghiệp đang gặp phải. Gói&nbsp;<a href=\"https://ketoanuytin.com/d%E1%BB%8Bch-v%E1%BB%A5-quyet-toan-thue/\"><strong>dịch vụ quyết toán thuế</strong></a>ra đời với sứ mệnh giải quyết một cách triệt để, nhanh chóng, kịp thời các vấn đề liên quan tới quyết toán thuế-sổ sách kế toán trên mọi loại hình doanh nghiệp. Chúng tôi sẽ thay mặt bạn thực hiện tất cả các công việc của một người kế toán, giao dịch, giải trình trước cơ quan thuế khi họ kiểm tra.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/dichvuquyettoanthue/ty-le_580901.png\" style=\"width: 320px;\" /></span></p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<h2><span style=\"font-size:16px;\"><span style=\"color:#ff0000;\">Nội dung thực hiện các công việc trong dịch vụ quyết toán thuế</span></span></h2>\r\n\r\n<h2><span style=\"font-size:16px;\">1. Phân loại, kiểm tra chứng từ trước khi quyết toán thuế</span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 1/ Phân loại, kiểm tra tính hợp lý hợp lý. Phân loại, kiểm tra chứng từ trước khi quyết toán thuế của chứng từ kế toán.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 2/ Nhận chứng từ của doanh nghiệp về để thực hiện công việc.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 3/ Phân loại chứng từ hợp lý, hợp lệ theo quy định tránh rủi ro khi quyết toán thuế.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 4/ Lập các chứng từ kế toán theo quy định của Bộ Tài Chính</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 5/ Lập các chứng từ Nhập, Xuất, Thu, Chi căn cứ từ chứng từ gốc.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 6/ Lập các chứng từ ngân hang liên quan, các phiếu kế toán khác.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 7/ Lập các văn bản, giấy tờ, hợp đồng thủ tục hành chính để làm chặt chẽ tính hợp lệ của chứng từ gốc.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 8/ Lưu giữ bộ chứng từ hoàn chỉnh đảm bảo quyết toán thuế.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:16px;\">2. Thiết lập hệ thống sổ sách</span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 1/ Lập hệ thống các loại sổ chi tiết: Hàng hóa, công nợ, phân bổ, khấu hao, lương&hellip;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 2/ Lập hệ thống sổ cái các tài khoản.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 3/ Lập sổ Nhật ký chung.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 4/ In bộ sổ sách chứng từ gồm:</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 5/ Bảng cân đối số phát sinh, Bảng cân đối kế toán,</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 6/ Báo cáo kết quả hoạt động sản xuất kinh doanh, Báo cáo lưu chuyển tiền tệ,</span></li>\r\n</ul>\r\n\r\n<h4><span style=\"font-size:16px;\">3. Cân đối lãi, lỗ, phân tích rủi ro</span></h4>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 1/ Phân tích các rủi ro thuế, kế toán của doanh nghiệp trong quá trình vận hành giao dịch chứng từ kinh doanh, nhằm phòng tránh tối đa thất thoát cho doanh nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 2/ Cân đối hàng hóa, các loại chi phí, lợi nhuận đảm bảo kế hoạch và quyền lợi của doanh nghiệp.</span></li>\r\n</ul>\r\n\r\n<h4><span style=\"font-size:16px;\">4. Hoàn thiện báo cáo, hồ sơ</span></h4>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 1/ Lập hồ sơ khai thuế GTGT: Tờ khai thuế, báo cáo tình hình sử dụng hóa đơn, quyết toán báo cáo tình hình sử dụng hóa đơn.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 2/ Lập hồ sơ khai thuế TNCN: Tờ khai thuế TNCN theo tháng, quý; tờ khai quyết toán thuế TNCN năm.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 3/ Lập quyết toán thuế TNDN: Tờ khai tạm tính quý, quyết toán năm.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 4/ Lập báo cáo tài chính năm.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 5/ Thanh tra thực tế: Ngồi làm việc, giải trình trực tiếp với thanh tra thuế khi quyết toán thực tế.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Thay vì phải bỏ ra một khoản tiền lớn để nộp phạt cho những lỗi sai không đáng có. Nay đã có gói dịch vụ quyết toán thuế, doanh nghiệp bạn chỉ phải bỏ từ 500.000 &ndash; 1.500.000 đ/ 1 tháng, Công ty bạn có thể hoàn toàn an tâm hoạt động kinh doanh và phát triển doanh nghiệp mà không phải lo lắng bất kỳ một vấn đề gì liên quan đến kế toán &ndash; thuế.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/dichvuquyettoanthue/hoan-thanh-nhanh-chong_801715.jpg\" style=\"width: 50%; height: 50%;\" /></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 03:10:24', '2018-11-26 04:19:24', NULL),
(5, 'Dịch Vụ Kế Toán Thuế', 'dich-vu-ke-toan-thue', '<p><span style=\"font-size:16px;\">&nbsp;Nhân lực kế toán thay đổi thường xuyên do kế toán nhảy việc hay trình độ, năng lực không đáp ứng được nhu cầu và bỏ lại sau lưng chủ doanh nghiệp là những báo cáo, sổ sách quá sơ sài, không đầy đủ, hóa đơn chứng từ đang lộn xộn. thất lạc. Bạn sẽ gặp vô số rắc rối khi cơ quan thuế kiểm tra.</span></p>', '<h1><span style=\"color:#ff0000;\"><strong><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"font-size:18px;\">DỊCH VỤ KẾ TOÁN THUẾ</span></span></strong></span></h1>\r\n\r\n<h1><span style=\"font-size:16px;\"><strong>Thực trạng chung vấn đề kế toán hiện nay ở những doanh nghiệp vừa và nhỏ</strong></span></h1>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;Nhân lực kế toán thay đổi thường xuyên do kế toán nhảy việc hay trình độ, năng lực không đáp ứng được nhu cầu và bỏ lại sau lưng chủ doanh nghiệp là những báo cáo, sổ sách quá sơ sài, không đầy đủ, hóa đơn chứng từ đang lộn xộn. thất lạc. Bạn sẽ gặp vô số rắc rối khi cơ quan thuế kiểm tra.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; Theo quy định của pháp luật doanh nghiệp phải tổ chức bộ máy kế toán, và phải bố trí người làm kế toán, kế toán trưởng. Nếu không bố trí người làm kế toán<strong>,</strong>&nbsp;kế toán trưởng thì phải ký hợp đồng với công ty&nbsp;<a href=\"https://ketoanuytin.com/d%E1%BB%8Bch-v%E1%BB%A5-ke-toan-thue-2/\"><strong>dịch vụ kế toán thuế</strong></a>&nbsp;chuyên nghiệp để thuê làm kế toán, kế toán trưởng.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://ketoangiare.vn/public/upload/ckeditor/dvkt.jpg\" /></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;&nbsp;Bạn là doanh nghiệp vừa và nhỏ, bạn không cần thuê nhân viên kế toán, kế toán trưởng nhằm giảm chi phí hoạt động</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; Bạn là chủ doanh nghiệp nhưng lại không am hiểu nhiều về kế toán-thuế thì làm thế nào bạn có thể kiểm tra, đánh giá nhân viên kế toán của mình, cũng như các&nbsp;<strong><a href=\"https://ketoanuytin.com/d%E1%BB%8Bch-v%E1%BB%A5-bao-cao-thue/\">báo cáo thuế</a></strong>,&nbsp;sổ sách, chứng từ mà họ làm liệu đã đúng quy định chưa</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; Doanh nghiệp sẽ rơi vào tình trạng rối bời, trả giá đắt bằng tiền cùng với sự phiền phức không mong muốn. Giá trị nộp phạt có thể lên tới vài chục hay vài trăm triệu.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; Là một công ty chuyên làm&nbsp;<strong><a href=\"https://ketoanuytin.com/d%E1%BB%8Bch-v%E1%BB%A5-ke-toan-thue-2/\">dịch vụ kế toán thuế</a></strong>&nbsp;uy tín tại Hồ Chí Minh&nbsp;với nhiều năm kinh nghiệm&nbsp;chúng tôi hiểu rõ doanh nghiệp bạn đang cần gì và chúng tôi biết phải làm gì.</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/ketoantrongoi/img20161115095622-768x576_560552.jpg\" style=\"width: 68%; height: 68%;\" /></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>', NULL, 1, 1, NULL, 1, '2018-11-26 03:20:57', '2018-11-26 04:21:35', NULL),
(6, 'Dịch Vụ Báo Cáo Thuế', 'dich-vu-bao-cao-thue', '<p><span style=\"font-size:16px;\">Doanh nghiệp bạn là công ty mới thành lập, chưa có nhân viên kế toán, kế toán trưởng và đang cần tuyển nhân viên kế toán để giải quyết những vấn đề liên quan đến thủ tục&nbsp;<strong>thuế-báo cáo thuế</strong>, hóa đơn , chứng từ, sổ sách kế toán.</span></p>', '<h1><span style=\"color:#ff0000;\"><strong><span style=\"font-size:18px;\">DỊCH VỤ BÁO CÁO THUẾ</span></strong></span></h1>\r\n\r\n<h1><span style=\"font-size:16px;\">Dịch vụ báo cáo thuế hàng tháng uy tín giá rẻ</span></h1>\r\n\r\n<h2><span style=\"font-size:16px;\"><em>Dịch vụ báo cáo thuế một trong những dịch vụ thủ tục thuế uy tín chuyên nghiệp của Công ty dịch vụ kế toán&nbsp;Nguyễn Thắng</em></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">Doanh nghiệp bạn là công ty mới thành lập, chưa có nhân viên kế toán, kế toán trưởng và đang cần tuyển nhân viên kế toán để giải quyết những vấn đề liên quan đến thủ tục&nbsp;<strong>thuế-báo cáo thuế</strong>, hóa đơn , chứng từ, sổ sách kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Bạn là doanh nghiệp vừa và nhỏ, không cần&nbsp; nhân viên kế toán, kế toán trưởng để thực hiện các công việc kế toán, báo cáo thuế hàng tháng để giảm thiểu chi phí hoạt động</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Bạn là chủ doanh nghiệp là người đại diện theo pháp luật cho doanh nghiệp nhưng bạn lại không am hiểu nhiều về kế toán, vậy làm sao để bạn kiểm tra kế toán của mình, cũng như các công việc mà họ đã và đang làm. Công việc mà họ làm liệu đã đúng và phù hợp với các quy định hiện hành chưa ?</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Vậy, giải pháp nào là tối ưu nhất cho những băn khoăn, khó khăn hay sự không yên tâm trên.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/dichvubaocaothue/ke-khai-thue_829383.jpg\" style=\"width: 320px;\" /></span></p>\r\n\r\n<p><strong>&nbsp;<span style=\"font-size:16px;\">Dịch vụ báo cáo thuế</span></strong><span style=\"font-size:16px;\">&nbsp;uy tín chuyên nghiệp, chính xác, hiệu quả, kịp thời của Công ty dịch vụ kế toán<strong>Nguyễn Thắng</strong>&nbsp;sẽ giúp bạn giải quyết một các triệt để nhất mọi khó khăn, vướng mắc trên với chi phí chỉ.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>500.000 &ndash;&nbsp;1.500.000đ/tháng</strong></span></span></p>\r\n\r\n<h2><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"color:#ff0000;\"><span style=\"font-size:16px;\"><strong>Lý do bạn chọn dịch vụ báo cáo thuế của Công ty chúng tôi</strong></span></span></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1/ &nbsp;Kế toán + Thuế là tất cả những gì chúng tôi làm</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;2/ Nguồn nhân lực làm dịch vụ cho doanh nghiệp bạn là những kế toán trưởng, chuyên viên, nhà quản lý có trình độ, kinh nghiệm nghề nghiệp trên dưới 10 năm.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 3/ Uy tín, chất lượng đã được khẳng định với những giải thưởng sản phẩm dịch vụ chất lượng</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 4/ Dịch vụ kế toán trải rộng khắp các địa bàn&nbsp;Tp.HCM</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 5/ Hỗ trợ nhiều phần mềm kế toán &ndash; Năng động, Misa, FTS, IBOSS, BIT, CNS.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 6/ Cam kết bảo mật dữ liệu khách hàng. (Chính sách bảo mật)</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 7/ Tiệt kiệm tối đa chi phí kế toán. (Chỉ 500.000-1.500.000Đ/ tháng)</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 8/ Luôn cập nhật thông tin mối nhất về chính sách pháp luật kế toán thuế.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 9/ Dịch vụ hỗ trợ khách hàng nhanh chóng</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;10/ &nbsp;Loại hình công ty khác nhau không phải là vấn đề với chúng tôi.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 11/ Luôn thu hút và giữ lại những nhân tài kế toán cho mình</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 12/ Quản lý cấp cao luôn hỗ trợ bạn bất kì khi nào bạn cần</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; 13/ 10&nbsp;năm kinh nghiệm</strong>&nbsp;giúp chúng tôi hiểu rõ được những khó khăn của bạn</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:16px;\"><strong>Nội dung chúng tôi làm ở gói dịch vụ báo cáo thuế hàng tháng</strong></span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;1/ Hàng tháng nhận chứng từ, hóa đơn từ phía doanh nghiệp</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 2/ Kê khai các hóa đơn đầu vào, đầu ra lên tờ khai thuế trên phần mềm hỗ trợ kê khai</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 3/ Xử lý, sắp xếp, phân loại, lưu giữ chứng từ</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 4/ Thực hiện các nghiệp vụ kế toán,&nbsp;<strong>làm báo cáo thuế</strong>&nbsp;tháng, quý, cuối năm cho doanh nghiệp</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;5/ Tối ưu chi phí, giảm thiểu tối đa khoản thuế mà doanh nghiệp phải nộp cho nhà nước</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;6/ Nôp các báo cáo cho cơ quan thuế theo thời hạn quy định</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;7/ Thay mặt doanh nghiệp quyết toán thuế khi có yêu cầu từ cơ quan thuế</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;8/ Trả lại tờ khai hàng tháng cho doanh nghiệp để kiểm tra đối chiếu (Tờ khai này là cơ sở quan trọng để quyết toán thuế cuối năm khi có yêu cầu từ cơ quan thuế)</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;9/ Tư vấn cho doanh nghiệp những tồn đọng trong hệ thống kế toán, đưa ra phương hướng khắc phục, giải quyết những tồn đọng trên</span>\r\n	<h3><span style=\"font-size:16px;\"><strong>Lợi ích khi doanh nghiệp bạn sử dụng dịch vụ báo cáo thuế của chúng tôi</strong></span></h3>\r\n\r\n	<p><span style=\"font-size:16px;\"><strong>Chất lượng</strong></span></p>\r\n\r\n	<ul>\r\n		<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;Cung cấp cho quý khách hàng các&nbsp;<strong>báo cáo thuế</strong>&nbsp;chính xác, kịp thời, nhanh chóng hàng tháng, quý, năm, hay bất kì thời điểm nào khi quý khách hàng yêu cầu.</span></li>\r\n	</ul>\r\n\r\n	<p><span style=\"font-size:16px;\"><strong>Chi phí:</strong></span></p>\r\n\r\n	<ul>\r\n		<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Chi phí&nbsp;<strong>dịch vụ báo cáo thuế</strong>&nbsp;của chúng tôi luôn thấp hơn nhiều lần so với việc thuê nhân viên kế toán có đủ trình độ để thực hiện</span></li>\r\n		<li>&nbsp;</li>\r\n	</ul>\r\n\r\n	<p><span style=\"font-size:16px;\"><strong>Tư vấn, hỗ trợ</strong></span></p>\r\n\r\n	<ul>\r\n		<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1/ Tư vấn, giải đáp mọi thắc mắc cho quy khách hàng các vấn đề liên quan, pháp luật thuế,hóa đơn mới&hellip;</span></li>\r\n		<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 2/ &nbsp;Thay mặt quý khách hàng giải trình trước cơ quan thuế khi cơ quan thuế kiểm tra ngay cả khi chúng tôi đã kết thúc hợp đồng với khách hàng</span></li>\r\n	</ul>\r\n\r\n	<p><span style=\"font-size:16px;\"><em><strong>CÓ TRÁCH NHIỆM GIẢI TRÌNH TRỰC TIẾP KHI QUYẾT TOÁN THUẾ THỰC TẾ.</strong></em></span></p>\r\n	</li>\r\n</ul>', NULL, 1, 1, NULL, 1, '2018-11-26 03:21:48', '2018-11-26 04:24:20', NULL),
(7, 'Dịch Vụ Làm Báo Cáo Tài Chính Cuối Năm', 'dich-vu-lam-bao-cao-tai-chinh-cuoi-nam', NULL, '<h1><span style=\"color:#ff0000;\"><strong><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"font-size:18px;\">DỊCH VỤ LÀM BÁO CÁO TÀI CHÍNH CUỐI NĂM</span></span></strong></span></h1>\r\n\r\n<h1 style=\"text-align: center;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/ketoantrongoi/img20161115095622-768x576_560552.jpg\" style=\"width: 68%; height: 68%;\" /></h1>\r\n\r\n<h1><span style=\"font-size:16px;\"><em>Dịch vụ làm báo cáo tài chính cuối năm uy tín giá rẻ&nbsp;</em></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\">Dịch vụ làm báo cáo tài chính uy tín, chuyên nghiệp, hiệu quả, hỗ trợ 24/7. Doanh nghiệp không còn phải lo lắng tới công tác làm báo cáo tài chính cuối năm nữa, yên tâm phát triển kinh doanh.</span></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;Nhằm giảm thiểu chi phí do các nghiệp vụ chưa phát sinh nhiều khi doanh nghiệp mới thành lập nên các chủ doanh nghiệp thường chọn giải pháp tự mình kê khai thuế tài chính hoặc thuê các kế toán ít kinh nghiệm với chi phí thấp nhất để phục vụ công việc kê khai thuế hàng tháng với cơ quan thuế. Vào cuối năm tài chính các doanh nghiệp mới hoàn thành được khâu kê khai thuế, còn hệ thống sổ sách và lập&nbsp;<strong>báo cáo tài chính</strong>&nbsp;chưa được làm kịp thời hoặc có làm nhưng cũng chưa hoàn thiện được điều này rất nguy hiểm cho các doanh nghiệp khi bị thanh tra, quyết toán thuế&hellip;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Và bạn đang nghĩ tới việc thuê một kế toán giỏi để giải quyết các công việc như&nbsp;<strong>làm báo cáo tài chính</strong>,kê khai thuế tài chính, thiết lập sổ sách, chứng từ, cân đối lãi lỗ&hellip;Chi phí để trả lương cho một kế toán như vậy là bao nhiêu&hellip;? Có thể là&nbsp;<strong>3 triệu, 4 triệu, 5 triệu</strong>&nbsp;và rất có thể con số đó còn lớn hơn thế nữa&hellip;Và còn vô vàn các chi phí phát sinh nữa như: phần mềm kế toán, bàn ghế cho nhân viên,máy in, máy vi tính, văn phòng phẩm&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Hiệu quả mang lại có xứng với chi phí cao&nbsp;như vậy mà bạn bỏ ra không ?</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/dichvubaocaotaichinhcuoinam/dich-vu-lam-bao-cao-tai-chinh_531176.jpg\" style=\"width: 68%; height: 68%;\" /></span></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"font-size:16px;\">Trước vô vàn những câu hỏi, khó khăn như vậy. Giải pháp nào là tối ưu, chính xác, hiệu quả ,chuyên nghiệp nhất cho Doanh nghiệp ?</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Vâng ! Tất cả những khó khăn, trăn trở, vướng mắc của Quý doanh nghiệp sẽ được giải quyết một cách nhanh chóng, chính xác, hiệu quả nhất với chi phí thấp nhất chỉ từ.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:#ff0000;\"><span style=\"font-size:26px;\"><strong>500.000-1.500.000đ&nbsp;</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>.</strong>khi doanh nghiệp sử dụng gói&nbsp;<strong>dịch vụ làm báo cáo tài chính</strong>&nbsp;của Công ty dịch vụ kế toán<strong>&nbsp;<a href=\"https://ketoanuytin.com/gioi-thieu/\">Nguyễn Thắng</a></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Là một trong những công ty hàng đầu chuyên cung cấp dịch vụ kế toán &ndash; kiểm toán, đào tạo kế toán. Với đội ngũ kế toán trưởng, chuyên viên có trên dưới 10 năm kinh nghiệm thực tế đã làm dịch vụ cho hàng trăm công ty, nắm bắt và hiểu rất rõ nghiệp vụ, cùng các chuyên gia về lĩnh vực Thuế, Tài chính, Kế toán. Chúng tôi cam kết cung cấp cho doanh nghiệp của bạn nhân viên kế toán làm việc hiệu quả &ndash; trung thực và nhiệt huyết nhằm mang lại sự hài lòng và tiết kiệm chi phí cho Quý doanh nghiệp!</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"font-size:16px;\"><strong>Công việc chính chúng tôi làm ở gói dịch vụ làm báo cáo tài chính</strong></span></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 1/ Kiểm tra, rà soát, đối chiếu tờ khai thuế hàng tháng với chứng từ gốc (hóa đơn đầu ra, đầu vào&hellip;), thực hiện điều chỉnh ngay nếu phát hiện có sai sót về hóa đơn giả, hóa đơn khống hay lỗi do kê khai sai sót.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2/ Tổ chức sắp xếp hoàn thiện và phân loại chứng từ trước khi lên các sổ sách(sổ chi tiết, tổng hợp, các bảng phân bổ&hellip;) và Báo cáo tài chính.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3/ Hoàn thiện chứng từ cần thiết: Phiếu thu &ndash; chi, phiếu nhập &ndash; xuất&hellip;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;4/ Thông báo cho doanh nghiệp kịp thời các vấn đề về quỹ tiền mặt, ngân hàng, hàng tồn kho, công nợ, chi phí các loại&hellip;.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5/ Tư vấn các chính sách, văn bản về thuế, kế toán và các vấn đề xử phạt thuế khi làm sai để doạnh nghiệp nắm bắt.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;6/ Chỉ ra cho doanh nghiệp những vấn đề tồn đọng trong các vấn đề về kế tóa tài chính năm qua và đưa ra các giải pháp khắc phục</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;7/ Cân đối lãi lỗ, tư vấn các vấn rủi ro cho doanh nghiệp</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;8/ Cân đối doanh thu chi phí&nbsp; &nbsp; &nbsp;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;9/ Cân đối các khoản thuế(GTGT, TNDN, TNCN&hellip;)&nbsp; &nbsp;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;10/ Cân đối hàng hóa, chi phí, lợi nhuận</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;11/ Cân đối dòng tiền cho doanh nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;12/ Chốt số liệu, lập các&nbsp;<strong>báo cáo tài chính</strong>&nbsp;hoàn chỉnh.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;13/ Bộ báo cáo tài chính</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;14/ Quyết toán thuế TNDN</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;15/ Quyết toán thuế TNCN&hellip;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;16/ In sổ sách, chứng từ bàn giao đầy đủ cho doanh nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;17/ Sau khi khảo sát hồ sơ tại doanh nghiệp, chúng tôi sẽ gửi danh mục công việc cần thực hiện cụ thể và báo giá đến Quý khách hàng</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;18/ Đồng thời trong quá trình thực hiện , chúng tôi sẽ tư vấn cho quý công ty các nội nội dung và vấn đề liên quan đến kế toán , luật thuế và hóa đơn chứng từ trong hoạt động kinh doanh .</span></li>\r\n</ul>', NULL, 1, 1, NULL, 1, '2018-11-26 03:22:40', '2018-11-26 04:27:44', NULL);
INSERT INTO `posts` (`id`, `title`, `path`, `description`, `content`, `image`, `post_type`, `is_active`, `category_item_id`, `user_id`, `created_at`, `updated_at`, `seo_id`) VALUES
(8, 'Dịch vụ gỡ rối xổ sách kế toán', 'dich-vu-go-roi-xo-sach-ke-toan', '<p><span style=\"font-size:16px;\">Sắp đến ngày nộp báo cáo năm, anh Minh yêu cầu cô Lan Anh-kế toán công ty,in tất cả các báo cáo để xem trước. Chờ cả ngày không thấy báo cáo đâu, anh sang phòng kế toán thì nhân sự báo cô Lan Anh xin phép nghỉ buổi chiều, gọi điện thoại thì nghe tò tí te. Kiểm tra mail thì anh nhận được thư cô Lan Anh gửi với nội dung</span></p>', '<h1><span style=\"color:#ff0000;\"><strong><span style=\"font-size:18px;\">GỠ RỐI SỔ SÁCH KẾ TOÁN</span></strong></span></h1>\r\n\r\n<h1 style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/ketoantrongoi/images-4_372981.jpg\" style=\"width: 68%; height: 68%;\" /></span></h1>\r\n\r\n<h1><span style=\"font-size:16px;\"><em>Dịch vụ gỡ rối&nbsp;sổ sách kế toán uy tín chuyên nghiệp</em></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Sắp đến ngày nộp báo cáo năm, anh Minh yêu cầu cô Lan Anh-kế toán công ty,in tất cả các báo cáo để xem trước. Chờ cả ngày không thấy báo cáo đâu, anh sang phòng kế toán thì nhân sự báo cô Lan Anh xin phép nghỉ buổi chiều, gọi điện thoại thì nghe tò tí te. Kiểm tra mail thì anh nhận được thư cô Lan Anh gửi với nội dung</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&rdquo; Sếp ạ, em căng thẳng quá, chắc em nghỉ vài ngày rồi em vào làm tiếp. Chứ sổ sách kế toán em chưa làm xong kịp, nhiều vấn đề em không biết xử lý làm sao. Mong anh thông cảm, em hứa sẽ vào hoàn tất sớm&hellip;.&rdquo; đọc đến đây anh Minh như sấm đánh ngang tai. Vậy theo bạn anh Minh đã xử lý thế nào ?</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nhờ một người bạn giới thiệu, anh đã có số điện thoại 028.3816.3247&nbsp; và ngay tức khắc gọi cho chúng tôi, trong vòng 30 phút chúng tôi đã có mặt tại công ty anh để hỗ trợ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Chúng tôi đã tư vấn, gỡ rối các vấn đề của công ty anh Minh để nhanh chóng có được các báo cáo chính xác nhất, không những thế chúng tôi còn đào tạo Cô Lan Anh nhiều nghiệp vụ để cô ấy xử lý khi gặp tính huống mà mình chưa gặp bao giờ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Giờ anh ấy cảm thấy an tâm hơn, lâu lâu anh lại gọi tới 028.3816.3247 không phải là nhờ tư vấn mà giới thiệu cho chúng tôi những khách hàng mới.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Bạn quan tâm về phí dịch vụ ?</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Khách hàng luôn quan tâm về mức phí mà mình phải trả thế nào khi sử dụng&nbsp;<a href=\"http://tuvanacb.com/\">dịch vụ kế toán</a><a href=\"http://tuvanacb.com/\" rel=\"noopener\" target=\"_blank\">.</a>&nbsp;Chắc chắn rằng bằng các công cụ hỗ trợ tìm kiếm trên internet các bạn có thể kiếm được mức giá rẻ nhất mà các bạn đang mong đợi.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Đối với chúng tôi, phí dịch vụ không phải là yếu tố quan trọng nhất trong sự tồn tại và phát triển của công ty. Chúng tôi muốn lắng nghe tâm tư, nguyện vọng của quý khách hàng, sự hỗ trợ mà chúng tôi có thể làm được cho bạn và cung cấp một mức giá phù hợp. Sự tin tưởng của khách hàng cho dịch vụ của chúng tôi là mức phí cao nhất mà chúng tôi nhận được.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/goroixosachketoan/hoan-thien-so-sach-quyet-toan-nam_107793.png\" style=\"width: 300px;\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Hiện nay đa số hệ thống sổ sách kế toán của các Doanh nghiệp không đầy đủ. Thậm chí không có hệ thống sổ sách kế toán. Hoặc hệ thống sổ sách kế toán được lập vờ, không tuân theo các nguyên tắc, các quy định của cơ quan nhà nước ban hành. Số liệu trên sổ sách kế toán không thống nhất với nhau hoặc lệch lạc so với chứng từ, các báo cáo thuế, BCTC, quyết toán thuế đã nộp.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tại sao lại vậy: Do Kế toán non kinh nghiệm không biết thực hiện; Bộ máy Kế toán của Doanh nghiệp không được quan tâm đúng mức mà chỉ trú trọng giải quyết bài toán tình thế:&nbsp;<strong>KHI NÀO CẦN THÌ THUÊ và KHÔNG TÌM HIỂU KỸ ĐỐI TƯỢNG ĐƯỢC THUÊ&nbsp;</strong>dẫn đến thuê phải kế toán thiếu trách nhiệm, trình độ, làm vo số liệu mà không căn cứ thực trạng chứng từ, tài liệu và hoạt động sản xuất kinh doanh thực tế của DN</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Hậu quả: Khi thực hiện quyết toán với cơ quan thuế. Doanh nghiệp bị bóc tách, truy thu thuế dựa trên Doanh thu của Doanh nghiệp mà đáng lẽ nếu Kế toán làm tốt sẽ không phải như vậy.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Khắc phục: Phát hiện kịp thời các sai phạm và thực hiện điều chỉnh kịp thời trước khi cơ quan thuế có quyết định thanh tra.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Là một trong những công ty làm<strong>&nbsp;dịch vụ kế toán</strong>-thuế chúng tôi cam kết bảo vệ quyền lợi khách hàng,cũng như chất lượng&nbsp;<a href=\"http://dichvuketoanthanhxuan.com/i448-dich-vu-don-dep-va-lam-so-sach.html\"><strong>dịch vụ dọn dẹp và làm sổ sách</strong></a>&nbsp;kế toán. Coi công ty khách hàng như là công ty của chính mình .&nbsp;</span></p>\r\n\r\n<h2><span style=\"font-size:16px;\"><strong>D</strong><strong>ịch vụ dọn dẹp sổ sách, kiểm tra, hoàn thiện sổ sách làm những gì?</strong></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;1/ Kế toán&nbsp;<strong>Nguyễn Thắng</strong>&nbsp;sẽ cử cán bộ đến trực tiếp Doanh nghiệp để nhận toàn bộ tài liệu, chứng từ của khách hàng mang về để xử lý công việc.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 2/ Lập thông báo các sai phạm, rủi ro và họp cùng DN để đưa ra phương án, biện pháp điều chỉnh, sửa sai.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:16px;\"><strong>Kiểm tra, xử lý tính hợp lý hợp lệ, hoàn thiện chứng từ kế toán theo pháp luật thuế</strong></span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp;&nbsp;&nbsp; 1/ Hóa đơn đầu vào: Phát hiện hóa đơn sai mục đích sử dụng; Chỉ tiêu; Hóa đơn thuộc DN bỏ trốn; Hóa đơn bị thất lạc; Hóa đơn viết sai&hellip; -&gt; Tư vấn biện pháp khắc phục điều chỉnh, bổ sung để tránh rủi ro xuất toán thuế và chi phí.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 2/ Hóa đơn đầu ra: Phát hiện thất thoát; Hủy không đúng quy định; Không khớp với hồ sơ bán hàng hóa, dịch vụ&hellip; -&gt;Tư vấn biện pháp khắc phục, điều chỉnh, bổ sung để tránh rủi ro bị truy thu thế GTGT, thuế TNDN.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 3/ Chứng từ kế toán: Lập các chứng từ kế toán, các văn bản giấy tờ hành chính theo quy định cho các chứng từ gốc đầu vào, đầu ra.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-size:16px;\"><strong>Kiểm tra, xử lý, hoàn thiện hệ thống sổ sách kế toán đảm bảo quyết toán thuế</strong></span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 1/ Rà soát, lập lại hệ thống các loại sổ chi tiết: Hàng hóa, công nợ, phân bổ, khấu hao, lương&hellip;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 2/ Rà soát, lập lại hệ thống sổ cái các tài khoản: Từ loại 1 đến loại 9.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 3/ Rà soát, lập lại sổ Nhật ký chung.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Kiểm tra thu nhập, giá vốn, chi phí, lãi lỗ và điều chỉnh theo phát sinh hóa đơn, hợp đồng thực tế của DN.</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;1/ Kiểm tra thu nhập: Rà soát lại doanh thu theo các hoá đơn đầu ra nhằm phát hiện sai sót trên hoá đơn gây tăng hoặc giảm DT, các thu nhập khác theo các chứng từ liên quan, kịp thời xử lý và điều chỉnh các sai sót.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;2/ Kiểm tra hang hoá, giá vốn: Rà soát lại kho hang hoá phát hiện thừa thiếu kho; kiểm tra giá vốn hang hoá bán hoặc giá thành SX, CT và xử lý lại tránh xuất toán.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;3/ Kiểm tra các chi phí: Rà soát đảm bảo các chi phí hợp lý, đúng theo tỷ lệ cho phép của cơ quan thuế; Xử lý hoặc cân đối lại các chi phí sao cho chặt chẽ và an toàn nhất.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp;4/ Cân đối lãi, lỗ của DN theo đúng kế hoạch, số đã báo cáo và pháp luật thuế quy định.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Thanh kiểm tra, điều chỉnh và hoàn thiện các báo cáo nộp cơ quan thuế.</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 1/ Rà soát, kiểm tra hồ sơ khai thuế GTGT: Kiểm tra phát hiện từ chứng từ kê khai trên tờ khai thuế, hướng dẫn điều chỉnh tờ khai nếu phát hiện sai sót.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 2/ Rà soát, kiểm tra hồ sơ khai thuế TNDN: Tờ khai thuế TNDN theo quý, năm phát hiện sai sót và điều chỉnh.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 3/ Rà soát, kiểm tra hồ sơ khai thuế TNCN: Tờ khai thuế TNCN theo tháng, quý, năm phát hiện sai sót và điều chỉnh.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; 4/ Rà soát kiểm tra Báo cáo tài chính: Kiểm tra các chỉ tiêu trên Báo cáo so với SSKT và hoàn thiện, điều chỉnh lại chính xác theo quy định. Tư vấn nộp điều chỉnh báo cáo tài chính với cơ quan thuế nếu sai sót quá nhiều.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Giao dịch,tư vấn, giải trình quyết toán trực tiếp với cơ quan thuế</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1/ Cử cán bộ làm việc, giải trình trực tiếp với thanh tra thuế khi quyết toán thực tế..</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2/ Hãy liên hệ với&nbsp;<strong>Nguyễn Thắng</strong>&nbsp;để được chia sẻ và tư vấn nhiều hơn.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Liên lạc với chúng tôi bằng cách nào ?</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Hãy liên lạc khi bạn gặp sự cố cần hỗ trợ, mặc dù bạn có thể không sử dụng dịch vụ của chúng tôi nhưng chúng tôi vẫn sẵn sàng tư vấn tận tình như những người bạn thân thiết.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Đừng chần chừ nhé.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/goroixosachketoan/2247339620599616142371711150953274o-768x576_697102.jpg\" style=\"width: 68%; height: 68%;\" /></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 03:42:02', '2018-11-26 07:31:28', NULL),
(9, 'Dịch vụ kế toán nội bộ', 'dich-vu-ke-toan-noi-bo', NULL, '<h1><span style=\"color:#ff0000;\"><strong><span style=\"font-size:18px;\">DỊCH VỤ LÀM KẾ TOÁN NỘI BỘ</span></strong></span></h1>\r\n\r\n<h1><span style=\"font-size:16px;\">Dịch vụ làm kế toán nội bộ uy tín chuyên nghiệp giá rẻ</span></h1>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp;Thực hiện tất cả các công việc của một người kế toán thuế, giữ vai trò là một nhân viên chính thức của Doanh nghiệp để giao dịch với cơ quan thuế. Có trách nhiệm cao, giải quyết tồn đọng kịp thời, sổ sách hoàn thành đúng thời hạn.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Việc quản trị tốt chi phí hoạt động, doanh thu, lãi lỗ giúp cho doanh nghiệp nắm bắt được tình hình tài chính, kinh doanh của công ty mình. Kinh tế càng ngày càng khó khăn, cạnh tranh gay gắt việc giảm thiểu chi phí là điều tất yếu quyết định sự sống còn của 1 doanh nghiệp và bạn đang nghĩ tới thuê&nbsp;<strong>dịch vụ kế toán nội bộ</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Cán bộ kế toán nội bộ không còn đơn thuần chỉ làm công việc đong đo, đếm, báo cáo thuế, kế toán thuế mà còn được sử dụng rộng rãi trong nhiều hoạt động của doanh nghiệp.&nbsp;<strong>Kế toán nội bộ&nbsp;</strong>ngày nay cần phải hiểu được các lĩnh vực chức năng khác như Marketing, tài chính, kế toán quản trị, ngoại giao&hellip;Bởi vậy để thuê được một nhân viên kế toán như thế quả thật là không dễ dàng chút nào chưa kể bạn phải chi trả một khoản lương tương đối lớn cho nhân viên này.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/dichvuketoannoibo/so-sach1-300x297_874372.png\" style=\"width: 68%; height: 68%;\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Với 10 năm kinh nghiệm làm&nbsp;<strong>dịch vụ kế toán</strong>, chúng tôi thấu hiểu được những khó khăn và nhu cầu về dịch vụ kế toán nội bộ của doanh nghiệp bạn là lớn như thế nào. Với phương châm coi doanh nghiệp khách hàng như doanh nghiệp của mình, chúng tôi đã và đang làm dịch vụ cho hàng trăm công ty lớn, nhỏ. Gói dịch vụ kế toán nội ra đời nhằm giúp doanh nghiệp bạn có cái nhìn chính xác nhất về tình trạng tài chính, kinh doanh, tháo gỡ những khó khăn bộ máy&nbsp;<strong>kế toán nội bộ&nbsp;</strong>qua đó yên tâm phát triển kinh doanh sản xuất.</span></p>\r\n\r\n<h2><span style=\"font-size:16px;\">Công việc chính chúng tôi làm ở dịch vụ kế toán nội bộ</span></h2>\r\n\r\n<p><span style=\"font-size:16px;\">&middot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;1/ Khảo sát bộ máy hoạt động của công ty từ cơ cấu tổ chức đến lĩnh vực hoạt động</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">(Đội ngũ kế toán trưởng của chúng tôi sẽ tới công ty quý doanh nghiệp khảo sát thực tế)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&middot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2/ Xây dựng hệ thống mẫu biểu, báo cáo quản trị và các báo cáo đặc thù khác</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&middot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3/ Hỗ trợ tư vấn và xây dựng hệ thống luân chuyển chứng từ giữa các bộ phận chức năng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&middot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;4/ Xây dựng hệ thống phân tích và lấy số liệu.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&middot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5/ Xây dựng và lên tổng hợp các báo cáo tài chính theo tháng, quý hoặc năm.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&middot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 6/ Đưa ra các đánh giá, nhận xét về doanh thu và quản trị chi phí theo yếu tố&hellip;</span></p>\r\n\r\n<h2><span style=\"font-size:16px;\">Lợi ích tới từ gói dịch vụ kế toán nội bộ</span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1/ Có số liệu chính xác được tổng hợp từ các số liệu chính xác và kịp thời.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 2/ Giảm thiểu, tiết kiệm chi phí cho bộ máy kế toán mà hiệu quả công việc vẫn đảm bảo.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 3/ Được tư vấn và hỗ trợ các chính sách, chế độ kế toán mới.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; 4/ Được hỗ trợ tư vấn trong việc đọc các số liệu, phân tích các số liệu báo cáo quản trị.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Bằng tất cả sự tận tâm, chuyên nghiệp và thái độ phục vụ tốt nhất, chúng tôi đã và đang là nhà cung cấp dịch vụ uy tín cho hàng trăm khách hàng lớn, nhỏ.</strong></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 03:46:52', '2018-11-26 07:31:21', NULL),
(10, 'Dịch vụ kế toán trưởng', 'dich-vu-ke-toan-truong', '<p>Theo quy định của Luật kế toán, khi thành lập các doanh nghiệp phải bố trí ngay người làm kế toán trưởng. Trường hợp khuyết kế toán trưởng thì cấp có thẩm quyền phải bố trí ngay kế toán trưởng mới. Người được thuê làm kế toán trưởng phải bắt buộc đáp ứng được các tiêu chuẩn, quy định của pháp luật kế toán.</p>', '<h1><strong><span style=\"color:#ff0000;\"><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"font-size:18px;\">DỊCH VỤ KẾ TOÁN TRƯỞNG</span></span></span></strong></h1>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/ketoantrongoi/img20161115095622-768x576_560552.jpg\" style=\"width: 68%; height: 68%;\" /></p>\r\n\r\n<p><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"font-size:16px;\"><strong>Dịch vụ kế toán trưởng&nbsp;uy tín chuyên nghiệp giá rẻ</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Theo quy định của Luật kế toán, khi thành lập các doanh nghiệp phải bố trí ngay người làm kế toán trưởng. Trường hợp khuyết kế toán trưởng thì cấp có thẩm quyền phải bố trí ngay kế toán trưởng mới. Người được thuê làm kế toán trưởng phải bắt buộc đáp ứng được các tiêu chuẩn, quy định của pháp luật kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Trong trường hợp không bố trí người làm kế toán trưởng, doanh nghiệp phải ký hợp đồng với công ty dịch vụ kế tóan để thuê làm kế toán, thuê làm kế toán truởng theo quy định của pháp luật.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Quý khách hàng đang cân nhắc lựa chọn một công ty dịch vụ kế toán được thành lập và họat động theo đúng quy định của pháp luật, nhân viên nghiệp vụ có chứng chỉ hành nghề kế toán, hành nghề thủ tục thuế thuế, chứng chỉ kế toán trưởng và đăng ký hành nghề kế toán, thuế theo quy định của pháp luật, nhân viên thực hiện dịch vụ&nbsp;&nbsp;kế toán được giám sát chặt chẽ bởi Bộ tài chính, Hội kế tóan và Kiểm toán hành nghề Việt Nam, tổng cục thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Quý khách là công ty mới thành lập, chưa có nhân viên kế toán, chưa có kế toán trưởng và đang cần chuyên viên kế toán có đủ trình độ, kinh nghiệm, năng lực, bằng cấp để tư vấn, hướng dẫn về hóa đơn, chứng từ, thủ tục kế toán, làm kế toán trưởng và tư vấn các quy định của các luật thuế có liên quan.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Quý khách hàng đang lo lắng, không tin tưởng&nbsp;&nbsp;và không yên tâm về họat động của phòng kế toán, bộ phận kế toán, kế toán trưởng của chính công ty mình.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/dichvuketoantruong/dich-vu-ke-toan-hung-phat_656245.jpg\" style=\"width: 68%; height: 68%;\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Công ty TNHH DV kế toán Nguyễn Thắng&nbsp;</strong>là một là một trong số ít các công ty đáp ứng được các điều kiện để cung cấp dịch vụ kế tóan và tư vấn thuế theo đúng các quy định hiện hành của pháp luật,&nbsp;thực hiện việc đăng ký hành nghề nghề kế tóan, thuế theo đúng Quy định của Bộ tài chính và đã được Hội kế tóan và Kiểm tóan hành nghề Việt Nam xác nhận đủ điều kiện để cung cấp dịch vụ kế tóan, dịch vụ thủ tục thuế, đủ điều kiện cung cấp dịch vụ kế toán trưởng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Nội dung của công việc dịch vụ&nbsp;kế toán trưởng của chúng tôi sẽ bao gồm:</em></strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; 1/ Đứng tên ký kế toán trưởng trên các hồ sơ kế toán của doanh nghiệp;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; 2/ Phân công công việc, đào tạo, huấn luyện nghiệp vụ cho nhân viên kế toán;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; 3/ Trực tiếp thực hiện công việc kế toán của nhân viên kế toán;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; 4/ Kiểm soát, kiểm tra lại tất cả các hồ sơ, chứng từ kế toán, hạch toán, các báo cáo thuế mà nhân viên kế toán đã thực hiện;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; 5/ Hướng dẫn nhân viên kế toán thực hiện công việc kế toán;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; 6/ Tư vấn các luật thuế, các quy định trong kinh doanh cho lãnh đạo doanh nghiệp;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; 7/ Đại diện doanh nghiệp làm việc và giải trình với cơ quan thuế;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; 8/ Tư vấn đầu tư, lập dự án, xây dựng phương án kinh doanh;</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; 9/ Thực hiện các thủ tục về đăng ký lao động, BHXH, BHYT cho doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Với việc lựa chọn&nbsp;<strong>&ldquo;<em>Dịch vụ&nbsp;kế toán trưởng&rdquo;</em></strong>&nbsp;của chúng tôi, chúng tôi sẽ thực hiện từ A đến Z cho quý khách, quý khách sẽ tiết kiệm được rất nhiều thời gian và chi phí, hoạt động của bộ phận kế toán luôn đảm bảo thực hiện theo đúng các quy định của pháp luật.</span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 07:18:56', '2018-11-26 07:31:13', NULL),
(11, 'Dịch vụ BHXH', 'dich-vu-bhxh', '<p>Để hỗ trợ quý doanh nghiệp hoàn thiện các thủ tục theo đúng quy định hiện hành liên quan đến đăng ký lao động, đăng ký thang bảng lương và đóng bảo hiểm xã hội, chúng Tôi xin hân hạnh được phục vụ qúy doanh nghiệp các dịch vụ&nbsp;<em>&ldquo;<strong>Đăng ký lao động, bảo hiểm xã hội</strong>&nbsp;&ldquo;</em><em>,</em>&nbsp;chi tiết như sau :</p>', '<h1><span style=\"font-size:18px;\"><span style=\"color:#ff0000;\"><strong><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\">DỊCH VỤ BHXH</span></strong></span></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>DỊCH VỤ BHXH TẠI TP.HCM</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;Để hỗ trợ quý doanh nghiệp hoàn thiện các thủ tục theo đúng quy định hiện hành liên quan đến đăng ký lao động, đăng ký thang bảng lương và đóng bảo hiểm xã hội, chúng Tôi xin hân hạnh được phục vụ qúy doanh nghiệp các dịch vụ&nbsp;<em>&ldquo;<strong>Đăng ký lao động, bảo hiểm xã hội</strong>&nbsp;&ldquo;</em><em>,</em>&nbsp;chi tiết như sau :</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>I/ Tư vấn các quy định của Pháp luật:</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;1/ Tham khảo và tư vấn về tình hình hiện tại của doanh nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;2/ Lựa chọn phương án tốt nhất để giảm tối đa mức đóng BHXH cho người sử &nbsp;dụng lao động.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;3/ Tư vấn, soạn thảo hợp đồng lao động.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;4/ Tư vấn, soạn thảo nội quy, quy chế lao động.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;5/ Tư vấn, tham gia đàm phán, soạn thảo thoả ước lao động tập thể.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;6/ Tư vấn, tham gia giải quyết các tranh chấp về lao động.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;7/ Tư vấn các vấn đề khác liên quan đến bảo hiểm xã hội, chế độ lao động;</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; 8/&nbsp;Trình tự Đăng ký&nbsp;BHXH, BHYT, BHTN:</strong></span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Đăng ký khai trình lao động</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Đăng ký công đoàn&nbsp;</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Đăng ký hệ thống thang lương.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Đăng ký BHXH, BHYT, BHTN.</span></li>\r\n</ol>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://localhost:8080/ketoan/images/uploads/images/dichvu/dichvubhxh/logo-bao-hiem-xa-hoi-300x300_851042.jpg\" style=\"width: 300px; height: 300px;\" /></span></p>\r\n\r\n<h2><span style=\"font-size:16px;\">&ndash;&nbsp;Trong thời hạn 30 ngày kể từ ngày giao kết hợp đồng lao động, hợp đồng làm việc hoặc tuyển dụng, người sử dụng lao động nộp hồ sơ tham gia bảo hiểm xã hội cho tổ chức bảo hiểm xã hội;&nbsp;<br />\r\n&ndash; Tổ chức bảo hiểm xã hội có trách nhiệm cấp sổ bảo hiểm xã hội trong thời hạn 30 ngày, kể từ ngày nhận được hồ sơ hợp lệ. Trong trường hợp không cấp thì phải trả lời bằng văn bản và nêu rõ lý do.&nbsp;<br />\r\n&ndash; Thời hạn trả lời hồ sơ 30 ngày Cơ quan thanh, kiểm tra&nbsp;<br />\r\n&ndash; Bộ lao động và thương binh xã hội;&nbsp;<br />\r\n&ndash; Uỷ ban nhân dân tại các tỉnh, thành phố.</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>III. Đại diện thực hiện các thủ tục:</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 1/ Đăng ký, theo dõitiến trình xử lý và nhận kết quả khai trình lao động.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2/ Đăng ký, theo dõi tiến trình xử lý và nhận kết quả hồ sơ công đoàn.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 3/ Đăng ký, theo dõi tiến trình xử lý và nhận kết quả hệ thống thang bảng lương.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4/ Đăng ký, theo dõi tiến trình xử lý và nhận kết quả sổ BHXH và thẻ BHYT.</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5/ Cam kết sau khi hoàn tất hồ sơ:</strong></span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Đảm bảo mức đóng BHXH, BHYT, BHTN là thấp nhất.</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Cung cấp văn bản pháp luật có liên quan theo yêu cầu</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Hướng dẫn các thủ tục sau khi nhận sổ BHXH và thẻ BHYT</span></li>\r\n	<li><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Tư vấn miễn phí về các quy định về BHXH, BHYT, BHTN sau khi hoàn tất</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>ĐĂNG KÝ BHXH</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;Bảo hiểm xã hội là việc tạo ra nguồn thu nhập thay thế trong trường hợp nguồn thu nhập bình thường bị gián đoạn đột ngột hoặc mất hẳn, bảo vệ cho những người lao động làm công ăn lương trong xã hội.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Bảo hiểm xã hội bắt buộc áp dụng với các doanh nghiệp có sử dụng người làm việc theo hợp đồng lao động không xác định thời hạn, hoặc hợp đồng lao động có thời hạn từ đủ 3 tháng trở lên, không liên quan đến số người lao động của doanh nghiệp.<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Tổ chức bảo hiểm xã hội cấp tỉnh, thành phố Lệ phí và Thời hạn Không quy định.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>QUY TRÌNH LAO ĐỘNG &ndash; BẢO HIỂM XÃ HỘI&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>CÁC BƯỚC ĐĂNG KÝ LAO ĐỘNG VÀ BHXH</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Để giảm bớt các thủ tục hành chính rườm rà, mất thời gian đi lại và phiền hà gây ra cho các doanh nghiệp. Quy định chuyên cung cấp dịch vụ kế toán, tư vấn tài chính sẽ nhận ủy quyền hoàn tất thủ tục đăng ký bảo hiểm xã hội, bảo hiểm y tế, đăng ký lao động cho các doanh nghiệp trong nước và ngoài nước tại Việt Nam.</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><u>-Bước 1 :</u>&nbsp;<u>Đăng ký Lđộng tại Phòng LĐTBXH (UBND Quận) gồm các hồ sơ sau: &nbsp; 5&nbsp; ngày</u></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Giấy phép ĐKKD : 01 bản sao y công chứng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Bảng khai trình lao động khổ A3.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><u>-Bước 2 :</u><u>&nbsp;</u><u>Thành lập công đoàn (Nếu &gt;=10 LĐ), X/n không đủ điều kiện thành lập công đoàn (Nếu &lt;10 LĐ) : 5 ngày</u></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Giấy phép ĐKKD : 01 bản sao y công chứng</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Bảng khai trình lao động khổ A3 (có x/n Phòng LĐTBXH )</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Bảng xin x/n không có công đoàn(Nếu &lt;10 LĐ)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Bộ hồ sơ xin thành lập công đoàn.(Nếu &gt;=10 LĐ)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><u>-Bước 3 :</u><u>&nbsp;Đăng ký thang bảng lương: 15 ngày</u></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Bảng đăng ký hệ thống thang lương và phụ cấp lương.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Hợp đồng lao động.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Công văn xin đăng ký thang lương</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Bảng chi tiết</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;+ Hợp đồng lao động (chỉ xem không nộp)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em><u>* Nếu &lt; 10 Lđộng:</u></em></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Công văn thống nhất thang bảng lương (Dưới 5 lao động không có công đoàn và cho nhân viên ký vào)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Xác nhận không có công đoàn (do liên đoàn Lđộng Quận cấp ) nếu cty không có Cđoàn, số lượng nhân viên ít dưới 10 LĐ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Quy định tiêu chuẩn từng chức danh trong thang lương.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em><u>* Nếu &gt;= 10 Lđộng:</u></em></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + QĐịnh thành lập CĐ (do liên đoàn Lđộng Quận cấp) nếu cty đủ điều kiện thành lập công đoàn.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Biên bản họp BCH công đoàn v/v thống nhất thang lương (Có công đoàn và trên 10 lao động)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Quy định tiêu chuẩn từng chức danh trong thang lương.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Nội qui Lđộng : gồm 4 bộ và 1 công văn, 1 Quy định ban hành nội qui</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Thỏa ước lao động: gồm 4 bộ và 1 công văn, 1 quy định ban hành nội qui, 1BB lấy ý kiến BCH Cđoàn &amp; 1 quyết định thành lập Cđoàn (do liên đoàn Lđộng Quận cấp).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi có xác nhận của phòng LĐTBXH tiến hành đăng ký BHXH.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><u>-Bước 4 :</u><u>&nbsp;Đăng ký BHXH, BHYT, BHTN gồm các hồ sơ sau:</u></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Giấy phép ĐKKD: 01 bản sao y công chứng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Phiếu đăng ký tham gia BHXH</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Danh sách lao động đề nghị cấp sổ BHXH, thẻ BHYT (Mẫu 1a &ndash; TBH)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Tờ khai tham gia BHXH (Mẫu 01-TBH)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Bảng lương từ&nbsp;tháng bắt đầu thành lập.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Hợp đồng lao động.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Đăng ký hệ thống thang lương và phụ cấp lương. (Có xác nhận của phòng LĐ)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Bảng khai trình lao động. (Có xác nhận của phòng LĐ)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Mẫu 301</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; + Mẫu 401</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><u>Lưu ý :</u></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &ndash;&nbsp;Nộp HSơ BHXH từ ngày 1 -&gt; ngày 20 hàng tháng</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &ndash;&nbsp;Thời gian thụ lý hồ sơ BHXH sau 5 ngày làm việc.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &ndash;&nbsp;BHTN : Phải ký HĐLĐ trên 10 người.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &ndash;&nbsp;Đ/v Dn nhà nước 5 Lđộng phải TLCĐoàn, Cty tư nhân 10 Lđộng phải TLCĐoàn.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &ndash;&nbsp;Người nước ngoài là ĐDPL hoặc thành viên cty thì không cần khai trình LĐ cho phòng LĐTBXH.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &ndash; &nbsp;Sở LĐTBXH :&nbsp;www.sldtbxh.hochiminhcity.gov.vn</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Danh mục trên có thể thay đổi tùy theo mỗi Quận/Huyện.</em></strong><br />\r\n<strong>BHYT &ndash; BHXH CHO DOANH NGHIỆP MỚI</strong></span></p>\r\n\r\n<p><br />\r\n<span style=\"font-size:16px;\"><strong><u>I. Hồ sơ yêu cầu:</u>&nbsp;</strong><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1. &nbsp;Phiếu đăng ký tham gia BHXH (theo mẫu 1 bản)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2. &nbsp;Giấy phép đăng ký kinh doanh hoặc quyết định thành lập (1 bản)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3. &nbsp;Bảng đăng ký sử dụng lao động (đã được đăng ký với cơ quan lao động &ndash; 1 bản)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;4. &nbsp;Thang, bảng lương (đã được đăng ký với cơ quan lao động &ndash; 1 bản)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5. &nbsp;Mẫu 1a-TBH &ldquo;Danh sách lao động đề nghị cấp sổ BHXH, thẻ BHYT&rdquo; (3 bản)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;6. &nbsp;Mẫu 3a-TBH : &ldquo;Danh sách điều chỉnh lao động và mức đóng BHXH-BHYT &ndash; BHTN bắt buộc&rdquo; (3 bản)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;7. &nbsp;HĐLĐ hoặc quyết định tuyển dụng, thuyên chuyển&hellip;(1 bản)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp;<strong>Đơn vị đăng ký trể so với giấy phép, bổ sung thêm:</strong><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; 1. &nbsp;Công văn giải trình lý do chậm đăng ký BHXH cho người lao động theo quy định (1 bản)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; 2. &nbsp;Bảng lương thực tế của đơn vị có ký nhận của NLĐ từ ngày thành lập (1 bản)</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><u>II. Cơ quan tiếp nhận:</u>&nbsp;</strong><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&ndash; &nbsp;Đối với doanh nghiệp sử dụng trên 100 lao động, doanh nghiệp có vốn đầu tư nước ngoài nộp tại cơ quan Bảo Hiểm Xã Hội tỉnh, thành phố<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&ndash; &nbsp;Đối với doanh nghiệp sử dụng dưới 100 lao động nộp tại cơ quan Bảo Hiểm xã Hội quận huyện nơi doanh nghiệp đặt trụ sở.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>ĐĂNG KÝ LAO ĐỘNG &ndash; BHXH &ndash; BHYT CHO NGƯỜI VIỆT NAM</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp;BIỂU PHÍ ĐĂNG KÝ LAO ĐỘNG &ndash; BHXH &ndash; BHYT</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>(NGƯỜI VIỆT NAM)</strong></span></p>\r\n\r\n<table border=\"1\" width=\"685\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\"><strong>&nbsp;Mức Phí</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>Số lượng người đăng ký</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\"><strong>2.500.000</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>Dưới 5 người</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\"><strong>3.000.000</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>Từ 5 người đến 10 người</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\"><strong>3.500.000</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>Từ 10 người đến dưới 20 người</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\"><strong>500.000</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>Nguyễn Thắng theo dõi hồ sơ BHXH hàng tháng</strong></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong><u>Lưu ý</u></strong></em>:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;&ndash; Nếu công ty thành lập từ năm 2010 về trước thì phí dịch vụ là : 3.000.000&nbsp;đ&nbsp;(ba triệu đồng)&nbsp; cho 20 người trở xuống.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp;&ndash;&nbsp;Nếu đăng ký tham gia bảo hiểm cho người VN &amp; NN: phí trên cộng 1.000.000đ. Nếu Nguyễn Thắng&nbsp;theo dõi hồ sơ BHXH hàng tháng : 300.000đ-700.000 đ</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; Với việc lựa chọn dịch vụ&nbsp;<strong>&rdquo; đăng ký lao động, bảo hiểm xã hội &rdquo;&nbsp;</strong>của chúng tôi, chúng tôi sẽ thực hiện từ A đến Z cho quý khách, quý khách sẽ tiết kiệm được rất nhiều thời gian và chi phí đồng thời hồ sơ luôn phù hợp với quy định của pháp luật, quý khách hàng chỉ việc ký trên các hồ sơ, biểu mẫu do chúng tôi soạn thảo sẵn.</span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 07:21:31', '2018-11-26 07:31:05', NULL);
INSERT INTO `posts` (`id`, `title`, `path`, `description`, `content`, `image`, `post_type`, `is_active`, `category_item_id`, `user_id`, `created_at`, `updated_at`, `seo_id`) VALUES
(12, 'Làm giấy phép kinh doanh', 'lam-giay-phep-kinh-doanh', '<p><strong>DÀNH CHO KHÁCH HÀNG BẬN RỘN :</strong>&nbsp;<strong>Khách hàng chỉ cần đi ra phòng công chứng thay vì đi 3 lần như bình thường (chi phí cộng thêm 200.000đ)</strong></p>', '<h1><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><strong><span style=\"font-size:18px;\"><span style=\"color:#ff0000;\">LÀM GIẤY PHÉP KINH DOANH</span></span></strong></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>I/ THÀNH LẬP MỚI: &nbsp; ( Liên hệ:&nbsp;&nbsp; Ms Phượng&nbsp; 0909.64.85.80&nbsp;&ndash; 0978.77.66.55 )</strong></span></p>\r\n\r\n<table border=\"1\" cellpadding=\"3\" cellspacing=\"0\" width=\"709\">\r\n	<tbody>\r\n		<tr>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>A.</strong><strong>&nbsp;GPKD , MST,&nbsp;&nbsp; Con dấu</strong></span></td>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>Thời gian làm việc</strong></span></td>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>Phí dịch vụ</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>Lệ phí nhà nước</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">&ndash; Tư vấn đặt tên, ngành, vốn điều lệ, &hellip;<br />\r\n			&ndash; Soạn hồ sơ<strong>&nbsp;thành lập công ty</strong><br />\r\n			&ndash; Đăng ký xin cấp Giấy Phép Kinh Doanh, MST,<br />\r\n			&ndash; Khắc con dấu tròn công ty</span></td>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><span style=\"font-size:16px;\">&ndash; 3 ngày có GPKD</span></p>\r\n\r\n			<p><span style=\"font-size:16px;\">&ndash; 3 ngày có dấu tròn</span></p>\r\n			</td>\r\n			<td nowrap=\"nowrap\">\r\n			<h2><span style=\"font-size:16px;\"><strong>350.000 đ</strong></span></h2>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-size:16px;\">&ndash; Phí Sở: 200.000đ</span></p>\r\n\r\n			<p><span style=\"font-size:16px;\">&ndash; Phí con dấu:&nbsp; 450.000đ</span></p>\r\n\r\n			<p><span style=\"font-size:16px;\">&ndash; Đăng bố cáo : 300.000đ&nbsp;( BẮT BUỘC)</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>B.&nbsp;Đăng ký</strong><strong>&nbsp;thuế ban đầu</strong></span></td>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>Thời gian làm việc</strong></span></td>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>Phí dịch vụ</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>Lệ phí nhà nước</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">&ndash; Thiết lập hồ sơ thuế ban đầu<br />\r\n			&ndash; Đăng ký thuế BĐ tại cơ quan thuế</span>\r\n			<p>&nbsp;</p>\r\n\r\n			<ul>\r\n				<li><span style=\"font-size:16px;\">Tặng 3 tháng báo cáo thuế free</span></li>\r\n			</ul>\r\n			</td>\r\n			<td><span style=\"font-size:16px;\">1 ngày</span></td>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>1.000.000</strong>&nbsp;đ</span></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\"><strong>C. Đăng bố cáo</strong></span></td>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>Thời gian làm việc</strong></span></td>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\"><strong>Phí dịch vụ</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>Lệ phí đăng báo</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">&ndash; Đăng bố cáo&nbsp;<b>thành lập công ty</b></span></td>\r\n			<td><span style=\"font-size:16px;\">1 ngày</span></td>\r\n			<td nowrap=\"nowrap\"><span style=\"font-size:16px;\">0</span></td>\r\n			<td><span style=\"font-size:16px;\">&ndash; 300.000đ (cổng thông tin quốc gia)</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>DÀNH CHO KHÁCH HÀNG BẬN RỘN :</strong>&nbsp;<strong>Khách hàng chỉ cần đi ra phòng công chứng