INSERT INTO `posts` (`id`, `title`, `path`, `description`, `content`, `image`, `image_small`, `sub_image`, `videos`, `post_type`, `is_active`, `category_item_id`, `user_id`, `created_at`, `updated_at`, `seo_id`) VALUES
(27, '5 địa điểm cho tuần trăng mật lý tưởng nhất thế giới', '5-dia-diem-cho-tuan-trang-mat-ly-tuong-nhat-the-gioi', '<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Với những cặp đôi vừa &ldquo;về cùng một nhà&rdquo;, tuần trăng mật là một trong những khoảng thời gian riêng tư đẹp nhất và đáng nhớ nhất.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Chắc hẳn bạn sẽ cùng người bạn đời của mình lên kế hoạch cho những chuyến đi xa, đến những nơi thật đẹp và ăn những món thật ngon đúng không?</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Nếu vẫn còn đang băn khoăn chưa biết sẽ đi đâu, hãy cùng <b>Hera Center</b> tham khảo 5 địa điểm để hưởng tuần trăng mật lý tưởng nhất thế giới bạn nhé!</span></span></span></span>\r\n</p>', '<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:18.0pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">TUẦN TRĂNG MẬT LÃNG MẠN</span></span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Với những cặp đôi vừa &ldquo;về cùng một nhà&rdquo;, tuần trăng mật là một trong những khoảng thời gian riêng tư đẹp nhất và đáng nhớ nhất.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Chắc hẳn bạn sẽ cùng người bạn đời của mình lên kế hoạch cho những chuyến đi xa, đến những nơi thật đẹp và ăn những món thật ngon đúng không?</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Nếu vẫn còn đang băn khoăn chưa biết sẽ đi đâu, hãy cùng <b>Hera Center</b> tham khảo 5 địa điểm để hưởng tuần trăng mật lý tưởng nhất thế giới bạn nhé!</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:normal\"><b><span new=\"\" roman=\"\" times=\"\">Maldives</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Nếu bạn mong muốn đến một nơi chỉ có riêng bạn và người bạn đời cùng đi dạo chân trần trên những bãi cát trắng mịn màng, dưới bóng dừa tươi mát và trầm mình trong màu biển xanh vô tận thì Maldives là nơi hoàn hảo nhất với vẻ đẹp thuần khiết và sự bình yên. Maldives như từng bức tranh ghép lại, cảnh sắc thiên nhiên hoà hợp, khí hậu mát mẻ và những dịch vụ vô cùng &ldquo;sang chảnh&rdquo; ở đây sẽ khiến tuần trăng mật của bạn trở thành những ngày tuyệt vời nhất để bắt đầu cuộc hôn nhân hạnh phúc.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/m_1.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/m_2.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><b><span new=\"\" roman=\"\" times=\"\">Hòn đảo Santorini</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Santorini hùng vĩ và đẹp như chốn thiên đường trên hạ giới là điều không còn gì bàn cãi nữa.&nbsp; Từ lâu, nơi đây đã nổi tiếng với những vách đá dựng đứng ôm lấy bờ biển cát đen đặc trưng của bụi núi lửa, cùng với nét kiến trúc độc đáo của hai gam màu trắng và xanh da trời, những căn nhà trên đỉnh Oia được xây theo hình bậc thang tạo nên vẻ cổ điển khó kiếm được nơi đâu khác. Và chính vì khung cảnh đặc biệt của nơi này mà Santorini được chọn làm bối cảnh của bộ phim Mama Mia nổi tiếng. Đặc biệt, chẳng cần phải tìm kiếm, bất cứ góc độ nào, khung cảnh nào ở Santorini cũng đều là phông nền tự nhiên hoàn hảo để ghi lại những khoảnh khắc hạnh phúc nhất của tình yêu. Đây chỉ có thể là Santorini!</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/s_1.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/s_2.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:normal\"><b><span new=\"\" roman=\"\" times=\"\">Thành phố Florence, Ý</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Được ca ngợi là thành phố đẹp nhất thế giới. Nơi đây vẫn còn giữ được gần như nguyên vẹn của những công trình nghệ thuật, nét cổ kính từ hàng trăm năm trước. Tại đây, bạn có thể dễ dàng bắt gặp những tòa lâu đài cổ, những nhà thờ, những viện bảo tàng đầy ắp những bức tranh quý hiếm cùng những bức tượng quý giá.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/f_1.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/f_2.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><b><span new=\"\" roman=\"\" times=\"\">Thị trấn St. Morizt, Thụy Sĩ</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Nhắc đến Thụy Sĩ, là nhắc đến những ngọn núi cao hùng vĩ cùng sườn núi trắng toát một màu của tuyết. Nổi tiếng trong số đó là Thị trấn Engadin St. Moritz. Nó luôn khoác lên mình những bộ cánh trắng muốt kiêu sa. Cũng một phần do địa hình cao và thời tiết &ldquo;phù hợp&rdquo; nên nơi này rất phổ biến các môn thể thao mùa đông. Nếu không thích bạn cũng có thể thử với những môn thể thao khác. Hấp dẫn hơn hết vẫn là các môn thể thao leo núi hay đạp xe quanh các lối mòn của thị trấn.. Đây sẽ là những trải nghiệm tuyệt vời nhất của bạn và vị hôn thê của mình đấy.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/1.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/2.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:normal\"><b><span new=\"\" roman=\"\" times=\"\">Quần đảo Hawaii</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Hawaii được Tạp chí Bride bình chọn là địa điểm du lịch trăng mật hàng đầu thế giới. Theo khảo sát của tờ này, hơn 20 triệu du khách trên thế giới đã đến Hawaii và hơn 6.000 đám cưới đã được tổ chức ở đây. Do nằm ở trung tâm nhiệt đới nên Hawaii chịu sự điều tiết của khí hậu biển, bốn mùa mát mẻ. Vì vậy mà quanh năm nơi đây đều thu hút nhiều khách du lịch tới tham quan và nghỉ dưỡng. Đến với đảo Hawaii, du khách sẽ phải ngỡ ngàng trước vẻ đẹp của những bãi biển và phong cảnh thiên nhiên xinh đẹp.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/h_1.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/5diadiem/h_2.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0in 0in 8pt\">\r\n	<span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"font-size:16px;\"><span style=\"line-height:normal\"><span new=\"\" roman=\"\" times=\"\">Với những gợi ý trên, <b><i>Hera Center</i></b> hi vọng những cặp đôi sẽ có được tuần trăng mật tuyệt vời để khởi đầu cuộc sống hôn nhân hạnh phúc!!! </span></span></span></span>\r\n</p>', 'images/uploads/images/tintuc/5diadiem/1.jpg', NULL, NULL, NULL, 1, 1, NULL, 1, '2018-10-02 03:24:35', '2018-10-03 06:51:30', 59),
(28, 'Áo dài cưới cho cô dâu chú rể: Lựa chọn thế nào để bắt kịp thời đại?', 'ao-dai-cuoi-cho-co-dau-chu-re-lua-chon-the-nao-de-bat-kip-thoi-dai', '<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:115%\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Với các cô dâu thì việc mặc áo dài cưới chắc chắn không còn xa lạ nhưng với các chú rể thì việc diện một bộ áo dài trong ngày trọng đại này thì chắc hẳn vẫn còn là điều khá mới mẻ. Vậy làm thế nào để lựa chọn áo dài cưới cho cô dâu, chú rể sao cho thật xứng đôi?</span></span></span></span></span>\r\n</p>', '<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:115%\"><b><i><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Áo dài cưới cho cô dâu chú rể: Lựa chọn thế nào để bắt kịp thời đại?</span></span></i></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:115%\"><b><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Với các cô dâu thì việc mặc áo dài cưới chắc chắn không còn xa lạ nhưng với các chú rể thì việc diện một bộ áo dài trong ngày trọng đại này thì chắc hẳn vẫn còn là điều khá mới mẻ. Vậy làm thế nào để lựa chọn áo dài cưới cho cô dâu, chú rể sao cho thật xứng đôi?</span></span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:Tahoma,Geneva,sans-serif;\"><span style=\"line-height:115%\"><b><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">&nbsp;Hãy tham khảo những chia sẻ dưới đây của <i>Hera Center</i> để tìm lời giải đáp và lựa chọn được những bộ áo dài thật hài hòa và độc đáo nhé.</span></span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Từ xưa, các mẫu áo dài truyền thống được lựa chọn làm trang phục cưới bởi vì nó mang những nét đặc trưng cho văn hóa dân tộc. Hiện nay, điều này vẫn còn được lưu giữ, các cặp đôi vẫn lựa chọn áo dài cưới cho ngày trọng đại của cuộc đời mình. Vẫn là những bộ áo dài cưới truyền thống nhưng lựa chọn như thế nào để không bị mất đi vẻ đẹp hiện đại và sang trọng, đó là việc khiến các cặp đôi lại càng trăn trở hơn. </span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Phải lựa chọn làm sao để thể hiện được sự nhất quan về phong cách, hài hòa có đôi có cặp và thể hiện được sự tương đồng về quan điểm cũng như lối sống hiện đại mà tất cả chúng ta đang hướng đến?</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/aodaicuoi/1.jpg\" style=\"width: 100%; height: 100%;\">\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	&nbsp;\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Việc chọn lựa áo dài cưới tưởng chừng đơn giản tuy nhiên khi có sự đồng quan điểm trong việc chọn áo dài cưới cũng đã phần nào báo hiệu cuộc sống vợ chồng hòa thuận, hạnh phúc. Hình ảnh cô dâu thướt tha, yểu điệu bên cạnh chú rể nam tính, mạnh mẽ trong tà áo dài chắc chắn sẽ khiến cho hai bạn vô cùng nổi bật giữa lễ cưới.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Và để làm được điều đó, các cô dâu, chú rể cần lưu ý những vấn đề sau:\\</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><b><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Lựa chọn áo dài cho cô dâu:</span></span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Áo dài từ lâu là trang phục truyền thống nhằm tôn vinh vẻ đẹp thuần khiết của người phụ nữ Việt Nam, do đó việc lựa chọn áo dài cho cô dâu trong ngày cưới lại càng được quan tâm hơn.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	&nbsp;\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/aodaicuoi/2.jpg\" style=\"width: 100%; height: 100%;\">\r\n</p>\r\n\r\n<p align=\"center\" style=\"text-align:center; margin:0in 0in 10pt\">\r\n	&nbsp;\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Nếu cô dâu có dáng người &ldquo;bánh bèo&rdquo; thì nên tránh chọn những chất liệu bóng hay có ánh cao. Vì như vậy sẽ khiến cho cơ thể của bạn thêm phần mũm mĩm. Thay vào đó hãy lựa chọn các loại vải mềm và rũ, như vậy sẽ giúp cho cô dâu trở nên thon gọn hơn.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Bên cạnh đó nếu muốn tạo sự thoải mái và nét thanh thoát cho trang phục, bạn có thể lựa chọn những mẫu áo dài cách tân có cổ thuyền hay cổ rộng cùng với đó là những họa tiết kiểu sọc đứng và được thiết kế trên nền vài trơn một màu.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Nếu cô dâu mang một vóc dáng gầy thì hãy lựa chọn những mẫu vải với màu trắng tươi sáng như màu trắng, màu kem hoặc hồng. Về họa tiết nên lựa chọn những thiết kế với họa tiết to, trải dài từ trên xuống dưới, có thể đính cườm hoặc đá ở phần ngực áo. Như vậy sẽ giúp cho cô dâu trở nên đầy đặn hơn.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/aodaicuoi/3.jpg\" style=\"width: 100%; height: 100%;\">\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Còn với những cô dâu có chiều cao hơi hạn chế thì không nên lựa chọn những kiểu áo tà lớn mà thay vào đó nên chọn những mẫu áo có tà dài vừa chạm đất, ống quần không may quá ôm với dáng người. Như vậy bạn có thể dễ dàng &ldquo;ăn gian&rdquo; chiều cao khi kết hợp cùng với giày cao gót. Nếu muốn khắc phục vấn đề chiều cao bạn nên chọn những mẫu áo dài với thiết kế kiểu hoa văn dọc theo thân áo từ phần vai xuống giữa tà áo. Lưu ý, những mẫu áo dài có họa tiết to trải ngang ngực sẽ dễ dàng &ldquo;tố cáo&rdquo; chiều cao thật của bạn do đó nếu không muốn mọi người nhận ra yếu điểm của mình thì hãy tránh lựa chọn những bộ áo dài như thế này.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><b><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Lựa chọn áo dài cưới cho chú rể</span></span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Khác với việc lựa chọn áo dài cưới cho cô dâu, việc chọn áo dài cưới cho chú rể phần nào được thực hiện dễ dàng hơn. Sang trọng, lịch sự và thể hiện được vẻ đẹp nam tính của mình là tất cả những điều mà các chú rể quan tâm khi lựa chọn áo dài cưới cho mình. </span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/aodaicuoi/4.jpg\" style=\"width: 100%; height: 100%;\">\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Với những chú rể có thân hình cân đối thì việc lựa chọn áo dài cưới cũng không quá khó khăn, chỉ cần bộ áo dài đó phù hợp với không gian ngày cưới cũng như tông màu áo dài của cô dâu để mang đến sự đồng điều nhất quán.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Đối với những chú rể hơi mập một chút thì những mẫu áo dài cưới tà lửng với tông màu trầm, thiết kế không quá ôm sát và các họa tiết được thiết kế theo chiều dọc,&nbsp; sẽ là lựa chọn tối ưu nhất.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Ngược lại với những chú rể hơi gầy thì nên chọn những bộ áo dài có tông màu sáng, kết hợp với những họa tiết trang trí theo chiều ngang.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/tintuc/aodaicuoi/5.jpg\" style=\"width: 100%; height: 100%;\">\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Các bạn lưu ý, về tông màu áo dài của chú rể nên chọn những màu đồng điệu với màu áo dài của cô dâu hoặc những tông màu có thể bổ trợ cho nhau. Tuy nhiên phải đảm bảo phù hợp với sự sang trọng của một lễ cưới.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	<span style=\"font-size:16px;\"><span style=\"line-height:115%\"><span style=\"font-family:Arial,sans-serif\"><span style=\"line-height:115%\"><span new=\"\" roman=\"\" times=\"\">Trong điều kiện hiện nay, việc chọn lựa áo dài cưới cho cô dâu chú rể không khó bởi những mẫu áo dài cưới rất phong phú và đa đạng. Chúng tôi xin chúc các cặp đôi có thể lựa chọn cho mình được những mẫu áo dài dưới cô dâu chú rể đẹp nhất, ấn tượng nhất.</span></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	&nbsp;\r\n</p>\r\n\r\n<p style=\"text-align:justify; margin:0in 0in 10pt\">\r\n	&nbsp;\r\n</p>', 'images/uploads/images/tintuc/aodaicuoi/1.jpg', NULL, NULL, NULL, 1, 1, NULL, 1, '2018-10-02 03:40:24', '2018-10-03 06:54:44', 60),
(29, 'Mách mẹ nơi tổ chức tiệc sinh nhật đáng yêu cho bé và tiết kiệm cho me lên đến 8.000.000 đồng.', 'mach-me-noi-to-chuc-tiec-sinh-nhat-dang-yeu-cho-be-va-tiet-kiem-cho-me-len-den-8000000-dong', '<p>\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"background-color:null;\">Ngày sinh nhật của &ldquo;gấu con&rdquo; yêu thương đang đến, mẹ và ba muốn tổ chức một buổi tiệc sinh nhật thật đáng yêu và ý nghĩa cho &ldquo;gấu con&rdquo; yêu thương nhưng chưa chọn được nơi tổ chức. Chúng tôi xin giới thiệu đến gia đình bé một nơi tổ chức sinh nhật lý tưởng, đầy hoa cỏ xanh tươi, thoáng mát và hứa hẹn cực kỳ thú vị ở ngay trung tâm đô </span><span style=\"background-color:null;\">thị Celadon, quân Tân Phú. Chắc chắn bữa</span><span style=\"background-color:null;\"> tiệc sinh nhật của bé sẽ làm hài lòng tất cả thành viên trong gia đình và quan khách một cách tuyệt đối.</span></span></span></span></span>\r\n</p>', '<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Ngày sinh nhật của &ldquo;gấu con&rdquo; yêu thương đang đến, mẹ và ba muốn tổ chức một buổi tiệc sinh nhật thật đáng yêu và ý nghĩa cho &ldquo;gấu con&rdquo; yêu thương nhưng chưa chọn được nơi tổ chức. Chúng tôi xin giới thiệu đến gia đình bé một nơi tổ chức sinh nhật lý tưởng, đầy hoa cỏ xanh tươi, thoáng mát và hứa hẹn cực kỳ thú vị ở ngay trung tâm đô <span style=\"\"><span style=\"\">thị Celadon, quân Tân Phú. Chắc chắn bữa</span></span> tiệc sinh nhật của bé sẽ làm hài lòng tất cả thành viên trong gia đình và quan khách một cách tuyệt đối.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Hera Center- Không gian đậm chất Châu Âu đầy tinh tế và sang trọng</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Tọa lạc ngay trung tâm đô thị phồn thịnh nhất Celadon City của quân Tân phú, Thành phô Hồ Chí Minh. Hera Center như một vệt sáng rất được ưa chuộng với các dịch vụ hàng đầu về hội nghị- tiệc cưới. Điểm nhấn của Hera Center chính là sự kết hợp hài hòa và tinh tế giữa phong cách cổ điển thời kỳ Phục Hưng Châu Âu và sự mộc mạc Á Đông tạo nên một vẻ đẹp giao thoa sang trọng và đẳng cấp.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/uudai/tiecsinhnhat/2.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Hera Center- tiệc &ldquo;sinh nhật hồng&rdquo; đáng yêu như truyện cổ tích cho &ldquo;gấu con&rdquo; yêu thương.</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Với đội ngũ nhân viên yêu nghề, chúng tôi luôn mang đến những bữa tiệc sinh nhật thật đáng yêu theo yêu cầu và sở thích của bé và gia đình, đảm bảo bé yêu của bạn sẽ mê tít bữa tiệc sinh nhật của chính mình. Hera Center luôn đồng hành cùng gia đình bé với tất cả sự tận tâm, chuyên nghiệp, sự thân thiện để gia đình và quan khách thoải mái nhất, mang lại cảm giác dù ở nhà hàng nhưng cũng như ở nhà.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/uudai/tiecsinhnhat/3.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Hera Center &ndash; tuyệt đỉnh ẩm thực.</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Với các chuyên gia đầu bếp giàu kinh nghiệm, Hera Center đáp ứng các yêu cầu về ẩm thực Á &ndash; Âu theo yêu cầu của khách hàng. Tiêu chí sạch &ndash; đảm bảo an toàn luôn được đặt lên hàng đầu để khách hàng yên tâm khi sử dụng dịch vụ.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Ẩm thực Hera Center được đánh giá là món ngon tròn vị và luôn làm hài lòng thực khách khó tính.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/uudai/tiecsinhnhat/4.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><b><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Hera Center- Nơi tổ chức tiệc sinh nhật giá rẻ ở TP HCM với dịch vụ chuyên nghiệp đẳng cấp 3 sao giúp mẹ tiết kiệm chi phí đến 8.000.000 đồng.</span></b></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Mẹ sẽ tiết kiệm được một khoản chi phí không hề nhỏ với gói ưu đãi không còn gì hấp dẫn hơn &ldquo;sinh nhật hồng&rdquo;cho tiệc sinh nhật của bé yêu.</span></span></span></span>\r\n</p>\r\n\r\n<ul>\r\n	<li style=\"margin:0in 0in 0.0001pt 0.5in\">\r\n		<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Tặng gói trang trí sinh nhật lên đến 8.000.000 đồng.</span></span></span></span>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify; margin:0in 0in 0.0001pt 0.5in\">\r\n		<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Trang trí bong bóng toàn sảnh tiệc.</span></span></span></span>\r\n	</li>\r\n	<li style=\"text-align:justify; margin:0in 0in 0.0001pt 0.5in\">\r\n		<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Bàn gallery.</span></span></span></span>\r\n	</li>\r\n	<li style=\"text-align:justify; margin:0in 0in 0.0001pt 0.5in\">\r\n		<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Tháp bánh cupcake</span></span></span></span>\r\n	</li>\r\n	<li style=\"text-align:justify; margin:0in 0in 0.0001pt 0.5in\">\r\n		<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Chú hề tạo hình bong bóng tặng khách.</span></span></span></span>\r\n	</li>\r\n	<li style=\"text-align:justify; margin:0in 0in 8pt 0.5in\">\r\n		<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">MC chú hề hoạt náo suốt tiệc.</span></span></span></span>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><u><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Điều kiện áp dung:</span></u></span></span></span>\r\n</p>\r\n\r\n<ul>\r\n	<li style=\"margin:0in 0in 0.0001pt 0.5in\">\r\n		<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Đặt tiệc 15 bàn trở lên, được tặng 100% gói trang trí trị giá lên đến 8.000.000 đồng.</span></span></span></span>\r\n	</li>\r\n	<li style=\"margin:0in 0in 8pt 0.5in\">\r\n		<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Đặt tiệc dưới 15 bàn, được tặng 50% gói trang trí trị giá lên đến 8.000.000 đồng.</span></span></span></span>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><u><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Thời gian áp dụng:</span></u><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"> từ 26/06/2018 đến 26/09/2018.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\">Hãy chọn Hera Center tổ chức sinh nhật cho bé ở Tp HCM để đưa bé vào không gian cổ tích thần tiên, tận hưởng dịch vụ hoàn hảo, ẩm thực tuyệt đỉnh nhưng lại tiết kiệm cho mẹ và hơn hết là có một buổi tiệc sinh nhật thật đáng nhớ.</span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/uudai/tiecsinhnhat/5.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/uudai/tiecsinhnhat/6.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>\r\n\r\n<p style=\"margin:0in 0in 8pt\">\r\n	<span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/uudai/tiecsinhnhat/7.jpg\" style=\"width: 100%; height: 100%;\"></span></span></span></span>\r\n</p>', 'images/uploads/images/uudai/tiecsinhnhat/1.png', NULL, NULL, NULL, 1, 1, NULL, 1, '2018-10-02 04:11:47', '2018-10-02 15:57:25', 61),
(30, 'TIỆC CƯỚI RỰC RỠ CÙNG “NGÀY HẠNH PHÚC”', 'tiec-cuoi-ruc-ro-cung-ngay-hanh-phuc', '<p>\r\n	<span style=\"font-size:13.5pt\"><span style=\"line-height:107%\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span style=\"\">Nắng đầu mùa, nóng và oi nồng. Bằng lăng tím hé nụ, rạo rực và chờ mong! Hè dần đến, những ngày tháng nóng bỏng nhất cũng đang dần đến và thời khắc hòa hợp của các đôi uyên ương đang cận kề. Cùng Hera Center tổ chức những tiệc cưới rực rỡ dưới nắng vàng cho mùa hè này thêm đắm say ngây ngất.</span></span></span></span>\r\n</p>', '<p style=\"margin-bottom:.25in; margin:0in 0in 8pt\">\r\n	&nbsp;\r\n</p>\r\n\r\n<p style=\"margin: 0in 0in 8pt; text-align: center;\">\r\n	<img alt=\"\" src=\"http://tieccuoihera.com/images/uploads/images/uudai/ngayhanhphuc/2.png\" style=\"width: 80%; height: 80%;\">\r\n</p>\r\n\r\n<p>\r\n	<b>Nắng đầu mùa, nóng và oi nồng. Bằng lăng tím hé nụ, rạo rực và chờ mong! Hè dần đến, những ngày tháng nóng bỏng nhất cũng đang dần đến và thời khắc hòa hợp của các đôi uyên ương đang cận kề. Cùng Hera Center tổ chức những tiệc cưới rực rỡ dưới nắng vàng cho mùa hè này thêm đắm say ngây ngất.</b>\r\n</p>\r\n\r\n<p>\r\n	Tiệc cưới rực rỡ dưới nắng vàng làm cho ngọn lửa tình yêu thêm bất diệt. Khi yêu, người ta thường mơ ước tới một hôn lễ như trong xứ sở thần tiên, màu nhiệm như truyện cổ tích. Ước mơ sẽ không còn là mơ ước khi tiệc cưới của bạn được tổ chức tại Hera Center, chúng tôi sẽ mang đến cho bạn tiệc cưới rực rỡ như trong tâm tưởng của bạn.\r\n</p>\r\n\r\n<p>\r\n	Tiệc cưới rực rỡ dưới nắng vàng, chúng tôi sẽ đưa bạn đi qua từng trang sách thần tiên mang tên Hera Center trong sắc xanh của hoa cỏ, mát ngọt trong lành của dòng nước trong xanh, yên bình ngưng đọng cùng tiếng gió thì thầm và tiếng hót thanh thót của các loài lông vũ. Cùng với tiếng nhạc du dương trong không gian tiệc cưới rực rỡ đầy lãng mạng ấy, câu chuyện tình yêu của bạn là một đề tài bất tận và là nguồn cảm hứng của biết bao thi sĩ đang đắm say hương nồng tình ái cho những tuyệt tác tương lai.\r\n</p>\r\n\r\n<p>\r\n	Tiệc cưới rực rỡ, rực rỡ cả không gian say lìm ý thơ, chất nhạc và tuyệt tác ẩm thực đỉnh cao. Tiệc cưới rực rỡ là rực rỡ đến từng giọt vang, miếng ngon thơm phức và từng giọt nước chấm nồng nàn.\r\n</p>\r\n\r\n<p>\r\n	Tiệc cưới rực rỡ sẽ càng rực rỡ hơn với chương trình ưu đãi hấp dẫn &quot;Ngày Hạnh Phúc&quot; mà chỉ Hera Center mới có.\r\n</p>\r\n\r\n<p>\r\n	Đặt tiệc 15 bàn, được tặng ngay:\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"display: none;\">&nbsp;</span><span style=\"color:#ffffff;\"><b>Tặng trọn gói trang trí cao cấp:</b></span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	&nbsp;\r\n</p>\r\n\r\n<p>\r\n	<b>+ Bàn gallery</b>\r\n</p>\r\n\r\n<p>\r\n	<b>+ Background</b>.\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"color:#ffffff;\"><b>Tặng tiền mặt 300.000đ/ bàn</b></span>\r\n	</li>\r\n	<li>\r\n		<span style=\"color:#ffffff;\"><b>Tặng nước ngọt, nước suối và bia Heineken chai trong suốt buổi tiệc.</b></span>\r\n	</li>\r\n	<li>\r\n		<span style=\"color:#ffffff;\"><b>Trọn gói nghi lễ (MC, ban nhạc điện tử, vũ đoàn)</b></span>\r\n	</li>\r\n	<li>\r\n		<span style=\"color:#ffffff;\"><b>Tặng toàn bộ thiêp cưới</b></span><span style=\"display: none;\">&nbsp;</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	&nbsp;\r\n</p>\r\n\r\n<p>\r\n	<u>ĐIỀU KIỆN ÁP DỤNG:</u>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"color:#ffffff;\">Áp dụng từ 200 khách trở lên.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"color:#ffffff;\">Thời gian ký hợp đồng từ 10/6/2018 đến 25/6/2018 và Thời gian tổ chức từ nay đến 31/10/2018.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"color:#ffffff;\">Không áp dụng chung với các chương trình ưu đãi khác.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"color:#ffffff;\">Không áp dụng với các hợp đồng đặt tiệc đã ký hoặc hợp đồng hủy ký lại.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	&nbsp;\r\n</p>\r\n\r\n<p>\r\n	Liên hệ ngay với&nbsp;<b>Trung tâm Tiệc cưới Hera Center</b>&nbsp;để được hỗ trợ tư vấn miễn phí.\r\n</p>\r\n\r\n<p>\r\n	Hotline: 01663997777\r\n</p>\r\n\r\n<p>\r\n	Email: info@heracenter.com\r\n</p>\r\n\r\n<p>\r\n	Website: http://heracenter.com\r\n</p>', 'images/uploads/images/uudai/ngayhanhphuc/2.png', NULL, NULL, NULL, 1, 1, NULL, 1, '2018-10-02 04:13:31', '2018-10-03 06:43:48', 62);
