INSERT INTO `posts` (`id`, `title`, `path`, `description`, `content`, `image`, `post_type`, `is_active`, `category_item_id`, `user_id`, `created_at`, `updated_at`, `seo_id`) VALUES
(13, 'Thủ tục hoàn thuế giá trị gia tăng', 'thu-tuc-hoan-thue-gia-tri-gia-tang', '<p><span style=\"font-size:16px;\">Quy định về điều kiện hoàn thuế GTGT, hướng dẫn làm hồ sơ hoàn thuế GTGT, các trường hợp được hoàn thuế GTGT theo Thông tư 99/2016/TT-BTC, Thông tư 130/2016 /TT-BTC,Thông tư 219/2013/TT-BTC và Thông tư 156/2013/TT-BTC.</span></p>', '<h1><span style=\"color:#ff0000;\"><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><strong><span style=\"font-size:18px;\">THỦ TỤC HOÀN THUẾ GIÁ TRÍ GIA TĂNG MỚI NHẤT</span></strong></span></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Thủ tục hồ sơ hoàn thuế giá trị gia tăng mới nhất</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều kiện và hồ sơ hoàn thuế GTGT mới nhất</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Quy định về điều kiện hoàn thuế GTGT, hướng dẫn làm hồ sơ hoàn thuế GTGT, các trường hợp được hoàn thuế GTGT theo Thông tư 99/2016/TT-BTC, Thông tư 130/2016 /TT-BTC,Thông tư 219/2013/TT-BTC và Thông tư 156/2013/TT-BTC.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Các trường hợp được hoàn thuế GTGT</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Kể từ ngày 01/7/2016 Theo Khoản 3 Điều 1 Thông tư 130/2016/TT-BTC quy định cụ thể như sau:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>1&gt; Cơ sở kinh doanh nộp thuế GTGT theo phương pháp khấu trừ thuế&nbsp;</em></strong>nếu có số thuế GTGT đầu vào chưa được khấu trừ hết trong tháng (đối với trường hợp kê khai theo tháng) hoặc trong quý (đối với trường hợp kê khai theo quý) thì được khấu trừ vào kỳ tiếp theo.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash;<strong>&nbsp;Trường hợp cơ sở kinh doanh có số thuế GTGT chưa được khấu trừ hết phát sinh trước kỳ tính thuế tháng 7/2016 (đối với trường hợp kê khai theo tháng) hoặc trước kỳ tính thuế quý 3/2016 (đối với trường hợp kê khai theo quý) đủ điều kiện hoàn thuế GTGT theo hướng dẫn tại khoản 1 Điều 18 Thông tư số 219/2013/TT-BTC thì cơ quan thuế giải quyết hoàn thuế theo quy định của pháp luật.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Doanh nghiệp A thực hiện khai thuế GTGT theo quý, tại kỳ tính thuế quý 3/2016 có số thuế GTGT chưa được khấu trừ hết là 80 triệu thì doanh nghiệp A được khấu trừ vào kỳ tính thuế quý 4/2016. Trường hợp các kỳ tính thuế quý 4/2016, quý 1/2017 và quý 2/2017 vẫn còn số thuế GTGT chưa được khấu trừ hết thì doanh nghiệp A chuyển số thuế GTGT chưa được khấu trừ hết để tiếp tục khấu trừ&nbsp;tại&nbsp;kỳ tính thuế quý 3/2017 và các kỳ tính thuế tiếp theo.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>2&gt; Cơ sở kinh doanh mới thành lập từ dự án đầu tư đã đăng ký kinh doanh, đăng ký nộp thuế giá trị gia tăng theo phương pháp khấu trừ</em></strong>,&nbsp;hoặc dự án tìm kiếm thăm dò và phát triển mỏ dầu khí đang trong giai đoạn đầu tư, chưa đi vào hoạt động, nếu thời gian đầu tư từ 01 năm trở lên thì được hoàn thuế giá trị gia tăng của hàng hóa, dịch vụ sử dụng cho đầu tư theo từng năm, trừ trường hợp hướng dẫn tại điểm c Khoản 3 Điều này. Trường hợp, nếu số thuế giá trị gia tăng lũy kế của hàng hóa, dịch vụ mua vào sử dụng cho đầu tư từ 300 triệu đồng trở lên thì được hoàn thuế giá trị gia tăng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>3&gt; Hoàn thuế GTGT đối với dự án đầu tư:</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">3.a/&nbsp;Cơ sở kinh doanh đang hoạt động thuộc đối tượng nộp thuế GTGT theo phương pháp khấu trừ có dự án đầu tư (trừ trường hợp hướng dẫn tại điểm c Khoản 3 Điều này và trừ dự án đầu tư xây dựng nhà để bán hoặc cho thuê mà không hình thành tài sản cố định) cùng tỉnh, thành phố, đang trong giai đoạn đầu tư thì cơ sở kinh doanh thực hiện kê khai riêng đối với dự án đầu tư và phải kết chuyển thuế GTGT đầu vào của dự án đầu tư để bù trừ với việc kê khai thuế GTGT của hoạt động sản xuất kinh doanh đang thực hiện. Số thuế&nbsp;GTGT được kết chuyển của dự án đầu tư tối đa bằng số thuế GTGT phải nộp của hoạt động sản xuất kinh doanh trong kỳ của cơ sở kinh doanh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi bù trừ nếu số thuế GTGT đầu vào của dự án đầu tư chưa được khấu trừ hết từ 300 triệu đồng trở lên thì được hoàn thuế GTGT cho dự án đầu tư. Sau khi bù trừ nếu số thuế GTGT đầu vào của dự án đầu tư mà chưa được khấu trừ hết nhỏ hơn 300 triệu đồng thì kết chuyển vào số thuế GTGT đầu vào của dự án đầu tư của kỳ kê khai tiếp theo.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Công ty A có trụ sở chính tại Hà Nội, tháng 7/2016, Công ty có dự án đầu tư tại Hà Nội, dự án đang trong giai đoạn đầu tư, Công ty A thực hiện kê khai riêng thuế GTGT đầu vào của dự án đầu tư này. Tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư là 500 triệu đồng; số thuế GTGT phải nộp của hoạt động sản xuất kinh doanh mà Công ty đang thực hiện là 900 triệu đồng. Công ty A phải bù trừ 500 triệu đồng thuế GTGT đầu vào của dự án đầu tư với số thuế phải nộp của hoạt động sản xuất kinh doanh đang thực hiện (900 triệu đồng), vậy số thuế GTGT mà Công ty A còn phải nộp trong kỳ tính thuế tháng 8/2016 là 400triệu đồng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp;Ví dụ:</strong>&nbsp;Công ty B có trụ sở chính tại Hải Phòng, tháng 7/2016, Công ty có dự án đầu tư tại Hải Phòng, dự án đang trong giai đoạn đầu tư,&nbsp;Công ty B thực hiện kê khai riêng thuế GTGT đầu vào của dự án đầu tư này. Tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư là 500 triệu đồng; số thuế GTGT phải nộp của hoạt động sản xuất kinh doanh mà Công ty đang thực hiện là 200 triệu đồng. Công ty B phải bù trừ 200 triệu đồng thuế GTGT đầu vào của dự án đầu tư với số thuế phải nộp&nbsp;của hoạt động sản xuất kinh doanh đang thực hiện (200 triệu đồng). Vậy, kỳ tính thuế tháng 8/2016 Công ty B có số thuế GTGT đầu vào của dự án đầu tư chưa được khấu trừ hết là 300 triệu đồng. Công ty B được xét hoàn thuế GTGT cho dự án đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Công ty C có trụ sở chính tại thành phố Hồ Chí Minh, tháng 7/2016, Công ty có dự án đầu tư tại thành phố Hồ Chí Minh, dự án đang trong giai đoạn đầu tư, Công ty C thực hiện kê khai riêng thuế GTGT đầu vào của dự án đầu tư này. Tháng 8/2016, số thuế GTGT&nbsp;đầu vào của dự án đầu tư là 500 triệu đồng; số thuế GTGT phải nộp của hoạt động sản xuất kinh doanh mà Công ty đang thực hiện là 300 triệu đồng. Công ty C phải bù trừ 300 triệu đồng thuế GTGT đầu vào của dự án đầu tư với số thuế phải nộp của hoạt động sản xuất kinh doanh đang thực hiện (300 triệu đồng). Vậy, kỳ tính thuế tháng 8/2016 Công ty C có số thuế GTGT đầu vào của dự án đầu tư chưa được khấu trừ hết là 200 triệu đồng. Công ty C không thuộc trường hợp được xét hoàn thuế GTGT cho dự án đầu tư, Công ty C thực hiện kết chuyển 200 triệu đồng vào số thuế GTGT đầu vào của dự án đầu tư của kỳ kê khai tháng 9/2016.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp;Ví dụ:</strong>&nbsp;Công ty D có trụ sở chính tại thành phố Đà Nẵng, tháng 7/2016, Công ty có dự án đầu tư tại thành phố Đà Nẵng, dự án đang trong giai đoạn đầu tư, Công ty D thực hiện kê khai riêng thuế GTGT đầu vàocủa dự án đầu tư này. Tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư là 500 triệu đồng; số thuế GTGT chưa được khấu trừ hết của hoạt động sản xuất kinh doanh mà Công ty đang thực hiện là 100 triệu đồng. Vậy, tại kỳ tính thuế tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư (500 triệu đồng) thuộc trường hợp được xét hoàn thuế GTGT cho dự án đầu tư, số thuế GTGT chưa được khấu trừ hết của hoạt động sản xuất kinh doanh đang thực hiện (100 triệu đồng) thì được khấu trừ vào kỳ tính thuế tháng 9/2016.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp;&nbsp;&nbsp; 1.b/ Trường hợp cơ sở kinh doanh đang hoạt động thuộc đối tượng nộp thuế GTGT theo phương pháp khấu trừ có dự án đầu tư mới (trừ trường hợp&nbsp;hướng dẫn tại điểm c Khoản 3 Điều này và trừ dự án đầu tư xây dựng nhà để bán hoặc cho thuê mà không hình thành tài sản cố&nbsp;định) tại địa bàn tỉnh, thành phố trực thuộc Trung ương khác với tỉnh, thành phố nơi đóng trụ sở chính, đang trong giai đoạn đầu tư chưa đi vào hoạt động, chưa đăng ký kinh doanh, chưa đăng ký thuế thì cơ sở kinh doanh lập hồ sơ khai thuế riêng cho dự án đầu tư&nbsp;đồng thời phải kết chuyển thuế GTGT đầu vào của dự án đầu tư để bù trừ với việc kê khai thuế GTGT của hoạt động sản xuất kinh doanh đang thực hiện. Số thuế GTGT được kết chuyển của dự án đầu tư tối đa bằng số thuế GTGT phải nộp của hoạt động sản&nbsp;xuất kinh doanh trong kỳ của cơ sở kinh doanh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi bù trừ nếu số thuế GTGT đầu vào của dự án đầu tư mới chưa được khấu trừ hết từ 300 triệu đồng trở lên thì được hoàn thuế GTGT cho dự án đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi bù trừ nếu số thuế GTGT đầu vào của dự án đầu tư mới chưa được khấu trừ hết nhỏ hơn 300 triệu đồng thì kết chuyển vào số thuế GTGT đầu vào của dự án đầu tư của kỳ kê khai tiếp theo.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trường hợp cơ sở kinh doanh có quyết định thành lập các Ban Quản lý dự án hoặc chi nhánh đóng tại các tỉnh, thành phố trực thuộc trung ương khác với tỉnh, thành phố nơi đóng trụ sở chính để thay mặt người nộp thuế trực tiếp quản lý một hoặc nhiều dự án đầu tư tại nhiều địa phương; Ban Quản lý dự án, chi nhánh có con dấu theo quy định của pháp luật, lưu giữ sổ sách chứng từ theo quy định của pháp luật về kế toán, có tài khoản gửi tại ngân hàng, đã đăng ký thuế và được cấp mã số thuế thì Ban Quản lý dự án, chi nhánh phải lập hồ sơ khai thuế, hoàn thuế riêng với cơ quan thuế địa phương nơi đăng ký thuế. Khi dự án đầu tư để thành lập doanh nghiệp đã hoàn thành và hoàn tất các thủ tục về đăng ký kinh doanh, đăng ký nộp thuế, cơ sở kinh doanh là chủ dự án đầu tư phải tổng hợp số thuế giá trị gia tăng phát sinh, số thuế giá trị gia tăng đã hoàn, số thuế giá trị gia tăng chưa được hoàn của dự án để bàn giao cho doanh nghiệp mớithành lập để doanh nghiệp mới thực hiện kê khai, nộp thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Dự án đầu tư được hoàn thuế GTGT theo quy định tại khoản 2, khoản 3 Điều này là dự án đầu tư theo quy định của pháp luật về đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Công ty A có trụ sở chính tại Hà Nội, tháng 7/2016, Công ty có dự&nbsp;án đầu tư mới tại Hưng Yên, dự án đang trong giai đoạn đầu tư, chưa đi vào hoạt động, chưa đăng ký kinh doanh, chưa đăng ký thuế, Công ty A thực hiện kê khai riêng thuế GTGT đầu vào của dự án đầu tư này tại Hà Nội trên Tờ khai thuế GTGT dành cho dự án đầu tư. Tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư là 500 triệu đồng; số thuế GTGT phải nộp của hoạt động sản xuất kinh doanh mà Công ty đang thực hiện là 900 triệu đồng. Công ty A phải bù trừ 500 triệu đồng thuế GTGT đầu vào của dự án đầu tư với số thuế phải nộp&nbsp;của hoạt động sản xuất kinh doanh đang thực hiện (900 triệu đồng), vậy Công ty A còn phải nộp trong kỳ tính thuế tháng 8/2016 là 400 triệu đồng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Công ty B có trụ sở chính tại Hải Phòng, tháng 7/2016, Công ty có dự án đầu tư mới tại Thái Bình, dự án đang trong giai đoạn đầu tư,&nbsp;chưa đi vào hoạt động, chưa đăng ký kinh doanh, chưa đăng ký thuế, Công ty B thực hiện kê khai riêng thuế GTGT đầu vào của dự án đầu tư này tại Hải Phòng trên Tờ khai thuế GTGT dành cho dự án đầu tư. Tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư là 500 triệu đồng; số thuế GTGT phải nộp của hoạt động sản xuất kinh doanh mà Công ty đang thực hiện là 200 triệu đồng. Công ty B phải bù trừ 200 triệu đồng thuế GTGT đầu vào của dự án đầu tư với số thuế phải nộp của hoạt động sản xuất kinh doanh đang thực hiện (200 triệu đồng). Vậy, kỳ&nbsp;tính thuế tháng 8/2016 Công ty B có số thuế GTGT đầu vào của dự án đầu tư mới chưa được khấu trừ hết là 300 triệu đồng. Công ty B được xét hoàn thuế GTGT cho dự án đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Công ty C có trụ sở chính tại thành phố Hồ Chí Minh, tháng 7/2016, Công ty có dự án đầu tư mới tại Đồng Nai, dự án đang trong giai đoạn đầu tư, chưa đi vào hoạt động, chưa đăng ký kinh doanh, chưa đăng ký thuế, Công ty C thực hiện kê khai riêng thuế GTGT đầu vào của dự án đầu tư này tại TP. Hồ Chí Minh trên Tờ khai thuế GTGT dành cho dự án đầu tư. Tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư là 500 triệu đồng; số thuế GTGT phải nộp của hoạt động sản xuất kinh doanh mà Công ty đang thực hiện là 300 triệu đồng. Công ty C phải bù trừ 300 triệu đồng thuế GTGT đầu vào của dự án đầu tư với số thuế phải nộp của hoạt động sản xuất kinh doanh đang thực hiện (300 triệu đồng). Vậy, kỳ tính thuế tháng 8/2016 Công ty C có số thuế GTGT đầu vào của dự án đầu tư mới chưa được khấu trừ hết là 200 triệu đồng. Công ty C không thuộc trường hợp được xét hoàn thuế GTGT cho dự án đầu tư, Công ty C thực hiện kết chuyển 200 triệu đồng vào số thuế GTGT đầu vào của dự án đầu tư của kỳ kê khai tháng 9/2016.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Công ty D có trụ sở chính tại thành phố Đà Nẵng, tháng 7/2016, Công ty có dự án đầu tư mới tại Quảng Nam, dự án đang trong giai đoạn đầu tư, chưa đi vào hoạt động, chưa đăng ký kinh doanh, chưa đăng ký thuế, Công ty D thực hiện kê khai riêng thuế GTGT đầu vào của dự án đầu tư này tại thành phố Đà Nẵng trên Tờ khai thuế GTGT dành cho dự án đầu tư. Tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư là 500 triệu đồng; số thuế GTGT chưa được khấu trừ hết của&nbsp;hoạt động sản xuất kinh doanh mà Công ty đang thực hiện là 100 triệu đồng. Vậy, tại kỳ tính thuế tháng 8/2016, số thuế GTGT đầu vào của dự án đầu tư (500 triệu đồng) thuộc trường hợp được xét hoàn thuế GTGT cho dự án đầu tư, số thuế GTGT chưa được khấu trừ hết của hoạt động sản xuất kinh doanh đang thực hiện (100 triệu đồng) thì được khấu trừ vào kỳ tính thuế tháng 9/2016.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">c) Cơ sở kinh doanh không được hoàn thuế giá trị gia tăng mà được kết chuyển số thuế chưa được khấu trừ của dự án đầu tư&nbsp;theo pháp luật về đầu tư sang kỳ tiếp theo đối với các trường hợp:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; c.1/&nbsp; Dự án đầu tư của cơ sở kinh doanh không góp đủ số vốn điều lệ như đã đăng ký theo quy định của pháp luật. Các hồ sơ đề nghị hoàn thuế dự án đầu tư nộp từ ngày 01/7/2016 của cơ sở kinh doanh nhưng tính đến ngày nộp hồ sơ không góp đủ số vốn điều lệ như đăng ký theo quy định của pháp luật thì không được hoàn thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; &nbsp;c.2/&nbsp; Dự án đầu tư của cơ sở kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện khi chưa đủ các điều kiện kinh doanh theo quy định của Luật đầu tư là dự án đầu tư của cơ sở kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện nhưng cơ sở kinh doanh chưa được cấp giấy phép kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện; chưa được cấp giấy chứng nhận đủ điều kiện kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện; chưa có văn bản của cơ&nbsp;quan nhà nước có thẩm quyền cho phép đầu tư kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện; hoặc chưa đáp ứng được điều kiện để thực hiện đầu tư kinh doanh có điều kiện mà không cần phải có xác nhận, chấp thuận dưới hình thức văn bản theo quy định của pháp luật về đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; c.3/ Dự án đầu tư của cơ sở kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện không bảo đảm duy trì đủ điều kiện kinh doanh trong quá trình hoạt động là dự án đầu tư của cơ sở kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện nhưng trong quá trình hoạt&nbsp;động cơ sở kinh doanh bị thu hồi giấy phép kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện; bị thu hồi giấy chứng nhận đủ điều kiện kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện; bị thu hồi văn bản của cơ quan nhà nước có thẩm quyền về đầu tư kinh doanh ngành, nghề đầu tư kinh doanh có điều kiện; hoặc trong quá trình hoạt động cơ sở kinh doanh không đáp ứng được điều kiện để thực hiện đầu tư kinh doanh có điều kiện theo quy định của pháp luật về đầu tư thì thời điểm không hoàn thuế giá trị gia tăng được tính từ thời điểm cơ sở kinh doanh bị thu hồi một trong các loại giấy tờ nêu trên hoặc từ thời điểm cơ quan nhà nước có&nbsp;thẩm quyền kiểm tra, phát hiện cơ sở kinh doanh không đáp ứng được các điều kiện về đầu tư kinh doanh có điều kiện.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp; c.4/ Dự án đầu tư khai thác tài nguyên, khoáng sản được cấp phép từ ngày 01/7/2016 hoặc dự án đầu tư sản xuất sản phẩm hàng hóa mà tổng trị giá tài nguyên, khoáng sản cộng với chi phí năng lượng chiếm từ 51% giá thành sản phẩm trở lên theo dự án đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Việc xác định tài nguyên, khoáng sản; trị giá tài nguyên, khoáng sản và thời điểm xác định trị giá tài nguyên, khoáng sản và chi phí năng lượng&nbsp;thực hiện theo hướng dẫn tại khoản 23 Điều 4 Thông tư này.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>4&gt; Hoàn thuế đối với hàng hóa, dịch vụ xuất khẩu</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp;&nbsp;4.a/&nbsp; Cơ sở kinh doanh trong tháng (đối với trường hợp kê khai theo tháng), quý (đối với trường hợp kê khai theo quý) có hàng hóa, dịch vụ xuất khẩu có số thuế giá trị gia tăng đầu vào chưa được khấu trừ từ 300 triệu đồng trở lên thì được hoàn thuế giá trị gia tăng theo tháng, quý; trường hợp trong tháng, quý số thuế giá trị gia tăng đầu vào chưa được khấu trừ chưa đủ 300 triệu đồng thì được khấu trừ vào tháng, quý tiếp theo.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Cơ sở kinh doanh trong tháng/quý vừa có hàng hoá, dịch vụ xuất khẩu, vừa có hàng hoá, dịch vụ bán trong nước thì cơ sở kinh doanh phải hạch toán riêng số thuế GTGT đầu vào sử dụng cho sản xuất kinh doanh hàng hóa, dịch vụ xuất khẩu. Trường hợp không hạch toán riêng được thì số thuế giá trị gia tăng đầu vào của hàng hóa, dịch vụ xuất khẩu được xác định theo tỷ lệ giữa doanh thu của hàng hóa, dịch vụ xuất khẩu trên tổng doanh thu hàng hóa, dịch vụ của các kỳ khai thuế giá trị gia tăng tính từ kỳ khai thuế tiếp theo kỳ hoàn thuế liền trước đến kỳ&nbsp;đề nghị hoàn thuế hiện tại.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Số thuế GTGT đầu vào của hàng hóa, dịch vụ xuất khẩu (bao gồm số thuế GTGT đầu vào hạch toán riêng được và số thuế GTGT đầu vào được phân bổ theo tỷ lệ nêu trên) nếu sau khi bù trừ với số thuế GTGT phải nộp của hàng hóa, dịch vụ tiêu thụ trong nước còn lại từ 300 triệu đồng trở lên thì cơ sở kinh doanh được hoàn thuế cho hàng hóa, dịch vụ xuất khẩu. Số thuế GTGT được hoàn của hàng hóa, dịch vụ xuất khẩu không vượt quá doanh thu của hàng hóa, dịch vụ xuất khẩu nhân (x) với 10%.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đối tượng được hoàn thuế trong một số trường hợp xuất khẩu như sau: Đối với trường hợp ủy thác xuất khẩu, là cơ sở có hàng hóa ủy thác xuất khẩu; đối với gia công chuyển tiếp, là cơ sở ký hợp đồng gia công xuất khẩu với phía nước ngoài; đối với hàng hóa xuất khẩu để&nbsp;thực hiện công trình xây dựng ở nước ngoài, là doanh nghiệp có hàng hóa, vật tư xuất khẩu thực hiện công trình xây dựng ở nước ngoài; đối với hàng hóa xuất khẩu tại chỗ là cơ sở kinh doanh có hàng hóa xuất khẩu tại chỗ.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Cơ sở kinh doanh không được hoàn thuế đối với trường hợp hàng hóa nhập khẩu sau đó xuất khẩu, hàng hóa xuất khẩu không thực hiện việc xuất khẩu tại địa bàn hoạt động hải quan theo quy định của Luật Hải quan, Nghị định số 01/2015/NĐ-CP ngày 02/01/2015 của Chính phủ quy định chi tiết thi hành phạm vi địa bàn hoạt động hải quan, trách nhiệm phối hợp trong phòng, chống buôn lậu, vận chuyển trái phép hàng hóa qua biên giới và các văn bản hướng dẫn.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Công ty thương mại A nhập khẩu 500 chiếc điều hòa từ Nhật Bản và đã nộp thuế GTGT ở khâu nhập khẩu. Sau đó Công ty thương mại A xuất khẩu 500 chiếc điều hòa này sang Campuchia thì Công ty thương mại A không phải tính thuế GTGT đầu ra, số thuế GTGT của 500 chiếc điều hòa này đã nộp ở khâu nhập khẩu và thuế GTGT đầu vào của dịch vụ vận chuyển, lưu kho không được hoàn thuế mà thực hiện khấu trừ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Công ty TNHH sản xuất và thương mại B xuất khẩu tinh bột sắn sang Trung Quốc qua đường mòn, lối mở không thuộc địa bàn hoạt động hải quan thì Công ty TNHH sản xuất và thương mại B không được hoàn thuế GTGT đối với tinh bột sắn xuất khẩu. c) Cơ quan thuế thực hiện hoàn thuế trước, kiểm tra sau đối với người nộp thuế sản xuất hàng hóa xuất khẩu không bị xử lý đối với hành vi buôn lậu, vận chuyển trái phép hàng hóa qua biên giới, trốn thuế, gian lận thuế, gian lận thương mại trong thời gian hai năm liên tục; người nộp thuế không thuộc đối tượng rủi ro cao theo quy định của Luật quản lý thuế và các văn bản hướng dẫn thi hành.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Tháng 9/2016, Công ty C đề nghị hoàn thuế GTGT đối với hàng xuất khẩu sang HongKong. Tháng 6/2015, Công ty C đã bị xử lý vi phạm pháp luật về thuế đối với hành vi trốn thuế. Cơ quan thuế thực hiện kiểm tra trước khi hoàn thuế đối với đề nghị hoàn thuế của Công ty C.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>&nbsp; &nbsp; 5&gt; Cơ sở kinh doanh nộp thuế giá trị gia tăng theo phương pháp khấu trừ thuế</em></strong>&nbsp;được hoàn thuế giá trị gia tăng khi chuyển đổi sở hữu, chuyển đổi doanh nghiệp, sáp nhập, hợp nhất, chia, tách, giải thể, phá sản, chấm dứt hoạt động có số thuế giá trị gia tăng nộp thừa hoặc số thuế giá trị gia tăng đầu vào chưa được khấu trừ hết.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Cơ sở kinh doanh trong giai đoạn đầu tư chưa đi vào hoạt động sản xuất kinh doanh nhưng phải giải thể, phá sản hoặc chấm dứt hoạt động chưa phát sinh thuế giá trị gia tăng đầu ra của hoạt động kinh doanh chính theo dự án đầu tư thì chưa phải điều chỉnh lại số thuế giá trị gia tăng đã kê khai, khấu trừ hoặc đã được hoàn. Cơ sở kinh doanh phải thông báo với cơ quan thuế quản lý trực tiếp về việc giải thể, phá sản, chấm dứt hoạt động theo quy định.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trường hợp cơ sở kinh doanh sau khi làm đầy đủ thủ tục theo quy định của pháp luật về giải thể, phá sản thì đối với số thuế GTGT đã được hoàn thực hiện theo quy định của pháp luật về giải thể, phá sản và quản lý thuế; đối với số thuế GTGT chưa được hoàn thì không được giải quyết hoàn thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trường hợp cơ sở kinh doanh chấm dứt hoạt động và không phát sinh thuế GTGT đầu ra của hoạt động kinh doanh chính thì phải nộp lại số thuế đã được hoàn vào ngân sách nhà nước. Trường hợp có phát sinh bán tài sản chịu thuế GTGT thì không phải điều chỉnh lại số thuế GTGT đầu vào tương ứng của tài sản bán ra.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Năm 2015, doanh nghiệp A trong giai đoạn đầu tư chưa đi vào hoạt động sản xuất kinh doanh, doanh nghiệp A có phát sinh số thuế GTGT đầu vào của giai đoạn đầu tư đã được cơ quan thuế hoàn trong tháng 8/2015 là 700 triệu đồng. Do khó khăn, tháng 2/2016 doanh nghiệp A quyết định giải thể và có văn bản gửi cơ quan thuế về việc sẽ giải thể thì trong giai đoạn doanh nghiệp A chưa hoàn thành thủ tục pháp lý để giải thể, cơ quan thuế chưa thu hồi lại thuế GTGT đã hoàn. Hai mươi ngày trước khi doanh nghiệp A có đủ thủ tục pháp lý để giải thể chính thức vào tháng 10/2016, doanh nghiệp thực hiện bán một (01) tài sản đã đầu tư thì doanh nghiệp A không phải điều chỉnh lại số thuế GTGT đầu vào tương ứng của tài sản bán ra (số thuế đã được cơ quan thuế hoàn). Đối với những tài sản không bán ra, doanh nghiệp A phải kê khai điều chỉnh để nộp lại số thuế GTGT đã được hoàn.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>&nbsp; &nbsp;6&gt; Hoàn thuế GTGT đối với các chương trình, dự án sử dụng nguồn vốn hỗ trợ phát triển chính thức (ODA) không hoàn lại hoặc viện trợ không hoàn lại, viện trợ nhân đạo.</em></strong></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:16px;\">a) Đối với dự án sử dụng vốn ODA không hoàn lại: chủ chương trình, dự án hoặc nhà thầu chính, tổ chức do phía nhà tài trợ nước ngoài chỉ định việc quản lý chương trình, dự án được hoàn lại số thuế GTGT đã trả đối với hàng hoá, dịch vụ mua ở Việt Nam để sử dụng cho chương trình, dự án.</span></li>\r\n	<li><span style=\"font-size:16px;\">b) Tổ chức ở Việt Nam sử dụng tiền viện trợ nhân đạo của tổ chức, cá nhân nước ngoài để mua hàng hoá, dịch vụ phục vụ cho chương trình, dự án viện trợ không hoàn lại, viện trợ nhân đạo tại Việt Nam thì được hoàn thuế GTGT đã trả của hàng hoá, dịch vụ đó.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Ví dụ:</strong>&nbsp;Hội chữ thập đỏ được Tổ chức quốc tế viện trợ tiền để mua hàng viện trợ nhân đạo cho nhân dân các tỉnh bị thiên tai là 200 triệu đồng. Giá trị hàng mua chưa có thuế là 200 triệu đồng, thuế GTGT là 20 triệu đồng. Hội chữ thập đỏ sẽ được hoàn thuế theo quy định là 20 triệu đồng. Việc hoàn thuế GTGT đã trả đối với các chương trình, dự án sử dụng nguồn vốn hỗ trợ phát triển chính thức (ODA) không hoàn lại thực hiện theo hướng dẫn của Bộ Tài chính.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>&nbsp;&nbsp;&nbsp;7&gt; Đối tượng được hưởng quyền ưu đãi miễn trừ ngoại giao</em></strong>&nbsp;theo quy định của pháp luật về ưu đãi miễn trừ ngoại giao mua hàng hóa, dịch vụ tại Việt Nam để sử dụng được hoàn số thuế giá trị gia tăng đã trả ghi trên hoá đơn giá trị gia tăng hoặc trên chứng từ thanh toán ghi giá thanh toán đã có thuế giá trị gia tăng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>&nbsp; &nbsp;8&gt; Người nước ngoài, người Việt Nam định cư ở nước ngoài mang hộ chiếu hoặc giấy tờ nhập cảnh do cơ quan có thẩm quyền nước ngoài cấp được hoàn thuế đối với&nbsp;hàng hoá mua tại Việt Nam</em></strong>mang theo người khi xuất cảnh. Việc hoàn thuế GTGT thực hiện theo hướng dẫn của Bộ Tài chính về hoàn thuế GTGT đối với hàng hoá của người nước ngoài, người Việt Nam định cư ở nước ngoài mua tại Việt Nam mang theo khi xuất cảnh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>&nbsp;&nbsp;&nbsp;8&gt; Cơ sở kinh doanh có quyết định xử lý hoàn thuế của cơ quan có thẩm quyền theo quy định của pháp luật và trường hợp hoàn thuế GTGT theo điều ước quốc tế mà Cộng hòa xã hội chủ nghĩa Việt Nam là thành viên.</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>( Theo khoản 3, Điều 1, Thông tư 130/2016/TT-BTC )</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp;1. Điều kiện hoàn thuế GTGT.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash;&nbsp;Là cơ sở kinh doanh nộp thuế theo phương pháp khấu trừ,</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đã được cấp giấy chứng nhận đăng ký doanh nghiệp hoặc giấy phép đầu tư (giấy phép hành nghề) hoặc quyết định thành lập của cơ quan có thẩm quyền,</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Có con dấu theo đúng quy định của pháp luật,</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Lập và lưu giữ sổ kế toán, chứng từ kế toán theo quy định của pháp luật về kế toán;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Có tài khoản tiền gửi tại ngân hàng theo mã số thuế của cơ sở kinh doanh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>(Theo điều 19 Thông tư 219/2013/TT-BTC)</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp;1. Hồ sơ hoàn thuế GTGT gồm:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Kể từ ngày 13/8/2016 Theo điều 10 Thông tư 99/2016/TT-BTC ngày 29/6/2016</strong><strong>, quy định cụ thể như sau:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Người nộp thuế được gửi hồ sơ hoàn thuế điện tử hoặc nộp trực tiếp tại cơ quan thuế hoặc gửi qua đường bưu chính.(gửi đến cơ quan thuế quản lý trực tiếp người nộp thuế)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Chi nhánh, đơn vị phụ thuộc của người nộp thuế theo quy định tại Khoản 1 Điều 45 Luật Doanh nghiệp số 68/2014/QH13 và không phải là đơn vị kế toán lập báo cáo tài chính theo quy định tại Khoản 4 Điều 3 Luật Kế toán số 88/2015/QH13, khi đề nghị hoàn thuế giá trị gia tăng phải có văn bản ủy quyền của doanh nghiệp trụ sở chính theo quy định của pháp luật về uỷ quyền.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Hồ sơ hoàn thuế giá trị gia tăng cụ thể như sau:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp;1. Hồ sơ hoàn thuế GTGT dự án đầu tư:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy đề nghị hoàn trả khoản thu NSNN mẫu 01/ĐNHT (ban hành kèm theo Thông tư 156/2013/TT-BTC).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tờ khai thuế GTGT dành cho dự án đầu tư theo mẫu số 02/GTGT</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bảng kê hóa đơn, chứng từ hàng hóa dịch vụ mua vào theo mẫu số 01-2/GTGT.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp;2. Hồ sơ hoàn thuế GTGT hàng xuất khẩu:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy đề nghị hoàn trả Khoản thu ngân sách nhà nước mẫu 01/ĐNHT (ban hành kèm theo Thông tư 156/2013/TT-BTC).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Hợp đồng mua bán, gia công, tờ khai hải quan, chứng từ thanh toán qua ngân hàng&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>III. Hoàn thuế GTGT đối với dự án ODA:</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\"><strong><em>Đối với Chủ dự án và Nhà thầu chính</em></strong></span></li>\r\n	<li><span style=\"font-size:16px;\">a) Nộp hồ sơ đề nghị hoàn thuế GTGT</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Chủ dự án ODA nộp hồ sơ đề nghị hoàn thuế GTGT gửi Cục Thuế quản lý trực tiếp địa bàn nơi thực hiện dự án vào bất kỳ thời điểm nào khi phát sinh số thuế GTGT đầu vào được hoàn trong thời gian thực hiện dự án; trường hợp dự án ODA có liên quan đến nhiều tỉnh thì hồ sơ hoàn thuế GTGT gửi Cục Thuế quản lý trực tiếp nơi chủ dự án đóng trụ sở chính.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Nhà thầu nước ngoài thực hiện dự án ODA nộp hồ sơ đề nghị hoàn thuế GTGT gửi cơ quan thuế nơi nộp hồ sơ khai thuế.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Hồ sơ hoàn thuế GTGT</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy đề nghị hoàn trả Khoản thu ngân sách nhà nước mẫu 01/ĐNHT (ban hành kèm theo Thông tư 156/2013/TT-BTC).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bảng kê hóa đơn, chứng từ hàng hóa dịch vụ mua vào mẫu số 01-1/ĐNHT ban hành kèm theo Thông tư 156;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Quyết định của cấp có thẩm quyền về việc phê duyệt dự án sử dụng vốn ODA không hoàn lại, dự án sử dụng vốn ODA vay ưu đãi được ngân sách nhà nước cấp phát (bản chụp có đóng dấu và chữ ký xác nhận của người có thẩm quyền của dự án). Trường hợp hoàn thuế nhiều lần thì chỉ xuất trình lần đầu.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Xác nhận của cơ quan chủ quản dự án ODA về hình thức cung cấp dự án ODA là ODA không hoàn lại hay ODA vay được ngân sách nhà nước cấp phát ưu đãi thuộc đối tượng được hoàn thuế giá trị gia tăng (bản chụp có xác nhận của cơ sở) và việc không được ngân sách nhà nước cấp vốn đối ứng để trả thuế giá trị gia tăng. Người nộp thuế chỉ phải nộp tài liệu này với hồ sơ hoàn thuế lần đầu của dự án.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trường hợp nhà thầu chính lập hồ sơ hoàn thuế thì ngoài những tài liệu nêu tại điểm này, còn phải có xác nhận của chủ dự án về việc dự án không được ngân sách nhà nước bố trí vốn đối ứng để thanh toán cho nhà thầu theo giá có thuế giá trị gia tăng; giá thanh toán theo kết quả thầu không có thuế giá trị gia tăng và đề nghị hoàn thuế cho nhà thầu chính.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:16px;\"><strong><em>Đối với Văn phòng đại diện nhà tài trợ dự án ODA</em></strong></span></li>\r\n	<li><span style=\"font-size:16px;\">a) Nộp hồ sơ đề nghị hoàn thuế GTGT Văn phòng đại diện nhà tài trợ dự án ODA thuộc diện được hoàn thuế GTGT nộp hồ sơ đề nghị hoàn thuế GTGT gửi Cục Thuế quản lý địa bàn nơi đặt văn phòng điều hành của dự án vào bất kỳ thời điểm nào khi phát sinh số thuế GTGT đầu vào được hoàn trong thời gian thực hiện dự án.</span></li>\r\n	<li><span style=\"font-size:16px;\">b) Hồ sơ hoàn thuế GTGT</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy đề nghị hoàn trả Khoản thu ngân sách nhà nước mẫu 01/ĐNHT (ban hành kèm theo Thông tư 156/2013/TT-BTC).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bảng kê hóa đơn, chứng từ hàng hóa dịch vụ mua vào mẫu số 01-1/ĐNHT ban hành kèm theo Thông tư 156;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Văn bản thỏa thuận giữa cơ quan nhà nước có thẩm quyền của Việt Nam với Nhà tài trợ về việc thành lập Văn phòng đại diện (bản chụp có xác nhận của Văn phòng).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Văn bản của cơ quan nhà nước có thẩm quyền về việc thành lập Văn phòng đại diện (bản chụp có xác nhận của Văn phòng).</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\"><strong>Hoàn thuế đối với tổ chức, cá nhân nước ngoài, tổ chức ở Việt Nam sử dụng tiền viện trợ nhân đạo, viện trợ không hoàn lại của nước ngoài mua hàng hoá, dịch vụ có thuế GTGT ở Việt Nam để viện trợ không hoàn lại, viện trợ nhân đạo.</strong></span></li>\r\n	<li><span style=\"font-size:16px;\"><strong><em>Nộp hồ sơ đề nghị hoàn thuế GTGT</em></strong></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Các tổ chức, cá nhân nêu trên nộp hồ sơ đề nghị hoàn thuế GTGT vào bất kỳ thời điểm nào khi phát sinh số thuế GTGT đầu vào được hoàn.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:16px;\"><strong><em>Hồ sơ hoàn thuế GTGT</em></strong></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy đề nghị hoàn trả Khoản thu ngân sách nhà nước mẫu 01/ĐNHT (ban hành kèm theo Thông tư 156/2013/TT-BTC).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bảng kê hóa đơn, chứng từ hàng hóa dịch vụ mua vào mẫu số 01-1/ĐNHT ban hành kèm theo Thông tư 156;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Văn bản phê duyệt các khoản viện trợ của cấp có thẩm quyền (bản chụp có xác nhận của người nộp thuế);</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Văn bản xác nhận của Bộ Tài chính (đối với viện trợ nước ngoài thuộc nguồn thu ngân sách trung ương) hoặc của Sở Tài chính (đối với viện trợ nước ngoài thuộc nguồn thu ngân sách địa phương) về khoản tiền viện trợ của tổ chức phi chính phủ nước ngoài nêu rõ: tên tổ chức viện trợ, giá trị khoản viện trợ, cơ quan tiếp nhận, quản lý viện trợ.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\"><strong>Hoàn thuế GTGT đối với đối tượng hưởng ưu đãi miễn trừ ngoại giao.</strong></span></li>\r\n	<li><span style=\"font-size:16px;\"><strong><em>Nộp hồ sơ đề nghị hoàn thuế và thủ tục giải quyết hồ sơ hoàn thuế GTGT</em></strong></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Các Cơ quan đại diện thuộc đối tượng được hoàn thuế GTGT, trong vòng 10 (mười) ngày đầu của tháng đầu quý, lập hồ sơ đề nghị hoàn thuế của quý trước và gửi hồ sơ cho Cục Lễ tân nhà nước trực thuộc Bộ Ngoại giao để xác nhận hồ sơ hoàn thuế GTGT.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trong vòng 15 (mười lăm) ngày làm việc kể từ khi nhận đầy đủ hồ sơ, Cục Lễ tân nhà nước có trách nhiệm xem xét hồ sơ và xác nhận về đối tượng, danh mục và số lượng hàng hoá, dịch vụ được hoàn thuế GTGT. Sau khi Cục Lễ tân nhà nước đã có xác nhận hồ sơ hoàn thuế GTGT, Cục Lễ tân nhà nước chuyển hồ sơ cho Cục Thuế các tỉnh, thành phố trực thuộc Trung ương giải quyết.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đối với trường hợp không thuộc đối tượng được hoàn thuế GTGT hoặc hồ sơ hoàn thuế GTGT được lập không đầy đủ, Cục Lễ tân nhà nước gửi trả hồ sơ hoàn thuế cho đối tượng hưởng ưu đãi miễn trừ ngoại giao trong vòng 05 (năm) ngày kể từ ngày nhận hồ sơ.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:16px;\">Hồ sơ hoàn thuế GTGT &ndash; Giấy đề nghị hoàn trả Khoản thu ngân sách nhà nước mẫu 01/ĐNHT (ban hành kèm theo Thông tư 156/2013/TT-BTC) có xác nhận của Cục Lễ tân nhà nước trực thuộc Bộ Ngoại giao.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bảng kê thuế GTGT của hàng hóa, dịch vụ mua vào dùng cho cơ quan đại diện ngoại giao theo mẫu số 01-2/ĐNHT ban hành kèm theo Thông tư này và Bảng kê thuế GTGT của hàng hóa, dịch vụ mua vào dùng cho viên chức ngoại giao tại Việt Nam theo mẫu số 01-3/ĐNHT ban hành kèm theo Thông tư 156.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bản gốc kèm theo 02 (hai) bản chụp hóa đơn GTGT có đóng dấu của cơ quan đại diện. Cơ quan thuế sẽ trả lại các hóa đơn gốc cho cơ quan đại diện sau khi thực hiện hoàn thuế GTGT.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\"><strong>Hoàn thuế theo Hiệp định tránh đánh thuế hai lần</strong></span></li>\r\n	<li><span style=\"font-size:16px;\"><strong><em>Nộp hồ sơ đề nghị hoàn thuế</em></strong></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tổ chức, cá nhân nộp hồ sơ hoàn thuế theo Hiệp định tránh đánh thuế hai lần tại Cục Thuế quản lý địa bàn nơi tổ chức có trụ sở điều hành, nơi cá nhân có cơ sở thường trú hoặc tại Cục Thuế nơi tổ chức, cá nhân đã nộp số thuế đề nghị hoàn.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:16px;\"><strong><em>Hồ sơ hoàn thuế</em></strong></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy đề nghị hoàn thuế theo Hiệp định tránh đánh thuế hai lần theo mẫu số 02/ĐNHT ban hành kèm theo Thông tư 156.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bản gốc (hoặc bản chụp đã được chứng thực) Giấy chứng nhận cư trú do cơ quan thuế của nước cư trú cấp đã được hợp pháp hóa lãnh sự (ghi rõ là đối tượng cư trú trong năm tính thuế nào).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bản chụp hợp đồng kinh tế, hợp đồng cung cấp dịch vụ, hợp đồng đại lý, hợp đồng uỷ thác, hợp đồng chuyển giao công nghệ hay hợp đồng lao động ký với tổ chức, cá nhân Việt Nam, giấy chứng nhận tiền gửi tại Việt Nam, giấy chứng nhận góp vốn vào Công ty tại Việt Nam (tuỳ theo loại thu nhập trong từng trường hợp cụ thể) có xác nhận của người nộp thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Xác nhận của tổ chức, cá nhân Việt Nam ký kết hợp đồng về thời gian và tình hình hoạt động thực tế theo hợp đồng (trừ trường hợp hoàn thuế đối với hãng vận tải nước ngoài).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy ủy quyền trong trường hợp tổ chức, cá nhân uỷ quyền cho đại diện hợp pháp thực hiện thủ tục áp dụng Hiệp định. Trường hợp tổ chức, cá nhân lập giấy uỷ quyền để uỷ quyền cho đại diện hợp pháp thực hiện thủ tục hoàn thuế vào tài khoản của đối tượng khác cần thực hiện thủ tục hợp pháp hóa lãnh sự (nếu việc uỷ quyền được thực hiện ở nước ngoài) hoặc công chứng (nếu việc uỷ quyền được thực hiện tại Việt Nam) theo quy định.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trường hợp tổ chức, cá nhân không thể cung cấp đủ các thông tin hoặc các tài liệu theo yêu cầu của Hồ sơ hoàn thuế theo Hiệp định, đề nghị giải trình cụ thể tại Giấy đề nghị hoàn thuế theo Hiệp định mẫu số 02/ĐNHT nêu trên để cơ quan thuế xem xét, quyết định.&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>( Theo Điều 10, Thông tư 99/2016/TT-BTC NGÀY 29/06/2016)</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>VII. Hồ sơ hoàn thuế, phí nộp thừa đối với người nộp thuế sáp nhập, hợp nhất, chia, tách, giải thể, phá sản, chuyển đổi sở hữu, chấm dứt hoạt động</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy đề nghị hoàn trả Khoản thu ngân sách nhà nước mẫu 01/ĐNHT (ban hành kèm theo Thông tư 156/2013/TT-BTC)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Quyết định của cấp có thẩm quyền về việc sáp nhập, hợp nhất, chia, tách, giải thể, phá sản, chuyển đổi sở hữu, chấm dứt hoạt động;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Hồ sơ quyết toán thuế hoặc hồ sơ khai thuế đến thời điểm sáp nhập, hợp nhất, chia, tách, giải thể, phá sản, chuyển đổi sở hữu, chấm dứt hoạt động.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>VIII. Hoàn thuế GTGT đối với thiết bị, máy móc, phương tiện vận tải chuyên dùng nằm trong dây chuyền công nghệ và vật tư xây dựng thuộc loại trong nước chưa sản xuất được cần nhập khẩu để tạo tài sản cố định của doanh nghiệp</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\"><strong><em>Phạm vi áp dụng</em></strong></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Thực hiện theo hướng dẫn tại Điều 1 Thông tư số 92/2010/TT-BTC ngày 17/6/2010 của Bộ Tài chính hướng dẫn thủ tục gia hạn nộp thuế và hoàn thuế GTGT đối với thiết bị, máy móc, phương tiện vận tải chuyên dùng nằm trong dây chuyền công nghệ và vật tư xây dựng thuộc loại trong nước chưa sản xuất được cần nhập khẩu để tạo tài sản cố định của doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trường hợp cơ sở kinh doanh thành lập từ dự án đầu tư đang trong giai đoạn đầu tư nhưng chưa đi vào hoạt động, nếu có nhu cầu đề nghị hoàn thuế trước khi nộp thuế GTGT khâu nhập khẩu hàng hóa thì thực hiện lập hồ sơ đề nghị hoàn thuế GTGT riêng đối với hàng hóa nhập khẩu thuộc loại trong nước chưa sản xuất được nêu trên và kê khai vào hồ sơ khai thuế GTGT dành cho dự án đầu tư theo quy định.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trường hợp cơ sở kinh doanh đang hoạt động thuộc đối tượng nộp thuế GTGT theo phương pháp khấu trừ có dự án đầu tư xây dựng dây chuyền sản xuất, đang trong giai đoạn đầu tư:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Đối với dự án đầu tư tại địa bàn cùng tỉnh, thành phố trực thuộc Trung ương nơi đóng trụ sở chính thì cơ sở kinh doanh phải kê khai bù trừ số thuế GTGT hàng hóa nhập khẩu thuộc loại trong nước chưa sản xuất được nêu trên cùng với việc kê khai thuế GTGT của hoạt động sản xuất kinh doanh đang thực hiện. Trường hợp sau khi bù trừ nếu có số thuế GTGT hàng hóa nhập khẩu thuộc loại trong nước chưa sản xuất được nêu trên mà chưa được khấu trừ hết thì được hoàn thuế theo dự án đầu tư.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Đối với dự án đầu tư tại địa bàn tỉnh, thành phố trực thuộc Trung ương khác với tỉnh, thành phố nơi đóng trụ sở chính, nếu có nhu cầu đề nghị hoàn thuế trước khi nộp thuế GTGT khâu nhập khẩu hàng hóa thì thực hiện lập hồ sơ đề nghị hoàn thuế GTGT riêng đối với hàng hóa nhập khẩu thuộc loại trong nước chưa sản xuất được nêu trên và kê khai vào hồ sơ khai thuế GTGT dành cho dự án đầu tư theo quy định.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:16px;\"><strong><em>Hồ sơ hoàn thuế</em></strong></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy đề nghị hoàn trả khoản thu Ngân sách nhà nước theo mẫu số 01/ĐNHT ban hành kèm theo Thông tư 156, trong đó ghi rõ số thuế GTGT hàng nhập khẩu của thiết bị, máy móc, phương tiện vận tải chuyên dùng nằm trong dây chuyền công nghệ và vật tư xây dựng thuộc loại trong nước chưa sản xuất được cần nhập khẩu để tạo tài sản cố định của doanh nghiệp và cam kết của doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bảng kê hồ sơ hàng hóa nhập khẩu theo mẫu số 01-4/ĐNHT ban hành kèm theo Thông tư 156.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>PHÍ DỊCH VỤ HOÀN THUẾ</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>BIỂU PHÍ DỊCH VỤ HOÀN THUẾ&nbsp;</strong><strong>TẠI NGUYỄN THẮNG</strong>&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>================</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi nghiên cứu yêu cầu dịch vụ của Quý doanh nghiệp và căn cứ các thông tin của Quý doanh nghiệp cung cấp.&nbsp;<strong>KẾ TOÁN NGUYỄN THẮNG</strong>&nbsp;xin đưa ra gói dịch vụ nhằm giúp Quý doanh nghiệp giải quyết được các vấn đề còn vướng mắc một cách hiệu quả nhất. Các phần hành công việc như sau:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp;&nbsp;&nbsp; 1/ Nội dung công việc</strong><strong>thực hiện:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Hướng dẫn khách hàng chuẩn bị chứng từ , sổ sách kế toán để hoàn thuế GTGT;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tư vấn,hướng dẫn khách hàng điều chỉnh hồ sơ hoàn thuế GTGT;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Kiểm tra, soát xét hồ sơ hoàn thuế GTGT;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Soạn thảo hồ sơ hoàn thuế GTGT;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Chuẩn bị hồ sơ hoàn thuế GTGT;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Thay mặt doanh nghiệp nộp hồ sơ hoàn thuế tại cơ quan thuế;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Theo dõi, cập nhật thông tin hồ sơ hoàn thuế;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Thay mặt doanh nghiệp giải trình với cơ quan thuế các nội dung liên quan đến hồ sơ hoàn thuế;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Nhận quyết định hoàn thuế tại cơ quan thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Công việc của khách hàng:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Cung cấp và chuẩn bị chứng từ, sổ sách kế toán theo yêu cầu của kế toán&nbsp;<strong>NGUYỄN THẮNG</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; 2/ Thời gian bắt đầu&nbsp;:</strong>&nbsp;Ngay sau khi ký kết hợp đồng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; 3/ Thời gian hoàn thành</strong>: 60 ngày.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp; 4/ Phí dịch vụ và tiến độ thanh toán&nbsp;:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Phí dịch vụ&nbsp;: 0,5%&nbsp; đến 5% số tiền xin hoàn thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Phí dịch vụ tối thiểu&nbsp;: 15.000.000đ/ hồ sơ.</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Tiến độ thanh toán&nbsp;:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đợt 1&nbsp;: Ứng 40% phí dịch vụ ngay sau khi ký hợp đồng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đợt 2&nbsp;: Ứng 40% phí dịch vụ ngay sau khi cơ quan thuế tiếp nhận hồ sơ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đợt 3&nbsp;: Thanh toán phí dịch vụ còn lại( 20%) sau khi có quyết định hoàn thuế</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Phí dịch vụ&nbsp; trên chưa bao gồm&nbsp; 10% thuế VAT.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Phí dịch vụ trên là chi phí hoàn thiện hồ sơ xin hoàn thuế và giải trình các nội dung liên quan đến hoàn thuế</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp; &nbsp;5. Phương thức thanh toán:&nbsp;</strong>Chuyển khoản.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>Bằng tất cả sự tận tâm, chuyên nghiệp và thái độ phục vụ tốt nhất, chúng tôi đã và đang là nhà cung cấp dịch vụ uy tín cho hàng trăm khách hàng lớn, nhỏ.</strong></em></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 07:30:35', '2018-11-26 07:30:49', NULL);
INSERT INTO `posts` (`id`, `title`, `path`, `description`, `content`, `image`, `post_type`, `is_active`, `category_item_id`, `user_id`, `created_at`, `updated_at`, `seo_id`) VALUES
(14, 'Thủ tục chuyển địa điểm đăng ký kinh doanh', 'thu-tuc-chuyen-dia-diem-dang-ky-kinh-doanh', NULL, '<h1><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><strong><span style=\"font-size:18px;\">THỦ TỤC CHUYỂN ĐỊA ĐIỂM ĐĂNG KÝ KINH DOANH</span></strong></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Thủ tục chuyển địa điểm đăng ký kinh doanh</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Thủ tục cần thiết khi&nbsp;chuyển địa điểm đăng ký kinh doanh như sau :</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2017/11/thong-bao-thay-doi-dia-chi-cong-ty.jpg\"><img alt=\"\" sizes=\"(max-width: 627px) 100vw, 627px\" src=\"https://ketoanuytin.com/wp-content/uploads/2017/11/thong-bao-thay-doi-dia-chi-cong-ty-300x169.jpg\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2017/11/thong-bao-thay-doi-dia-chi-cong-ty-300x169.jpg 300w, https://ketoanuytin.com/wp-content/uploads/2017/11/thong-bao-thay-doi-dia-chi-cong-ty-100x56.jpg 100w, https://ketoanuytin.com/wp-content/uploads/2017/11/thong-bao-thay-doi-dia-chi-cong-ty.jpg 310w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>I. Liên hệ với sở kế hoạch đầu tư:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Việc đầu tiên các bạn cần làm là liên hệ với Sở KHĐT về thủ tục thay đổi giấy ĐKKD, các bạn lên đó sẽ được hướng dẫn cần làm các mẫu giấy tờ gì cần thiết:<br />\r\n<em>Thành phần hồ sơ, bao gồm:</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Thông báo thay đổi nội dung đăng ký doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Quyết định bằng văn bản và bản sao biên bản họp của Hội đồng thành viên về việc thay đổi các nội dung đăng ký của doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Các giấy tờ, tài liệu khác kèm theo tương ứng đối với một số trường hợp thay đổi: (Vì có nhiều trường hợp cần thay đổi như: Thay đổi giấy phép ĐKKD, Thay đổi tên công ty, thay đổi địa chỉ, thay đổi ngành nghề kinh doanh, thay đổi vốn điều lệ..Tùy từng trường hợp mà cần thêm các giấy tờ khác, nên tốt nhất các bạn liên hệ với Sở kế hoạch đầu tư để biết chi tiết và tải mẩu về)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Số lượng hồ sơ: 01&nbsp; (bộ)</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi có giấy ĐKKD mới thì bạn gửi thông báo thay đổi thông tin đăng ký thuế đến cơ quan thuế, cụ thể như sau:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>II. Liên hệ với cơ quan thuế:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Theo Mục 2 Thông tư 80/2012/TT-BTC ngày 22/5/2012 có hiệu lực từ ngày 01/7/2012: Quy định về việc Thay đổi thông tin đăng ký thuế cụ thể như sau:</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>1. Trách nhiệm và thời hạn thông báo thay đổi thông tin đăng ký:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>a. Đối với các doanh nghiệp</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Nếu doanh nghiệp thay đổi nội dung đăng ký thuế mà không thay đổi nội dung đăng ký kinh doanh thì chỉ cần: gửi Công văn đến Phòng đăng ký kinh doanh cấp tỉnh nơi doanh nghiệp đặt trụ sở chính.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Nội dung công văn gồm:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+. Tên, địa chỉ trụ sở chính, mã số doanh nghiệp và ngày cấp Giấy chứng nhận đăng ký kinh doanh, Giấy chứng nhận đăng ký thuế hoặc Giấy chứng nhận đăng ký doanh nghiệp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+. Nội dung thay đổi thông tin đăng ký thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>b. Các tổ chức, hộ gia đình và cá nhân khác:</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Phải thông báo cho cơ quan Thuế trực tiếp quản lý trong thời hạn 10 (mười) ngày kể từ ngày có sự thay đổi theo Mẫu 08/MST (ban hành kèm theo&nbsp;<strong><em>Thông tư 156/2013/TT-BTC</em></strong>).<br />\r\n<strong>2. Hồ sơ, thủ tục thay đổi thông tin:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>a. Đổi tên cơ sở kinh doanh:</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Cơ sở kinh doanh khi đổi tên phải khai bổ sung với cơ quan thuế trực tiếp quản lý.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Hồ sơ gồm:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tờ khai điều chỉnh đăng ký thuế theo mẫu 08-MST ban hành kèm theo Thông tư 156/2013/TT-BTC .</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Giấy chứng nhận đăng ký thuế (bản gốc).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bản sao không yêu cầu chứng thực Giấy chứng nhận đăng ký kinh doanh điều chỉnh theo tên mới;<br />\r\nTrong thời hạn 03 (ba) ngày làm việc kể từ ngày nhận được đầy đủ hồ sơ khai bổ sung, cơ quan thuế cấp lại Giấy chứng nhận đăng ký thuế cho DN theo tên mới với mã số thuế đã được cấp trước đây, đồng thời thu hồi Giấy chứng nhận đăng ký thuế cũ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>b. Chuyển địa điểm đăng ký kinh doanh:</strong></em></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2017/11/t%E1%BA%A3i-xu%E1%BB%91ng-1.jpg\"><img alt=\"\" sizes=\"(max-width: 494px) 100vw, 494px\" src=\"https://ketoanuytin.com/wp-content/uploads/2017/11/t%E1%BA%A3i-xu%E1%BB%91ng-1.jpg\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2017/11/tải-xuống-1.jpg 276w, https://ketoanuytin.com/wp-content/uploads/2017/11/tải-xuống-1-100x66.jpg 100w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trường hợp chuyển địa điểm kinh doanh, người nộp thuế phải khai báo với cơ quan thuế quản lý trực tiếp để làm thủ tục chuyển địa điểm. Mọi trường hợp thay đổi địa điểm kinh doanh đều không thay đổi mã số thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>+/ Trường hợp chuyển địa điểm kinh doanh trong cùng địa bàn tỉnh:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Hồ sơ gồm:</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tờ khai điều chỉnh đăng ký thuế theo mẫu 08-MST, trong đó ghi rõ thông tin thay đổi về địa điểm kinh doanh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trường hợp người nộp thuế thuộc Cục Thuế trực tiếp quản lý thì hồ sơ được gửi đến Cục Thuế. Cục Thuế trực tiếp quản lý người nộp thuế có trách nhiệm cập nhật các thông tin thay đổi vào hệ thống dữ liệu đăng ký thuế trong thời hạn 02 (hai) ngày làm việc kể từ ngày nhận đủ hồ sơ điều chỉnh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trường hợp người nộp thuế thuộc Chi cục Thuế quản lý thì hồ sơ được lập thành 02 (hai) bộ để gửi đến Chi cục Thuế nơi người nộp thuế chuyển đi và Chi cục Thuế nơi người nộp thuế chuyển đến. Chi cục Thuế nơi người nộp thuế chuyển đi phải lập thông báo tình hình nộp thuế của người nộp thuế theo mẫu số 09-MST gửi đến Chi cục Thuế nơi người nộp thuế chuyển đến và Cục Thuế trong thời hạn 03 (ba) ngày làm việc kể từ ngày nhận được hồ sơ đề nghị chuyển địa điểm.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>+/ Trường hợp chuyển địa điểm đăng ký kinh doanh giữa các tỉnh:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>&ndash; Tại nơi người nộp thuế chuyển đi.</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Hồ sơ khai gồm:</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Thông báo chuyển địa điểm;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Giấy chứng nhận đăng ký thuế (bản gốc);</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Thông báo tình trạng kê khai, nộp thuế của đơn vị chuyển địa điểm kinh doanh theo mẫu 09-MST ban hành kèm theo Thông tư 80/2012/TT-BTC.&nbsp;<em>(DN bạn phải quyết toán thuế với cơ quan thuế cũ sau đó họ sẽ cấp cho mẫu 09/MST)</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trong thời hạn 03 (ba) ngày làm việc, kể từ ngày nhận được hồ sơ khai chuyển địa điểm của người nộp thuế, cơ quan thuế nơi người nộp thuế chuyển đi thu hồi Giấy chứng nhận đăng ký thuế và lập thông báo tình hình nộp thuế của người nộp thuế theo mẫu 09-MST gửi 01 (một) bản cho người nộp thuế, 01 (một) bản cho cơ quan thuế nơi người nộp thuế chuyển đến.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>&ndash; Tại nơi người nộp thuế chuyển địa điểm đăng ký kinh doanh&nbsp;đến:</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trong thời hạn 05 (năm) ngày làm việc, kể từ ngày được cấp đổi Giấy phép kinh doanh, Giấy chứng nhận đầu tư&hellip; theo địa chỉ mới, người nộp thuế phải thực hiện đăng ký thuế tại cơ quan thuế nơi chuyển đến.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Hồ sơ đăng ký thuế gồm:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Tờ khai đăng ký thuế (ghi mã số thuế đã được cấp trước đó);</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Bản sao không yêu cầu chứng thực Giấy phép kinh doanh hoặc giấy chứng nhận đầu tư&hellip; do cơ quan có thẩm quyền nơi chuyển đến cấp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trong thời hạn 03 (ba) ngày làm việc kể từ ngày nhận hồ sơ đăng ký thuế đầy đủ, chính xác, cơ quan thuế phải cấp lại Giấy chứng nhận đăng ký thuế cho người nộp thuế và giữ nguyên mã số thuế mà người nộp thuế đã được cấp trước đó.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>c. Thông báo thay đổi các chỉ tiêu khác trên tờ khai đăng ký thuế:</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Khi thay đổi các nội dung thông tin đã kê khai trong đăng ký thuế, người nộp thuế phải thực hiện thông báo thay đổi thông tin đăng ký thuế theo &ldquo;Tờ khai điều chỉnh đăng ký thuế&rdquo; mẫu 08-MST trong thời hạn 05 (năm) ngày kể từ ngày có sự thay đổi.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>Hồ sơ bổ sung đăng ký thuế gồm:</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tờ khai điều chỉnh đăng ký thuế theo mẫu 08-MST;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bản sao không yêu cầu chứng thực Giấy phép đăng ký kinh doanh bổ sung hoặc Giấy phép thành lập và hoạt động hoặc Giấy chứng nhận đầu tư đối với các trường hợp thay đổi thông tin phải cấp lại Giấy chứng nhận đăng ký kinh doanh bổ sung hoặc Giấy phép thành lập và hoạt động hoặc Giấy chứng nhận đầu tư;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Bảng kê kèm theo tờ khai đăng ký thuế ban đầu (nếu có).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>3. Xử lý hóa đơn:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Nếu không muốn tiếp tục sử dụng các hóa đơn đã đặt in hoặc mua trước đó thì các bạn có thể hủy.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Nếu các bạn muốn tiếp tục sử dụng các hóa đơn thì phải thông báo cho cơ quan thuế.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>4. Về con dấu:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Làm thủ tục thay đổi mẫu dấu .</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Liên hệ với bên Công an để biết chi tiết các thủ tục và mẫu biểu cần thiết.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>Bằng tất cả sự tận tâm, chuyên nghiệp và thái độ phục vụ tốt nhất, chúng tôi đã và đang là nhà cung cấp dịch vụ uy tín cho hàng trăm khách hàng lớn, nhỏ.</strong></em></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 08:06:44', '2018-11-26 08:08:39', NULL),
(15, 'Thủ tục thành lập doanh nghiệp', 'thu-tuc-thanh-lap-doanh-nghiep', NULL, '<h1><strong><span style=\"color:#ff0000;\"><span style=\"font-size:18px;\">THỦ TỤC THÀNH LẬP DOANH NGHIỆP</span></span></strong></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Thủ tục thành lập doanh nghiệp</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>I</strong><strong>. Chuẩn bị hồ sơ</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Dưới đây là những điều kiện cần để bạn có thể xây dựng bộ hồ sơ thành lập công ty theo đúng quy định của pháp luật:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Thẻ căn cước công dân hoặc&nbsp;Chứng minh thư nhân dân bản chính (hoặc các giấy tờ tương đương khác), kèm theo 01 bản photo có chứng thực trong thời gian không quá 06 tháng;</span></li>\r\n	<li><span style=\"font-size:16px;\">Chọn loại hình doanh nghiệp&nbsp;và tên công ty muốn đăng ký (tên công ty không được trùng lặp hoặc gây nhầm lẫn với các đơn vị&nbsp;đã thành lập trước đó);</span></li>\r\n	<li><span style=\"font-size:16px;\">Chọn địa chỉ trụ sở chính cho công ty (địa chỉ trụ sở phải thuộc quyền sử dụng hợp pháp của doanh nghiệp, không phải là chung cư và nhà tập thể, cần có hợp đồng thuê địa điểm);</span></li>\r\n	<li><span style=\"font-size:16px;\">Lựa chọn vốn điều lệ phù hợp với quy mô doanh nghiệp&nbsp;và các&nbsp;ngành nghề đăng ký kinh doanh;</span></li>\r\n	<li><span style=\"font-size:16px;\">Lựa chọn người đại diện theo pháp luật cho&nbsp;doanh nghiệp;</span></li>\r\n	<li><span style=\"font-size:16px;\">Lựa chọn&nbsp;ngành nghề kinh doanh&nbsp;(lĩnh vực mà doanh nghiệp được phép hoạt động);</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>II. Tóm tắt các bước thành lập công ty</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash;&nbsp;Tiến hành thực hiện thủ tục&nbsp;đăng ký kinh doanh tại Phòng đăng ký kinh doanh thuộc Sở kế hoạch và đầu tư;</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-5.jpg\"><img alt=\"\" sizes=\"(max-width: 484px) 100vw, 484px\" src=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-5.jpg\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-5.jpg 265w, https://ketoanuytin.com/wp-content/uploads/2017/11/images-5-100x72.jpg 100w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Nộp hồ sơ đến Phòng đăng ký kinh doanh cấp tỉnh nơi doanh nghiệp đặt trụ sở chính (Điều 25 Nghị định 43/2010/NĐ-CP ngày 15/04/2010). Lưu ý: Không nhất thiết người đại diện pháp luật của công ty phải đi nộp hồ sơ. Người đại diện pháp luật của công ty có thể ủy quyền cho người khác đi nộp thay. Nếu trường hợp ủy quyền thì người được ủy quyền cần có giấy ủy quyền hợp lệ (Điều 9 &ndash; Thông tư số 01/2013/TT-BKHĐT ngày 21/01/2013 của Bộ Kế hoạch và Đầu tư).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau 03 ngày làm việc kể từ ngày tiếp nhận hồ sơ, nếu hồ sơ của bạn hợp lệ bạn sẽ được cấp Giấy chứng nhận đăng ký doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tiến hành khắc dấu và thông báo về việc sử dụng mẫu con dấu với&nbsp;cơ quan nhà nước;</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-6.jpg\"><img alt=\"\" sizes=\"(max-width: 616px) 100vw, 616px\" src=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-6-300x96.jpg\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-6-300x96.jpg 300w, https://ketoanuytin.com/wp-content/uploads/2017/11/images-6-100x32.jpg 100w, https://ketoanuytin.com/wp-content/uploads/2017/11/images-6.jpg 396w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Mang một bản sao Giấy chứng nhận đăng ký doanh nghiệp đến cơ sơ có chức năng khắc dấu để thực hiện việc làm con dấu pháp nhân cho công ty. Cơ sở khắc dấu sau khi khắc xong dấu pháp nhân sẽ chuyển cho cơ quan công an tỉnh, thành phố để công an tiến hành kiểm tra đăng ký và trả con dấu cho doanh nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">Nhận con dấu pháp nhân &ndash; Khi đến nhận con dấu, đại diện doanh nghiệp mang theo Giấy chứng nhận đăng ký doanh nghiệp (bản gốc) và xuất trình CMND cho cơ quan công an. Ngoài ra, nếu đại diện hợp pháp của doanh nghiệp không thể trực tiếp đi nhận con dấu thì có thể ủy quyền (ủy quyền có công chứng) cho người khác đến nhận con dấu.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tiến hành đăng công bố thông tin thành lập doanh nghiệp trên Cổng thông tin đăng ký doanh nghiệp quốc gia.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>III. Những việc phải làm sau khi công ty được thành lập</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Một doanh nghiệp kinh doanh ngành nghề không có điều kiện sau khi có Đăng ký kinh doanh và con dấu là có thể tiến hành các hoạt động kinh doanh của mình theo quy định tại Điều 8 Luật Doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Tuy nhiên theo quy định pháp luật,&nbsp;<strong>sau khi có Đăng ký kinh doanh Doanh nghiệp cần thực hiện các công việc như sau:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\"><strong>Bước 1:&nbsp;</strong>Tiến hành đăng ký khai thuế ban đầu với cơ quan thuế tại nơi đăng ký kinh doanh trong thời hạn quy định.</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>Bước 2:</strong>Tiến hành đăng ký kê khai thuế qua mạng điện tử thông qua dịch vụ chữ ký số, &ldquo;Từ ngày 01 tháng 7 năm 2013 tất cả các doanh nghiệp trong cả nước phải kê khai, nộp tờ khai thuế qua mạng, nội dung này được quy định trong Luật số 21/2012/QH13 về việc sửa đổi, bổ sung một số điều của Luật Quản lý thuế&rdquo;.</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>Bước 3:</strong>Đăng bố cáo&nbsp;Điều 28 Luật Doanh Nghiệp</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>Bước 4:</strong>Nộp tờ khai và nộp thuế môn bài (theo Mẫu số 01/MBAI ban hành kèm theo Thông tư số 156/2013/TT-BTC ngày 06/11/2013 của Bộ Tài Chính).</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>Bước 5:</strong>Nộp thông báo áp dụng phương pháp tính thuế GTGT.</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>Bước 6:</strong>Làm thủ tục mua, đặt in, tự in hóa đơn theo thông tư 39/2014/TT-BTC hóa đơn chứng từ có hiệu lực từ 01/06/2014. Kể từ ngày 1/9/2014 các doanh nghiệp mới thành lập sẽ được đăng ký phương pháp tính thuế GTGT theo phương pháp khấu trừ và được đặt in hóa đơn GTGT sử dụng.</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>Bước 7:</strong>Doanh nghiệp bắt buộc dán hoặc treo &ldquo;hóa đơn mẫu liên 2&rdquo; tại trụ sở công ty.</span></li>\r\n	<li><span style=\"font-size:16px;\"><strong>Bước 8:</strong>Chuẩn bị đầy đủ các điều kiện kinh doanh đối với những ngành nghề kinh doanh có điều kiện.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>CHUẨN BỊ HỒ SƠ ĐĂNG KÝ KINH DOANH</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Dưới đây là những giấy tờ, tài liệu cần thiết để&nbsp;xây dựng hồ sơ đăng ký kinh doanh phù hợp với từng loại hình doanh nghiệp:</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-4.jpg\"><img alt=\"\" sizes=\"(max-width: 401px) 100vw, 401px\" src=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-4.jpg\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2017/11/images-4.jpg 243w, https://ketoanuytin.com/wp-content/uploads/2017/11/images-4-100x85.jpg 100w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Hồ sơ thành lập doanh nghiệp tư nhân</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Giấy đề nghị xin cấp giấy chứng nhận đăng ký kinh doanh theo mẫu của&nbsp;Phòng&nbsp;đăng ký kinh doanh.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản sao thẻ căn cước, chứng minh thư nhân dân, hộ chiếu hoặc các giấy tờ chứng thực cá nhân hợp pháp khác của chủ doanh nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">Nếu doanh nghiệp có đăng ký các ngành nghề kinh doanh có điều kiện về vốn pháp định (chẳng hạn như kinh doanh bất động sản yêu cầu vốn pháp định tối thiểu là 20 tỷ đồng, tư vấn đầu tư chứng khoán yêu cầu 10 tỷ đồng,&hellip;) thì chủ doanh nghiệp phải có văn bản xác&nbsp;nhận vốn pháp định của cơ quan, tổ chức có thẩm quyền.</span></li>\r\n	<li><span style=\"font-size:16px;\">Nếu doanh nghiệp có đăng ký các ngành nghề kinh doanh có điều kiện về chứng chỉ hành nghề (chẳng hạn như kiểm toán, kế toán, môi giới bất động sản, dịch vụ thuế,&hellip;) thì tùy vào từng ngành nghề&nbsp;mà người chủ doanh nghiệp hoặc các thành viên bắt buộc&nbsp;phải có chứng chỉ hành nghề theo quy định của pháp luật.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Hồ sơ thành lập công ty hợp danh</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Giấy đề nghị xin cấp giấy chứng nhận đăng ký kinh doanh theo mẫu của Phòng đăng ký kinh doanh.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản dự thảo Điều lệ công ty.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản thống kê danh sách thành viên.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản sao thẻ căn cước, chứng minh thư nhân dân, hộ chiếu hoặc các giấy tờ chứng thực cá nhân hợp pháp khác của&nbsp;tất cả các thành viên.</span></li>\r\n	<li><span style=\"font-size:16px;\">Văn bản xác nhận đủ điều kiện về vốn pháp định (Nếu công ty hợp danh&nbsp;có đăng ký các ngành nghề kinh doanh có điều kiện về vốn pháp định)</span></li>\r\n	<li><span style=\"font-size:16px;\">Chứng chỉ hành nghề (Nếu công ty&nbsp;có đăng ký các ngành nghề kinh doanh có điều kiện yêu cầu cần chứng chỉ hành nghề)</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Hồ sơ thành lập công ty trách nhiệm hữu hạn</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Giấy đề nghị xin cấp giấy chứng nhận đăng ký kinh doanh theo mẫu của Phòng đăng ký kinh doanh.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản dự thảo Điều lệ công ty.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản thống kê danh sách thành viên và các giấy tờ sau đây:</span>\r\n	<ul>\r\n		<li><span style=\"font-size:16px;\"><em>Đối với thành viên là cá nhân:</em>Bản sao thẻ căn cước, chứng minh thư nhân dân, hộ chiếu hoặc các giấy tờ chứng thực cá nhân hợp pháp khác.</span></li>\r\n		<li><span style=\"font-size:16px;\"><em>Đối với thành viên là tổ chức:</em>Bản sao quyết định thành lập, Giấy phép&nbsp;kinh doanh hoặc tài liệu khác có giá trị pháp lý tương đương của tổ chức. Ngoài ra đối với người đại diện theo&nbsp;pháp luật được ủy quyền thì cần thêm Giấy ủy quyền, bản sao thẻ căn cước, chứng minh thư nhân dân, hộ chiếu hoặc các giấy tờ chứng thực cá nhân hợp pháp khác của người được ủy quyền.</span></li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:16px;\">Văn bản xác nhận đủ điều kiện về vốn pháp định (Nếu công ty TNHH&nbsp;có đăng ký các ngành nghề kinh doanh có điều kiện về vốn pháp định)</span></li>\r\n	<li><span style=\"font-size:16px;\">Chứng chỉ hành nghề (Nếu công ty có đăng ký các ngành nghề kinh doanh có điều kiện yêu cầu cần chứng chỉ hành nghề)</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Hồ sơ thành lập công ty cổ phần</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Giấy đề nghị xin cấp giấy chứng nhận đăng ký kinh doanh theo mẫu của Phòng đăng ký kinh doanh.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản dự thảo Điều lệ công ty.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản thống kê danh sách cổ đông sáng lập và các giấy tờ sau đây:</span>\r\n	<ul>\r\n		<li><span style=\"font-size:16px;\"><em>Đối với cổ đông là cá nhân:</em>Bản sao thẻ căn cước, chứng minh thư nhân dân, hộ chiếu hoặc các giấy tờ chứng thực cá nhân hợp pháp khác.</span></li>\r\n		<li><span style=\"font-size:16px;\"><em>Đối với cổ đông là tổ chức:</em>Bản sao quyết định thành lập, Giấy phép kinh doanh hoặc tài liệu khác có giá trị pháp lý tương đương của tổ chức. Ngoài ra đối với người đại diện theo&nbsp;pháp luật được ủy quyền thì cần thêm Giấy ủy quyền, bản sao thẻ căn cước, chứng minh thư nhân dân, hộ chiếu hoặc các giấy tờ chứng thực cá nhân hợp pháp khác của người được ủy quyền.</span></li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-size:16px;\">Văn bản xác nhận đủ điều kiện về vốn pháp định (Nếu công ty cổ phần&nbsp;có đăng ký các ngành nghề kinh doanh có điều kiện về vốn pháp định)</span></li>\r\n	<li><span style=\"font-size:16px;\">Chứng chỉ hành nghề (Nếu công ty có đăng ký các ngành nghề kinh doanh có điều kiện yêu cầu cần chứng chỉ hành nghề)</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>3 CÁCH THÀNH LẬP CÔNG TY</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Khi tiến hành thủ tục đăng ký thành lập công ty, doanh nghiệp có thể thực hiện theo 03 cách sau đây:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Cách 1:</em></strong>&nbsp;<strong><em>Công ty tiến hành nộp hồ sơ thành lập công ty trực tiếp tại Phòng Đăng ký Kinh doanh &ndash; Sở Kế hoạch và Đầu tư.</em></strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Người thành lập công ty&nbsp;tiến hành soạn thảo&nbsp;hồ sơ đăng ký kinh doanh theo đúng quy định của pháp luật và&nbsp;nộp hồ sơ tại Phòng đăng ký kinh doanhở các tỉnh/thành phố nơi đặt trụ sở công ty. Hồ sơ đăng ký kinh doanh của&nbsp;các loại hình công ty&nbsp;khác nhau cũng khác nhau.</span></li>\r\n	<li><span style=\"font-size:16px;\">Trong vòng&nbsp;03 ngày làm việckể từ ngày bạn nộp hồ sơ, Phòng đăng ký kinh doanh có trách nhiệm&nbsp;thông báo kết quả cho bạn. Nếu hồ sơ hợp lệ thì bạn sẽ nhận được&nbsp;giấy chứng nhận đăng ký kinh doanh. Nếu&nbsp;không, bạn sẽ nhận được thông báo bằng văn bản có nêu rõ lý do và hướng dẫn các yêu cầu chỉnh sửa hoặc bổ sung, sau đó bạn nộp lại hồ sơ và tiếp tục chờ đợi.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Thực tế cho thấy ngay cả những đơn vị luật uy tín nhất cũng có lúc&nbsp;phải chỉnh sửa và nộp lại hồ sơ,&nbsp;còn trường hợp các bạn (chủ doanh nghiệp) trực tiếp thực hiện thủ tục thành lập công ty phải điều chỉnh lại hồ sơ thì xảy ra thường xuyên bởi&nbsp;sự hiểu biết hạn chế về luật doanh nghiệp nên thậm chí có những người sau khi&nbsp;mất quá nhiều thời gian và công sức đi lại vẫn phải tìm đến công ty luật để nhờ giúp đỡ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Các bạn có thể nhận kết quả trực tiếp tại Phòng đăng ký kinh doanh hoặc thông qua dịch vụ chuyển phát của bưu điện. Khi nhận được giấy phép đăng ký kinh doanh phải kiểm tra cẩn thận, nếu có nhầm lẫn hoặc sai sót phải thông báo ngay cho cơ quan ĐKKD để điều chỉnh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Cách 2:</em></strong>&nbsp;<strong><em>Công ty tiến hành đăng ký kinh doanh qua mạng (online) sử dụng chữ ký số công cộng trên Cổng thông tin điện tử Quốc gia về đăng ký doanh nghiệp</em></strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Trước tiên, cá nhân/tổ chức thành lập công ty phải tiến hành đăng ký tài khoản trên trang web:&nbsp;<a href=\"https://dangkykinhdoanh.gov.vn/\">https://dangkykinhdoanh.gov.vn</a></span></li>\r\n	<li><span style=\"font-size:16px;\">Khi đã có tài khoản, công ty tiến hành việc nộp Hồ sơ qua tài khoản và sử dụng chữ ký số Công cộng để ký.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Bạn phải khai báo chính xác các trường thông tin theo mẫu trên trên trang đăng ký kinh doanh. Việc nhập thông tin, tải lên các tài liệu (định dạng PDF), thực hiện ký số vào hồ sơ và nộp lệ phí các bạn thực hiện theo đúng quy trình được hướng dẫn trên website.</span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:16px;\">Sau khi hoàn tất việc nộp hồ sơ đăng ký kinh doanh, doanh nghiệp sẽ nhận được Giấy biên nhận hồ sơ đăng ký kinh doanh qua email dưới định dạng file PDF.</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Trường hợp hồ sơ hợp lệ thì Phòng Đăng ký kinh doanh sẽ gửi thông tin sang cơ quan quản lý thuế để tự động tạo mã số thuế cho doanh nghiệp. Sau khi nhận được mã số từ cơ quan thuế, Phòng Đăng ký kinh doanh sẽ cấp Giấy chứng nhận đăng ký kinh doanh cho doanh nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">Trường hợp hồ sơ không hợp lệ, Phòng Đăng ký kinh doanh sẽ gửi thông báo cho doanh nghiệp để yêu cầu sửa đổi, bổ sung hồ sơ.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Cách 3:</em></strong>&nbsp;<strong><em>Công ty tiến hành đăng ký thành lập doanh nghiệp qua mạng điện tử sử dụng Tài khoản đăng ký kinh doanh:</em></strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Trước tiên, bạn phải tiến hành đăng ký tài khoản trên website Cổng thông tin đăng ký doanh nghiệp quốc gia, sau đó gửi yêu cầu cấp Tài khoản đăng ký kinh doanh trên website này.</span></li>\r\n	<li><span style=\"font-size:16px;\">Sau khi có tài khoản, người thành lập công ty tiến hành nhập thông tin, tải lên các giấy tờ cần thiết (định dạng PDF) và xác thực việc nộp hồ sơ ĐKKD qua mạng điện tử theo hướng dẫn trên website Cổng thông tin đăng ký doanh nghiệp quốc gia.</span></li>\r\n	<li><span style=\"font-size:16px;\">Sau khi hoàn thành việc nộp hồ sơ, bạn sẽ nhận được Giấy biên nhận hồ sơ đăng ký doanh nghiệp qua email (là 1 file PDF).</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Trường hợp hồ sơ chưa hợp lệ, cơ quan đăng ký kinh doanh sẽ ra thông báo về việc yêu cầu sửa đổi, bổ sung hồ sơ qua email của bạn. Doanh nghiệp thực hiện lại bước 2.</span></li>\r\n	<li><span style=\"font-size:16px;\">Trường hợp hồ sơ hợp lệ, cơ quan đăng ký kinh doanh tiến hành chuyển thông tin sang cơ quan quản lý thuế để tạo mã số thuế doanh nghiệp. Việc tạo mã số thuế được hệ thống tạo tự động trên cơ sở dữ liệu. Sau khi nhận được mã số thuế, Phòng Đăng ký kinh doanh sẽ gửi thông báo về hồ sơ hợp lệ qua email cho bạn (là 1 file PDF).</span></li>\r\n</ul>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:16px;\">Sau khi nhận được email thông báo hồ sơ hợp lệ, bạn tiến hành&nbsp;nộp hồ sơ bản giấykèm theo&nbsp;giấy biên nhận hồ sơvà&nbsp;giấy thông báo hồ sơ hợp lệ&nbsp;lên cơ quan đăng ký kinh doanh (có thể nộp trực tiếp hoặc gửi qua đường bưu điện).</span></li>\r\n	<li><span style=\"font-size:16px;\">Sau khi nhận được hồ sơ bản cứng, Cơ quan Đăng ký kinh doanh sẽ tiến hành đối chiếu các đầu mục giấy tờ với hồ sơ doanh nghiệp đã nộp online. Kết quả xử lý hồ sơ sẽ được gửi về email của bạn.</span></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Nếu hồ sơ trùng khớp thì bạn sẽ nhận được giấy chứng nhận đăng ký doanh nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">Nếu hồ sơ không trùng khớp thì bạn phải nộp lại hồ sơ bản cứng với các thông tin chính xác so với hồ sơ online (bước 4).</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\">Thực tế cho thấy, việc nộp hồ sơ trực tiếp tại phòng ĐKKD được các doanh nghiệp áp dụng nhiều hơn bởi ưu điểm là bạn sẽ được các chuyên viên xử lý hồ sơ hướng dẫn và yêu cầu sửa đổi, bổ sung tại chỗ khi có sai sót. Tuy nhiên việc nộp hồ sơ online sẽ trở thành xu hướng tất yếu nhằm tiết kiệm thời gian, chi phí và công sức đi lại cho doanh nghiệp. Đây cũng là một trong những điểm nổi bật trong chủ trương cải cách các thủ tục hành chính mà nước ta đang nỗ lực thực hiện.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>Bằng tất cả sự tận tâm, chuyên nghiệp và thái độ phục vụ tốt nhất, chúng tôi đã và đang là nhà cung cấp dịch vụ uy tín cho hàng trăm khách hàng lớn, nhỏ.</strong></em></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 08:08:31', '2018-11-26 08:08:31', NULL),
(16, 'Dịch vụ giải thể doanh nghiệp', 'dich-vu-giai-the-doanh-nghiep', '<p><span style=\"font-size:16px;\">Luật Doanh nghiệp 2014 &nbsp;có quy định tại&nbsp;<strong>khoản 7 Điều 4</strong>: &ldquo;Doanh nghiệp là tổ chức có tên riêng, có tài sản, có trụ sở giao dịch, được đăng ký thành lập theo quy định của pháp luật nhằm mục đích kinh doanh&rdquo;.&nbsp;<em>Giải thể doanh nghiệp</em>&nbsp;là việc doanh nghiệp chấm dứt hoạt động kinh doanh do đã đạt được những mục tiêu mà các thương nhân kinh doanh đã đặt ra hoặc bị giải thể theo quy định của pháp luật.</span></p>', '<h1><span style=\"color:#ff0000;\"><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><strong><span style=\"font-size:18px;\">DỊCH VỤ GIẢI THỂ DOANH NGHIỆP</span></strong></span></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Thủ tục giải thể doanh nghiệp</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Luật Doanh nghiệp 2014 &nbsp;có quy định tại&nbsp;<strong>khoản 7 Điều 4</strong>: &ldquo;Doanh nghiệp là tổ chức có tên riêng, có tài sản, có trụ sở giao dịch, được đăng ký thành lập theo quy định của pháp luật nhằm mục đích kinh doanh&rdquo;.&nbsp;<em>Giải thể doanh nghiệp</em>&nbsp;là việc doanh nghiệp chấm dứt hoạt động kinh doanh do đã đạt được những mục tiêu mà các thương nhân kinh doanh đã đặt ra hoặc bị giải thể theo quy định của pháp luật.</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2017/11/giai-the-cong-doanh-nghiep1.png\"><img alt=\"\" sizes=\"(max-width: 588px) 100vw, 588px\" src=\"https://ketoanuytin.com/wp-content/uploads/2017/11/giai-the-cong-doanh-nghiep1-300x90.png\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2017/11/giai-the-cong-doanh-nghiep1-300x90.png 300w, https://ketoanuytin.com/wp-content/uploads/2017/11/giai-the-cong-doanh-nghiep1-100x30.png 100w, https://ketoanuytin.com/wp-content/uploads/2017/11/giai-the-cong-doanh-nghiep1.png 500w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>I. Các trường hợp và&nbsp;điều kiện giải thể doanh nghiệp</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">1.&nbsp;Doanh nghiệp bị giải thể trong các trường hợp sau đây:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">a) Kết thúc thời hạn hoạt động đã ghi trong Điều lệ công ty mà không có quyết định gia hạn;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">b) Theo quyết định của chủ doanh nghiệp đối với doanh nghiệp tư nhân, của tất cả thành viên hợp danh đối với công ty hợp danh, của Hội đồng thành viên, chủ sở hữu công ty đối với công ty trách nhiệm hữu hạn, của Đại hội đồng cổ đông đối với công ty cổ phần;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">c) Công ty không còn đủ số lượng thành viên tối thiểu theo quy định của Luật này trong thời hạn 06 tháng liên tục mà không làm thủ tục chuyển đổi loại hình doanh nghiệp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">d) Bị thu hồi Giấy chứng nhận&nbsp;đăng kýdoanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">2. Doanh nghiệp chỉ được giải thể khi bảo đảm thanh toán hết các khoản nợ và nghĩa vụ tài sản khác và doanh nghiệp không trong quá trình giải quyết tranh chấp tại Tòa án hoặc cơ quan trọng tài. Người quản lý có liên quan và doanh nghiệp quy định tại điểm d khoản 1 Điều này cùng liên đới chịu trách nhiệm về các khoản nợ của doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Thủ tục giải thể đối với tất cả các loại hình doanh nghiệp chỉ khác nhau về giấy tờ hồ sơ, còn trình tự thực hiện thủ tục là giống nhau:</em></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>II. Trình tự, thủ tục giải thể doanh nghiệp</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Việc giải thể doanh nghiệp trong các trường hợp quy định tại các điểm a, b và c khoản 1 Điều 201 của Luật doanh nghiệp 2014 được thực hiện theo quy định sau đây:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">1. Thông qua quyết định giải thể doanh nghiệp. Quyết định giải thể doanh nghiệp phải có các nội dung chủ yếu sau đây:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">a) Tên, địa chỉ trụ sở chính của doanh nghiệp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">b) Lý do giải thể;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">c) Thời hạn, thủ tục thanh lý hợp đồng và thanh toán các khoản nợ của doanh nghiệp; thời hạn thanh toán nợ, thanh lý hợp đồng không được vượt quá 06 tháng, kể từ ngày thông qua quyết định giải thể;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">d) Phương án xử lý các nghĩa vụ phát sinh từ hợp đồng lao động;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">đ) Họ, tên, chữ ký của người đại diện theo pháp luật của doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">2. Chủ doanh nghiệp tư nhân, Hội đồng thành viên hoặc chủ sở hữu công ty, Hội đồng quản trị trực tiếp tổ chức thanh lý tài sản doanh nghiệp, trừ trường hợp Điều lệ công ty quy định thành lập tổ chức thanh lý riêng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">3. Trong thời hạn 07 ngày làm việc kể từ ngày thông qua, quyết định giải thể và biên bản họp phải được gửi đến Cơ quan đăng ký kinh doanh, cơ quan thuế, người lao động trong doanh nghiệp, đăng quyết định giải thể trên Cổng thông tin quốc gia về đăng ký doanh nghiệp và phải được niêm yết công khai tại trụ sở chính, chi nhánh, văn phòng đại diện của doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trường hợp doanh nghiệp còn nghĩa vụ tài chính chưa thanh toán thì phải gửi kèm theo quyết định giải thể phương án giải quyết nợ đến các chủ nợ, người có quyền lợi và nghĩa vụ có liên quan. Thông báo phải có tên, địa chỉ của chủ nợ; số nợ, thời hạn, địa điểm và phương thức thanh toán số nợ đó; cách thức và thời hạn giải quyết khiếu nại của chủ nợ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">4. Cơ quan đăng ký kinh doanh phải thông báo tình trạng doanh nghiệp đang làm thủ tục giải thể trên Cổng thông tin quốc gia về đăng ký doanh nghiệp ngay sau khi nhận được quyết định giải thể của doanh nghiệp. Kèm theo thông báo phải đăng tải quyết định giải thể và phương án giải quyết nợ (nếu có).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">5. Các khoản nợ của doanh nghiệp được thanh toán theo thứ tự sau đây:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">a) Các khoản nợ lương, trợ cấp thôi việc, bảo hiểm xã hội theo quy định của pháp luật và các quyền lợi khác của người lao động theo thỏa ước lao động tập thể và hợp đồng lao động đã ký&nbsp;kết;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">b) Nợ thuế;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">c) Các khoản nợ khác.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">6. Sau khi đã thanh toán hết các khoản nợ và chi phí giải thể doanh nghiệp, phần còn lại chia cho chủ doanh nghiệp tư nhân, các thành viên, cổ đông hoặc chủ sở hữu công ty theo tỷ lệ sở hữu phần vốn góp, cổ phần.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">7. Người đại diện theo pháp luật của doanh nghiệp gửi đề nghị giải thể cho Cơ quan đăng ký kinh doanh trong 05 ngày làm việc kể từ ngày thanh toán hết các khoản nợ của doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">8. Sau thời hạn 180 ngày, kể từ ngày nhận được quyết định giải thể theo khoản 3 Điều 202 Luật doanh nghiệp 2014 mà không nhận được ý kiến&nbsp;vềviệc giải thể từ doanh nghiệp hoặc phản đối của bên có liên quan bằng văn bản hoặc trong 05 ngày làm việc kể từ ngày nhận hồ sơ giải thể, Cơ quan đăng ký kinh doanh cập nhật tình trạng pháp lý của doanh nghiệp trên Cơ sở dữ liệu quốc gia về đăng ký doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">9. Chính phủ quy định chi tiết về trình tự, thủ tục giải thể doanh nghiệp.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>III.</strong><strong>&nbsp;Giải thể doanh nghiệp trong&nbsp;trường hợp&nbsp;bị thu hồi Giấy chứng nhận đăng ký doanh nghiệp hoặc theo quyết định của Tòa án</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Việc giải thể doanh nghiệp theo quy định tại điểm d khoản 1 Điều 201 của Luật này được thực hiện theo trình tự, thủ tục sau đây:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">1. Cơ quan đăng ký kinh doanh, phải thông báo tình trạng doanh nghiệp đang làm thủ tục giải thể trên Cổng thông tin&nbsp;quốcgia&nbsp;vềđăng ký doanh nghiệp đồng thời với việc ra quyết định thu hồi Giấy chứng nhận&nbsp;đăng ký&nbsp;doanh nghiệp hoặc ngay sau khi nhận được quyết định giải thể của Tòa án đã có hiệu lực thi hành. Kèm theo thông báo phải đăng tải quyết định thu hồi Giấy chứng nhận đăng ký doanh nghiệp hoặc&nbsp;quyết&nbsp;định của Tòa án;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">2. Trong thời hạn 10 ngày, kể từ ngày nhận được quyết định thu hồi Giấy chứng nhận đăng ký doanh nghiệp hoặc quyết định của Tòa án có hiệu lực, doanh nghiệp phải triệu tập họp để quyết định giải thể. Quyết định giải thể và bản sao quyết định thu hồi Giấy chứng nhận đăng ký doanh nghiệp hoặc quyết định của Tòa án có hiệu lực phải được gửi đến Cơ quan đăng ký kinh doanh, cơ quan thuế, người lao động trong doanh nghiệp và phải được niêm yết công khai tại trụ sở chính và chi nhánh của doanh nghiệp. Đối với trường hợp mà pháp luật yêu cầu phải đăng báo thì quyết định giải thể doanh nghiệp phải được đăng ít nhất trên một tờ báo viết hoặc báo điện tử&nbsp;trongba số liên tiếp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trường hợp doanh nghiệp còn nghĩa vụ tài chính chưa thanh toán thì phải đồng thời gửi kèm theo quyết định giải thể của doanh nghiệp phương án giải quyết nợ đến các chủ nợ, người có quyền lợi và nghĩa vụ có liên quan. Thông báo phải có tên, địa chỉ của chủ nợ; số nợ, thời hạn, địa điểm và phương thức thanh toán số nợ đó; cách thức và thời hạn giải quyết khiếu nại của chủ nợ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">3. Việc thanh toán các khoản nợ của doanh nghiệp được thực hiện theo&nbsp;quy địnhtại khoản 5 Điều 202 của Luật này.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">4. Người đại diện theo pháp luật của doanh nghiệp gửi đề nghị giải thể cho Cơ quan đăng ký kinh doanh&nbsp;trong05 ngày làm việc, kể từ ngày thanh toán hết các khoản nợ của doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">5. Sau thời hạn 180 ngày, kể từ ngày thông báo tình trạng giải thể doanh nghiệp theo quy định tại khoản 1 Điều này mà không nhận phản đối của bên có liên quan bằng văn bản hoặc&nbsp;trong05 ngày làm việc kể từ ngày nhận hồ sơ giải thể, Cơ quan đăng ký kinh doanh cập nhật tình trạng pháp lý của doanh nghiệp trên Cơ sở dữ liệu quốc gia về đăng ký doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">6. Cá nhân người quản lý công ty có liên quan phải chịu trách nhiệm cá nhân về thiệt hại do việc không thực hiện hoặc không thực hiện đúng quy định tại Điều này.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>IV</strong><strong>. Hồ sơ giải thể doanh nghiệp</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">1. Hồ sơ giải thể doanh nghiệp bao gồm giấy tờ sau đây:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">a) Thông báo về giải thể doanh nghiệp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đăng quyết định giải thể trên Cổng thông tin quốc gia về đăng ký doanh nghiệp và phải được niêm yết công khai tại trụ sở chính, chi nhánh, văn phòng đại diện của doanh nghiệp</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">b) Báo cáo thanh lý tài sản doanh nghiệp; danh sách chủ nợ và số nợ đã thanh toán, gồm cả thanh toán hết các khoản nợ về thuế và nợ tiền đóng bảo hiểm xã hội, người lao động sau khi quyết định giải thể doanh nghiệp (nếu có);</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">c) Con dấu và giấy chứng nhận mẫu dấu.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi thực hiện xong thủ tục tại Chi cực thuế, doanh nghiệp được cấp Thông báo đóng mã số thuế. Doanh nghiệp tiến hành các thủ tục trả con dấu pháp nhân tại&nbsp;<strong><em>Phòng Cảnh sát quản lý hành chính về trật tự xã hôi&nbsp;</em></strong>&ndash; Công an thành phố nơi doanh nghiệp được cấp GCN Đăng ký kinh doanh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Hồ sơ thực hiện việc trả con dấu gồm:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Công văn trả dấu;</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản chính GCN đăng ký mẫu dấu;</span></li>\r\n	<li><span style=\"font-size:16px;\">Dấu pháp nhân;</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản sao GCN đăng ký kinh doanh có công chứng;</span></li>\r\n	<li><span style=\"font-size:16px;\">Giấy giới thiệu (trong trường hợp Đại diện theo pháp luật của Công ty không trục tiếp tiến hành trả con dấu).</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Lưu ý:</strong>&nbsp;Đối với doanh nghiệp sử dụng con dấu do cơ quan công an cấp, doanh nghiệp có trách nhiệm trả con dấu, Giấy chứng nhận đã đăng ký mẫu con dấu cho cơ quan công an để được cấp giấy chứng nhận đã thu hồi con dấu.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">d) Giấy chứng nhận đăng ký doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Công an tỉnh ra quyết định huỷ dấu, Doanh nghiệp gửi Quyết định huỷ dấu này lên Phòng đăng ký kinh doanh và chờ quyết định giải thể cuối cùng của Phòng Đăng ký kinh doanh (Phòng Đăng ký kinh doanh ra Quyết định xoá tên Doanh nghiệp trong sổ Đăng ký kinh doanh thì hoàn thành thủ tục giải thể).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">2. Thành viên Hội đồng quản trị công ty cổ phần, thành viên Hội đồng thành viên công ty trách nhiệm hữu hạn, chủ sở hữu công ty, chủ doanh nghiệp tư nhân, Giám đốc hoặc Tổng giám đốc, thành viên hợp danh, người đại diện theo pháp luật của doanh nghiệp chịu trách nhiệm về tính trung thực, chính xác của hồ sơ giải thể doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">3. Trường hợp hồ sơ giải thể không chính xác, giả mạo, những người quy định tại khoản 2 Điều 204 Luật doanh nghiệp 2014 phải liên đới chịu trách nhiệm thanh toán số nợ chưa thanh toán, số thuế chưa nộp và quyền lợi của người lao động chưa được giải quyết và chịu trách nhiệm cá nhân trước pháp luật về những hệ quả phát sinh&nbsp;trongthời hạn 05 năm, kể từ ngày nộp hồ sơ giải thể doanh nghiệp đến Cơ quan&nbsp;đăng kýkinh doanh.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>V</strong><strong>. Các hoạt động bị cấm kể từ khi có quyết định giải thể</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">1.Kể từ khi có quyết định giải thể doanh nghiệp, nghiêm cấm doanh nghiệp, người quản lý doanh nghiệp thực hiện các hoạt động sau đây:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">a) Cất giấu, tẩu tán tài sản;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">b) Từ bỏ hoặc giảm bớt quyền đòi nợ;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">c) Chuyển các khoản nợ không có bảo đảm thành các khoản nợ có bảo đảm bằng tài sản của doanh nghiệp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">d) Ký kết hợp đồng mới trừ trường hợp để thực hiện giải thể doanh nghiệp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">đ) Cầm cố, thế chấp, tặng cho, cho thuê tài sản;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">e) Chấm dứt thực hiện hợp đồng đã có hiệu lực;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">g) Huy động vốn dưới mọi hình thức.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">2. Tùy theo tính chất và mức độ vi phạm, cá nhân có hành vi vi phạm khoản 1 Điều này có thể bị xử phạt vi phạm hành chính hoặc truy cứu trách nhiệm hình sự; nếu gây thiệt hại thì phải bồi thường.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Bằng tất cả sự tận tâm, chuyên nghiệp và thái độ phục vụ tốt nhất, chúng tôi đã và đang là nhà cung cấp dịch vụ uy tín cho hàng trăm khách hàng lớn, nhỏ.</em></strong></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 08:10:44', '2018-11-26 08:10:44', NULL);
INSERT INTO `posts` (`id`, `title`, `path`, `description`, `content`, `image`, `post_type`, `is_active`, `category_item_id`, `user_id`, `created_at`, `updated_at`, `seo_id`) VALUES
(17, 'Thủ tục tạm ngưng kinh doanh', 'thu-tuc-tam-ngung-kinh-doanh', '<p><span style=\"font-size:16px;\">Trong tình hình biến động của kinh tế thị trường, nhiều doanh nghiệp lâm vào cảnh rất khó khăn nên phải tạm ngừng hoạt động để chờ đợi sự phục hồi của nền kinh tế. Do đó, việc tạm ngưng là một giải pháp mà các doanh nghiệp khó khăn nên cân nhắc.&nbsp;</span></p>', '<h1><span style=\"color:#ff0000;\"><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><span style=\"font-size:16px;\"><strong>THỦ TỤC TẠM NGỪNG KINH DOANH</strong></span></span></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\">Trong tình hình biến động của kinh tế thị trường, nhiều doanh nghiệp lâm vào cảnh rất khó khăn nên phải tạm ngừng hoạt động để chờ đợi sự phục hồi của nền kinh tế. Do đó, việc tạm ngưng là một giải pháp mà các doanh nghiệp khó khăn nên cân nhắc.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2017/11/tAM-NGUNG-KINH-DOANH-1.jpg\"><img alt=\"\" sizes=\"(max-width: 495px) 100vw, 495px\" src=\"https://ketoanuytin.com/wp-content/uploads/2017/11/tAM-NGUNG-KINH-DOANH-1-300x159.jpg\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2017/11/tAM-NGUNG-KINH-DOANH-1-300x159.jpg 300w, https://ketoanuytin.com/wp-content/uploads/2017/11/tAM-NGUNG-KINH-DOANH-1-100x53.jpg 100w, https://ketoanuytin.com/wp-content/uploads/2017/11/tAM-NGUNG-KINH-DOANH-1.jpg 395w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp;Tạm ngưng hoạt động là gì?</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Tạm ngưng hoạt động hay còn gọi là tạm ngừng kinh doanh được quy định tại Điều 200 của Luật doanh nghiệp 2014 như sau:&rdquo;Doanh nghiệp có quyền tạm ngừng kinh doanh nhưng phải thông báo bằng văn bản về thời điểm và thời hạn tạm ngừng hoặc tiếp tục kinh doanh cho Cơ quan đăng ký kinh doanh chậm nhất 15 ngày trước ngày tạm ngừng hoặc tiếp tục kinh doanh. Quy định này áp dụng trong trường hợp doanh nghiệp tiếp tục kinh doanh trước thời hạn đã thông báo&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Cơ sở pháp lý:</em></strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\"><em>Khoản 4, Điều 8 Nghị định số: 83/2013/NĐ-CP;</em></span></li>\r\n	<li><span style=\"font-size:16px;\"><em>Khoản 1, Điều 4 Nghị định 91/2014/NĐ-CP</em></span></li>\r\n	<li><span style=\"font-size:16px;\"><em>Khoản 4, Điều 57 Nghị định số: 78/2015/NĐ-CP;</em></span></li>\r\n	<li><span style=\"font-size:16px;\"><em>Thông tư số&nbsp;156/2013/TT-BTC;</em></span></li>\r\n	<li><span style=\"font-size:16px;\"><em>Điều 14 Thông tư 151/2014/TT-BTC</em></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Vậy, làm thủ tục tạm ngừng kinh doanh&nbsp;có cần xin xác nhận của cơ quan thuế không?</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Khi quyết định tạm ngừng kinh doanh, doanh nghiệp chỉ nộp hồ sơ tại cơ quan đăng ký kinh doanh và không cần phải nộp thông báo cho cơ quan thuế.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>Vấn đề này được quy định tại các văn bản sau:</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>+&nbsp;<em>Khoản 1, Điều 4, Nghị định 91/2014/NĐ-CP</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sửa đổi&nbsp;Khoản 5 Điều 5&nbsp;<em><strong>Nghị định số&nbsp;</strong><strong>83/2013/NĐ-CP&nbsp;</strong></em>như sau:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ldquo;5. Trường hợp người nộp thuế tạm ngừng hoạt động kinh doanh đã có văn bản đề nghị gửi cơ quan đăng ký kinh doanh nơi người nộp thuế đã đăng ký thì không phải nộp hồ sơ khai thuế trong thời gian tạm ngừng hoạt động kinh doanh. Trường hợp người nộp thuế tiếp tục hoạt động kinh doanh trở lại sau thời gian tạm ngừng và đã có văn bản thông báo gửi cơ quan đăng ký kinh doanh nơi người nộp thuế đã đăng ký thì thực hiện nộp hồ sơ khai thuế theo quy định. Cơ quan đăng ký kinh doanh nơi doanh nghiệp, hộ kinh doanh đã đăng ký có trách nhiệm thông báo cho cơ quan thuế biết về thông tin doanh nghiệp, hộ kinh doanh tạm ngừng kinh doanh, tiếp tục hoạt động kinh doanh trở lại.&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>+&nbsp;<em>Điều 14,&nbsp;Thông tư 151/2014/TT-BTC</em></strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>Sửa đổi điểm đ, Khoản 1, Điều 10 Thông tư số&nbsp;156/2013/TT-BTC&nbsp;ngày 06/11/2013 của Bộ Tài chính hướng dẫn thi hành một số điều của Luật Quản lý thuế; Luật sửa đổi, bổ sung một số điều của Luật Quản lý thuế và Nghị định số&nbsp;83/2013/NĐ-CP&nbsp;ngày 22/7/2013 của Chính phủ (gọi chung là Thông tư số&nbsp;156/2013/TT-BTC)&nbsp;như sau:</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ldquo;đ) Người nộp thuế trong thời gian tạm ngừng kinh doanh không phát sinh nghĩa vụ thuế thì không phải nộp hồ sơ khai thuế của thời gian tạm ngừng hoạt động kinh doanh. Trường hợp người nộp thuế&nbsp;tạm ngừng&nbsp;kinh doanh không trọn năm dương lịch hoặc năm tài chính thì vẫn phải nộp hồ sơ quyết toán thuế năm.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">đ.1) Đối với người nộp thuế&nbsp;thực hiện&nbsp;thủ tục&nbsp;đăng ký&nbsp;kinh doanh&nbsp;tại&nbsp;cơ quan&nbsp;đăng ký kinh doanh phải thông báo bằng văn bản về việc tạm ngừng kinh doanh hoặc&nbsp;hoạt động kinh doanh trở lại&nbsp;với&nbsp;cơ quan&nbsp;đăng ký kinh doanh nơi người nộp thuế đã đăng ký theo quy định.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Cơ quan&nbsp;đăng ký kinh doanh có trách nhiệm thông báo cho&nbsp;cơ quan thuế biết về thông tin người nộp thuế tạm ngừng kinh doanh&nbsp;hoặc&nbsp;hoạt động kinh doanh trở lại chậm nhất không quá 02&nbsp;(hai)&nbsp;ngày làm việc kể từ ngày nhận được văn bản của người nộp thuế.&nbsp;Trường hợp&nbsp;người nộp thuế&nbsp;đăng ký tạm ngừng kinh doanh,&nbsp;cơ quan thuế có trách nhiệm&nbsp;thông báo&nbsp;cho&nbsp;cơ&nbsp;quan đăng ký kinh doanh về nghĩa vụ thuế&nbsp;còn nợ với ngân sách nhà nước&nbsp;của người nộp thuế chậm nhất không quá 02&nbsp;(hai)&nbsp;ngày làm việc kể từ ngày nhận được thông tin từ cơ quan đăng ký kinh doanh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">đ.2) Đối với người nộp thuế thuộc đối tượng đăng ký cấp mã số thuế trực tiếp tại cơ quan thuế thì trước khi tạm ngừng kinh doanh, người nộp thuế phải có thông báo bằng văn bản gửi cơ quan thuế quản lý trực tiếp chậm nhất 15 (mười lăm) ngày trước khi tạm ngừng kinh doanh. Nội dung thông báo gồm:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tên, địa chỉ trụ sở, mã số thuế;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Thời hạn tạm ngừng kinh doanh, ngày bắt đầu và ngày kết thúc thời hạn tạm ngừng;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Lý do tạm ngừng kinh doanh;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Họ, tên, chữ ký của người đại diện theo pháp luật của doanh nghiệp, đại diện của nhóm cá nhân kinh doanh, của chủ hộ kinh doanh.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Hết thời hạn tạm ngừng kinh doanh, người nộp thuế phải thực hiện kê khai thuế theo quy định. Trường hợp người nộp thuế ra kinh doanh trước thời hạn theo thông báo tạm ngừng kinh doanh thì phải có thông báo bằng văn bản gửi cơ quan thuế quản lý trực tiếp đồng thời thực hiện nộp hồ sơ khai thuế theo quy định&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>+ Khoản 4, Điều 57, Nghị định 78/2015/NĐ-CP:&nbsp;</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Phòng Đăng ký kinh doanh trao Giấy biên nhận hồ sơ cho doanh nghiệp sau khi tiếp nhận thông báo tạm ngừng kinh doanh, thông báo tiếp tục kinh doanh trước thời hạn đã thông báo của doanh nghiệp. Trong thời hạn 03 ngày làm việc, kể từ ngày nhận được hồ sơ hợp lệ, Phòng Đăng ký kinh doanh cấp Giấy xác nhận về việc doanh nghiệp, chi nhánh, văn phòng đại diện, địa điểm kinh doanh đã đăng ký tạm ngừng kinh doanh, Giấy xác nhận về việc doanh nghiệp, chi nhánh, văn phòng đại diện, địa điểm kinh doanh đăng ký tiếp tục kinh doanh trước thời hạn đã thông báo.&nbsp;Phòng Đăng ký kinh doanh gửi thông tin doanh nghiệp đăng ký tạm ngừng kinh doanh, tiếp tục kinh doanh trước thời hạn đã thông báo đến cơ quan thuế để phối hợp quản lý nhà nước đối với doanh nghiệp.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Thời hạn tạm ngừng kinh doanh:</strong><br />\r\nĐược quy định tại khoản 3, điều 57, nghị định 43/2010/NĐ-CP, ngày 15 tháng 4 năm 2010, về Đăng ký doanh nghiệp.&nbsp;<em>&ldquo;3. Thời hạn tạm ngừng kinh doanh, ngày bắt đầu và ngày kết thúc thời hạn tạm ngừng: Thời hạn tạm ngừng kinh doanh ghi trong thông báo không được quá một năm. Sau khi hết thời hạn đã thông báo, nếu doanh nghiệp, hộ kinh doanh vẫn tiếp tục tạm ngừng kinh doanh thì phải thông báo tiếp cho cơ quan đăng ký kinh doanh. Tổng thời gian tạm ngừng kinh doanh liên tiếp không được quá hai (02) năm.&rdquo;</em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Thuế môn bài:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp;Căn cứ công văn số 1263/TCT &ndash; KK ngày 18 tháng 4 năm 2013 về thuế Môn bài, trong đó có đoạn:<br />\r\n<em>&ldquo;1. Đối với người nộp thuế có thông báo tạm ngừng kinh doanh cả năm dương lịch (01/01-31/12) thì không phải kê khai, nộp thuế môn bài đối với năm dương lịch tạm ngừng kinh doanh. Nếu người nộp thuế ra kinh doanh trước thời hạn trong 06 tháng đầu năm nộp mức thuế môn bài cả năm; nếu người nộp thuế ra kinh doanh trong 06 tháng cuối năm nộp 50% mức thuế môn bài cả năm. 2. Đối với người nộp thuế có thông báo tạm ngừng kinh doanh 12 tháng, không trùng với năm dương lịch (thời gian tạm nghỉ kinh doanh kéo dài trong 02 năm dương lịch) thì thực hiện kê khai, nộp thuế môn bài cả năm của năm dương lịch thứ nhất. Đối với năm dương lịch thứ 2, nếu người nộp thuế ra kinh doanh trong 06 tháng đầu năm nộp mức thuế môn bài cả năm; nếu người nộp thuế ra kinh doanh trong 06 tháng cuối năm nộp 50% mức thuế môn bài cả năm.</em></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2017/11/Screen-Shot-2016-12-21-at-10.20.37-AM.png\"><img alt=\"\" sizes=\"(max-width: 543px) 100vw, 543px\" src=\"https://ketoanuytin.com/wp-content/uploads/2017/11/Screen-Shot-2016-12-21-at-10.20.37-AM-300x132.png\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2017/11/Screen-Shot-2016-12-21-at-10.20.37-AM-300x132.png 300w, https://ketoanuytin.com/wp-content/uploads/2017/11/Screen-Shot-2016-12-21-at-10.20.37-AM-100x44.png 100w, https://ketoanuytin.com/wp-content/uploads/2017/11/Screen-Shot-2016-12-21-at-10.20.37-AM.png 666w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>I.&nbsp; &nbsp;Hồ sơ thông báo tạm ngừng hoạt động kinh doanh</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Thông báo tạm ngừng hoạt động kinh doanh (Theo mẫu được ban hành kèm theo thông tư 20/2015/TT-BKHĐT). Thông báo phải bao gồm một số nội dung như:</span></li>\r\n	<li><span style=\"font-size:16px;\">Tên, mã số doanh nghiệp;</span></li>\r\n	<li><span style=\"font-size:16px;\">Thời điểm và thời hạn tạm ngừng hoạt động kinh doanh;</span></li>\r\n	<li><span style=\"font-size:16px;\">Lý do tạm ngừng kinh doanh;</span></li>\r\n	<li><span style=\"font-size:16px;\">Đối với trường hợp doanh nghiệp có chi nhánh hoặc văn phòng đại diện, địa điểm kinh doanh thì phải bổ sung thêm thông tin về tên và mã số thuế chi nhánh, văn phòng đại diện, địa điểm kinh doanh; thời điểm và thời hạn tạm ngừng; lý do tạm ngừng.</span></li>\r\n	<li><span style=\"font-size:16px;\">Quyết định về việc tạm ngừng hoạt động kinh doanh của chủ doanh nghiệp tư nhân, của chủ sở hữu đối với công ty Trách nhiệm hữu hạn một thành viên, của Hội đồng thành viên đối với công ty Trách nhiệm hữu hạn 2 thành viên và của Hội đồng quản trị đối với Công ty cổ phần.</span></li>\r\n	<li><span style=\"font-size:16px;\">Biên bản hợp thông qua việc tạm ngừng hoạt động kinh doanh của Hội đồng thành viên công ty Trách nhiệm hữu hạn 2 thành viên và của Hội đồng quản trị công ty Cổ phần.</span></li>\r\n	<li><span style=\"font-size:16px;\">Bản sao giấy chứng nhận đăng ký thành lập doanh nghiệp</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>II.&nbsp; Thời hạn giải quyết hồ sơ</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi tiếp nhận hồ sơ tạm ngừng hoạt động kinh doanh của doanh nghiệp, phòng đăng ký kinh doanh sẽ trao giấy biên nhận hồ sơ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trong vòng&nbsp;3 ngày&nbsp;kể từ ngày tiếp nhận hồ sơ hợp lệ, Phòng đăng ký kinh doanh sẽ cấp giấy xác nhận về việc doanh nghiệp tạm ngừng kinh doanh và gửi thông báo tạm ngừng cho cơ quan thuế để phối hợp quản lý.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>III.&nbsp; Trình tự thực hiện:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>1. Đối với cá nhân, tổ chức:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Gửi hồ sơ đề nghị chấp thuận tạm ngừng hoạt động đến UBCKNN</span></li>\r\n	<li><span style=\"font-size:16px;\">Sửa đổi, bổ sung hồ sơ hoặc giải trình các vấn đề liên quan theo yêu cầu của UBCKNN (nếu có)</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>2. Đối với cơ quan hành chính nhà nước:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Tiếp nhận hồ sơ báo cáo, xem xét hồ sơ và có ý kiến bằng văn bản nếu hồ sơ chưa đáp ứng điều kiện</span></li>\r\n	<li><span style=\"font-size:16px;\">Trong thời hạn 15 ngày, kể từ ngày nhận được hồ sơ hợp lệ, UBCKNN ra quyết định chấp thuận tạm ngừng hoạt động cho trụ sở chính, chi nhánh, phòng giao dịch của công ty chứng khoán. Trường hợp từ chối, UBCKNN phải trả lời bằng văn bản và nêu rõ lý do.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>IV. Ra kinh doanh trở lại:</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&nbsp;Trên thông báo tạm ngừng kinh doanh đã ghi rõ tạm ngừng đến ngày nào. Do vậy khi hết hạn tạm ngừng kinh doanh theo thông báo ban đầu, doanh nghiệp được phép hoạt động kinh doanh trở lại bình thường. Khi đó doanh nghiệp thực hiện đầy đủ nghĩa vụ thuế với cơ quan thuế quản lý. Trường hợp doanh nghiệp muốn ra kinh doanh trước thời hạn trong thông báo tạm ngừng thì phải có văn bản gửi cơ quan thuế quản lý trực tiếp và cơ quản đăng ký kinh doanh.</span></p>\r\n\r\n<h3><span style=\"font-size:16px;\">V.&nbsp; &nbsp; Như vậy, theo quy định trên trong thời hạn tạm ngừng kinh doanh:</span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Muốn tạm ngừng phải họp hành ra biên bản, quyết định và gửi thông báo<br />\r\n	Trong thời hạn tạm ngừng không phải nộp hồ sơ khai thuế(như thuế GTGT, TNCN, TNDN&hellip;.)</span></li>\r\n	<li><span style=\"font-size:16px;\">Nếu tạm ngừng không trọn năm dương lịch hoặc năm tài chính thì vẫn phải quyết toán các loại thuế theo năm. (Ví dụ DN tạm ngừng chỉ một số tháng trong năm, hoặc tạm ngừng từ thời điểm nào đó đến ngày cuối năm, hoặc tạm ngừng mà liên quan đến 2 năm tài chính hoặc 2 năm dương lịch).</span></li>\r\n	<li><span style=\"font-size:16px;\">Nếu tạm ngừng trọn năm tài chính hoặc năm dương lịch thì không phải nộp hồ sơ quyết toán thuế năm (Ví dụ DN tạm ngừng đủ từ 01/01/N đến 31/12/N).</span></li>\r\n	<li><span style=\"font-size:16px;\">Thuế môn bài nộp căn cứ theo thời gian ra kinh doanh.</span></li>\r\n	<li><span style=\"font-size:16px;\">Thời hạn tạm ngừng kinh doanh liên tiếp không được quá 2 năm.</span></li>\r\n	<li><span style=\"font-size:16px;\">Hết thời hạn tạm ngừng phải thực hiện kê khai thuế theo quy định.</span></li>\r\n	<li><span style=\"font-size:16px;\">Muốn ra kinh doanh trước hạn phải gửi thông báo tới cơ quan thuế và cơ quan quản lý kinh doanh.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong><em>Trên đây là hướng dẫn chi tiết các bước và&nbsp;thủ tục tạm ngừng kinh doanh.&nbsp;Bạn có thể chưa gặp trường hợp này, nhưng hãy lưu thông tin này lại. Biết đâu một ngày nào đó bạn sẽ cần đến nó.</em></strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>Bằng tất cả sự tận tâm, chuyên nghiệp và thái độ phục vụ tốt nhất, chúng tôi đã và đang là nhà cung cấp dịch vụ uy tín cho hàng trăm khách hàng lớn, nhỏ.</strong></em></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 08:12:15', '2018-11-26 08:12:15', NULL),
(18, 'Báo cáo tài chính năm 2017 lưu ý 7 vấn đề chính', 'bao-cao-tai-chinh-nam-2017-luu-y-7-van-de-chinh', NULL, '<h1><span style=\"color:#ff0000;\"><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><strong><span style=\"font-size:16px;\">BÁO CÁO TÀI CHÍNH 2017 LƯU Ý 7 VẤN ĐỀ</span></strong></span></span></h1>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>7 quy định về lập báo cáo tài chính năm 2017.</strong>&nbsp;Năm tài chính 2017 này, doanh nghiệp áp dụng theo chế độ kế toán nào? Các doanh nghiệp nào cần phải nộp báo cáo tài chính? Hồ sơ nộp gồm những gì? Nộp báo cáo tài chính ở đâu?,&hellip; Các bạn tham khảo bài viết dưới đây nhé.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>1. Kỳ lập báo cáo tài chính</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Báo cáo tài chính được lập theo quý hoặc theo năm.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tất cả các loại hình doanh nghiệp đều phải lập báo cáo tài chính theo năm.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Chỉ những loại hình doanh nghiệp phải nộp báo cáo tài chính theo quý&nbsp;gồm: Doanh nghiệp Nhà nước, doanh nghiệp có vốn đầu tư nước ngoài, những doanh nghiệp tham gia thị trường chứng khoán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>2. Chế độ kế toán doanh nghiệp lựa chọn áp dụng năm 2017</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Tùy thuộc vào đặc điểm, nguồn vốn kinh doanh của doanh nghiệp, doanh nghiệp có thể lựa chọn một trong những chế độ kế toán sau để thực hiện:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Chế độ kế toán theo&nbsp;Thông tư 133/2016/TT-BTC&nbsp;(áp dụng đối với những doanh nghiệp vừa và nhỏ)</span></li>\r\n	<li><span style=\"font-size:16px;\">Chế độ kế toán theo&nbsp;Thông tư 200/2014/TT-BTC(Được áp dụng đối với những doanh nghiệp lớn hoặc doanh nghiệp vừa và nhỏ muốn thực hiện theo thông tư 200)</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\">3. Quy định về kiểm toán báo cáo tài chính</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Có một số doanh nghiệp bắt buộc trước khi nộp báo cáo tài chính cho các cơ quan liên quan phải tiến hành kiểm toán báo cáo tài chính đã lập. Bao gồm các doanh nghiệp sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Doanh nghiệp có vốn đầu tư nước ngoài,</span></li>\r\n	<li><span style=\"font-size:16px;\">Doanh nghiệp tham gia thị trường chứng khoán</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\">5. Nơi nhận báo cáo tài chính</span></p>\r\n\r\n<table width=\"726\">\r\n	<tbody>\r\n		<tr>\r\n			<td width=\"138\">&nbsp;</td>\r\n			<td width=\"72\"><span style=\"font-size:16px;\">Sở Tài chính</span></td>\r\n			<td width=\"72\"><span style=\"font-size:16px;\">Cơ quan thuế</span></td>\r\n			<td width=\"78\"><span style=\"font-size:16px;\">Cơ quan Thống kê</span></td>\r\n			<td width=\"90\"><span style=\"font-size:16px;\">Doanh nghiệp mẹ (Doanh nghiệp cấp trên)</span></td>\r\n			<td width=\"84\"><span style=\"font-size:16px;\">Cơ quan đăng ký kinh doanh</span></td>\r\n			<td width=\"108\"><span style=\"font-size:16px;\">Ủy ban chứng khoán NN và sở giao dịch chứng khoán</span></td>\r\n			<td width=\"84\"><span style=\"font-size:16px;\">Ban quản lý khu chế xuất</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"138\"><span style=\"font-size:16px;\">Doanh nghiệp nhà nước</span></td>\r\n			<td width=\"72\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"72\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; X</span></td>\r\n			<td width=\"78\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; X</span></td>\r\n			<td width=\"90\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"84\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; X</span></td>\r\n			<td width=\"108\">&nbsp;</td>\r\n			<td width=\"84\">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"138\"><span style=\"font-size:16px;\">Doanh nghiệp có vốn đầu tư nước ngoài</span></td>\r\n			<td width=\"72\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"72\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"78\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; X</span></td>\r\n			<td width=\"90\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; X</span></td>\r\n			<td width=\"84\">&nbsp;</td>\r\n			<td width=\"108\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; X</span></td>\r\n			<td width=\"84\">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"138\"><span style=\"font-size:16px;\">Doanh nghiệp ở khu chế xuất, khu CN, khu CN cao</span></td>\r\n			<td width=\"72\">&nbsp;</td>\r\n			<td width=\"72\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"78\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"90\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"84\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; X</span></td>\r\n			<td width=\"108\">&nbsp;</td>\r\n			<td width=\"84\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; X</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"138\"><span style=\"font-size:16px;\">Doanh nghiệp còn lại</span></td>\r\n			<td width=\"72\">&nbsp;</td>\r\n			<td width=\"72\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"78\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"90\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"84\"><span style=\"font-size:16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;X</span></td>\r\n			<td width=\"108\">&nbsp;</td>\r\n			<td width=\"84\">&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:16px;\">5. Bộ báo cáo tài chính phải nộp</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Bộ báo cáo tài chính hoàn chỉnh doanh nghiệp phải nộp cho các cơ quan quản lý bao gồm những mẫu biểu sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:16px;\">Bảng cân đối kế toán,</span></li>\r\n	<li><span style=\"font-size:16px;\">Báo cáo kết quả kinh doanh,</span></li>\r\n	<li><span style=\"font-size:16px;\">Báo cáo lưu chuyển tiền tệ (Doanh nghiệp áp dụng&nbsp;chế độ kế toán theo Thông tư 133&nbsp;không cần nộp),</span></li>\r\n	<li><span style=\"font-size:16px;\">Thuyết minh báo cáo tài chính,</span></li>\r\n	<li><span style=\"font-size:16px;\">Bảng cân đối số phát sinh (Doanh nghiệp áp dụng&nbsp;chế độ kế toán theo Thông tư 200 không cần nộp),</span></li>\r\n	<li><span style=\"font-size:16px;\">Quyết toán thuế TNDN và các phụ lục đi kèm</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:16px;\">6. Thời hạn nộp báo cáo tài chính</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Mỗi loại hình doanh nghiệp có thời hạn nộp báo cáo tài chính khác nhau.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">a) Đối với doanh nghiệp nhà nước:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash;&nbsp;Các công ty con&nbsp;phải nộp báo cáo tài chính năm cho công ty mẹ&nbsp;chậm nhất là&nbsp;sau 30 ngày, kể từ ngày kết thúc năm tài chính.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash;&nbsp;Công ty mẹ&nbsp;nộp báo cáo tài chính&nbsp;chậm nhất là&nbsp;sau 90 ngày, kể từ ngày kết thúc năm tài chính.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">b) Đối với các loại hình doanh nghiệp khác:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Thời hạn nộp Báo cáo tài chính năm chậm nhất là&nbsp;90 ngày, kể từ ngày kết thúc năm tài chính.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Chi nhánh, đơn vị kế toán trực thuộc nộp Báo cáo tài chính năm cho đơn vị kế toán cấp trên theo quy định của đơn vị kế toán cấp trên.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">7. Các doanh nghiệp không phải nộp báo cáo tài chính năm</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">a) Doanh nghiệp được phép gộp Báo cáo tài chính</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Tại khoản 4 điều 12&nbsp;<strong>luật kế toán</strong>&nbsp;số 88/2015/QH13 NGÀY 20/11/2015 quy định:&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>&ldquo;4. Trường hợp kỳ kế toán năm đầu tiên hoặc kỳ kế toán năm cuối cùng có thời gian&nbsp;</strong></em><em><strong>ngắn hơn&nbsp;chín mươi ngày&nbsp;</strong></em><em><strong>thì được phép cộng (+) với kỳ kế toán năm tiếp theo hoặc cộng (+) với kỳ kế toán năm trước đó để tính thành một kỳ kế toán năm. Kỳ kế toán năm đầu tiên hoặc kỳ kế toán năm cuối cùng phải&nbsp;ngắn hơn mười lăm tháng.&rdquo;</strong></em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Lưu ý:&nbsp;Nếu doanh nghiệp muốn gộp báo cáo tài chính, doanh nghiệp nên làm công văn đề nghị gộp báo cáo tài chính gửi cơ quan thuế quản lý</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">b) Doanh nghiệp không phải lập Báo cáo tài chính</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Doanh nghiệp đang trong thời hạn tạm ngừng kinh doanh nguyên năm tài chính từ 01/01-31/12 năm tài chính hiện tại thì không phải nộp Báo cáo tài chính.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\">Có thể bạn quan tâm:&nbsp;&ldquo;Mức phạt vi phạm về báo cáo tài chính&rdquo;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Dưới đây là một số mức phạt tương ứng với các vi phạm khá phổ biến trong hoạt động liên quan đến báo cáo tài chính quy định tại&nbsp;Điều 10 Nghị định 105/2013/NĐ-CP&nbsp;mà mọi người cần biết.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\"><strong><em>STT</em></strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong><em>Hành vi vi phạm</em></strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong><em>Mức phạt tiền</em></strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">1</span></td>\r\n			<td><span style=\"font-size:16px;\">Không lập báo cáo tài chính hoặc lập báo cáo tài chính không đầy đủ nội dung theo quy định</span></td>\r\n			<td rowspan=\"5\"><span style=\"font-size:16px;\"><em>Từ 5 triệu đồng<br />\r\n			đến 10 triệu đồng</em></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">2</span></td>\r\n			<td><span style=\"font-size:16px;\">Lập và trình bày báo cáo tài chính không đúng phương pháp; không rõ ràng; không nhất quán theo quy định</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">3</span></td>\r\n			<td><span style=\"font-size:16px;\">Nộp báo cáo tài chính, báo cáo quyết toán cho cơ quan nhà nước có thẩm quyền chậm từ 01 tháng đến 03 tháng theo thời hạn quy định</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">4</span></td>\r\n			<td><span style=\"font-size:16px;\">Công khai báo cáo tài chính không đầy đủ nội dung theo quy định, gồm:<br />\r\n			+ Quyết toán thu, chi ngân sách nhà nước năm và các khoản thu chi tài chính khác;<br />\r\n			+ Tình hình tài sản, nợ phải trả, vốn chủ sở hữu, kết quả hoạt động kinh doanh, trích lập và sử dụng các quỹ, thu nhập của nguời lao động</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">5</span></td>\r\n			<td><span style=\"font-size:16px;\">Công khai báo cáo tài chính chậm từ 01 &ndash; 03 tháng theo thời hạn quy định</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">6</span></td>\r\n			<td><span style=\"font-size:16px;\">Nộp báo cáo tài chính, báo cáo quyết toán cho cơ quan nhà nước có thẩm quyền chậm quá 03 tháng theo thời hạn quy định</span></td>\r\n			<td rowspan=\"8\"><span style=\"font-size:16px;\"><em>Từ 20 triệu đồng<br />\r\n			đến 30 triệu đồng</em></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">7</span></td>\r\n			<td><span style=\"font-size:16px;\">Lập báo cáo tài chính không đúng với số liệu trên sổ kế toán và chứng từ kế toán</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">8</span></td>\r\n			<td><span style=\"font-size:16px;\">Giả mạo báo cáo tài chính, khai man số liệu trên báo cáo tài chính</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">9</span></td>\r\n			<td><span style=\"font-size:16px;\">Thỏa thuận hoặc ép buộc người khác giả mạo báo cáo tài chính, khai man số liệu trên báo cáo tài chính</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">10</span></td>\r\n			<td><span style=\"font-size:16px;\">Cố ý, thỏa thuận hoặc ép buộc người khác cung cấp, xác nhận thông tin, số liệu kế toán sai sự thật</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">11</span></td>\r\n			<td><span style=\"font-size:16px;\">Thực hiện việc công khai báo cáo tài chính chậm quá 03 tháng theo thời hạn quy định</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">12</span></td>\r\n			<td><span style=\"font-size:16px;\">Thông tin, số liệu công khai báo cáo tài chính sai sự thật</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">13</span></td>\r\n			<td><span style=\"font-size:16px;\">Nộp báo cáo tài chính cho cơ quan nhà nước có thẩm quyền không đính kèm báo cáo kiểm toán đối với các trường hợp mà pháp luật quy định phải kiểm toán</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px;\"><a href=\"https://ketoanuytin.com/wp-content/uploads/2016/07/22473396_2059961614237171_1150953274_o.jpg\"><img alt=\"\" sizes=\"(max-width: 373px) 100vw, 373px\" src=\"https://ketoanuytin.com/wp-content/uploads/2016/07/22473396_2059961614237171_1150953274_o-300x225.jpg\" srcset=\"https://ketoanuytin.com/wp-content/uploads/2016/07/22473396_2059961614237171_1150953274_o-300x225.jpg 300w, https://ketoanuytin.com/wp-content/uploads/2016/07/22473396_2059961614237171_1150953274_o-768x576.jpg 768w, https://ketoanuytin.com/wp-content/uploads/2016/07/22473396_2059961614237171_1150953274_o-1024x768.jpg 1024w, https://ketoanuytin.com/wp-content/uploads/2016/07/22473396_2059961614237171_1150953274_o-100x75.jpg 100w, https://ketoanuytin.com/wp-content/uploads/2016/07/22473396_2059961614237171_1150953274_o.jpg 1334w\" style=\"width: 68%; height: 68%;\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em><strong>Bằng tất cả sự tận tâm, chuyên nghiệp và thái độ phục vụ tốt nhất, chúng tôi đã và đang là nhà cung cấp dịch vụ uy tín cho hàng trăm khách hàng lớn, nhỏ.</strong></em></span></p>', NULL, 1, 1, NULL, 1, '2018-11-26 08:14:02', '2018-11-26 08:14:02', NULL);
INSERT INTO `posts` (`id`, `title`, `path`, `description`, `content`, `image`, `post_type`, `is_active`, `category_item_id`, `user_id`, `created_at`, `updated_at`, `seo_id`) VALUES
(19, 'Chế độ kế toán mới áp dụng từ ngày 01/01/2018', 'che-do-ke-toan-moi-ap-dung-tu-ngay-01012018', NULL, '<h1><span style=\"color:#ff0000;\"><span style=\"font-family:Lucida Sans Unicode,Lucida Grande,sans-serif;\"><strong><span style=\"font-size:18px;\">Chế độ kế toán mới áp dụng từ ngày 01/01/2018</span></strong></span></span></h1>\r\n\r\n<h2><span style=\"font-size:16px;\">Hướng dẫn chế độ kế toán mới áp dụng từ ngày 01/01/2018 theo&nbsp;Thông tư 107/2017/TT-BTC&nbsp;</span></h2>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>&nbsp;Bộ Tài chính ban hành Thông tư 107/2017/TT-BTC hướng dẫn chế độ kế toán hành chính, sự nghiệp.&nbsp;Thông tư bắt đầu có hiệu lực kể từ ngày 24/11/2017&nbsp;và áp dụng kể từ ngày 01/01/2018. Theo đó,&nbsp;Thông tư hướng dẫn danh mục biểu mẫu và phương pháp lập chứng từ kế toán bắt buộc; danh mục hệ thống tài khoản và phương pháp hạch toán tài khoản kế toán; danh mục mẫu sổ và phương pháp lập sổ kế toán; danh mục mẫu báo cáo và phương pháp lập và trình bày báo cáo tài chính.</strong></span></p>\r\n\r\n<table width=\"786\">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\"><strong>BỘ TÀI CHÍNH</strong><br />\r\n			<strong>&mdash;&mdash;-</strong></span></td>\r\n			<td><span style=\"font-size:16px;\"><strong>CỘNG HÒA XÃ HỘI CHỦ NGHĨA VIỆT NAM<br />\r\n			Độc lập &ndash; Tự do &ndash; Hạnh phúc<br />\r\n			&mdash;&mdash;&mdash;&mdash;&mdash;</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-size:16px;\">Số: 107/2017/TT-BTC</span></td>\r\n			<td><span style=\"font-size:16px;\"><em>Hà Nội</em><em>, ngày&nbsp;</em><em>10</em><em>&nbsp;tháng&nbsp;</em><em>10</em><em>&nbsp;năm 20</em><em>17</em></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>THÔNG TƯ</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">HƯỚNG DẪN CHẾ ĐỘ KẾ TOÁN HÀNH CHÍNH, SỰ NGHIỆP</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Căn cứ Luật Kế toán s</em><em>ố</em><em>&nbsp;88/2015/QH13 ngày 20 tháng 11 năm 2015;</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Căn cứ Nghị định số&nbsp;</em><em>1</em><em>74/2016/NĐ-CP ngày 30 tháng 12 năm 2016 của Chính phủ quy định chi tiết và hướng dẫn thi hành một số điều của Luật Kế toán;</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Căn cứ Nghị định số 87/2017/NĐ-CP ngày 26 tháng 7 năm 2017 của Chính phủ quy định chức năng, nhiệm vụ, quyền hạn và cơ cấu tổ chức của Bộ Tài chính;</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Theo đề nghị của Vụ trưởng Vụ Chế độ Kế toán và Ki</em><em>ể</em><em>m toán,</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Bộ trưởng Bộ Tài chính ban hành Thông tư hướng dẫn Chế độ kế toán hành chính, sự nghiệp.</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Chương I:&nbsp;</strong><strong>QUI ĐỊNH CHUNG</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 1. Phạm vi điều chỉnh</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Thông tư này hướng dẫn danh mục biểu mẫu và phương pháp lập chứng từ kế toán bắt buộc; danh mục hệ thống tài khoản và phương pháp hạch toán tài khoản kế toán; danh mục mẫu sổ và phương pháp lập sổ kế toán; danh mục mẫu báo cáo và phương pháp lập và trình bày báo cáo tài chính, báo cáo quyết toán ngân sách của các đơn vị quy định tại Điều 2 Thông tư này.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 2. Đối tượng áp dụng</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Thông tư này hướng dẫn kế toán áp dụng cho: Cơ quan nhà nước; đơn vị sự nghiệp công lập, trừ các đơn vị sự nghiệp công lập tự đảm bảo chi thường xuyên và chi đầu tư được vận dụng cơ chế tài chính như doanh nghiệp, áp dụng chế độ kế toán doanh nghiệp khi đáp ứng đủ các điều kiện theo quy định hiện hành; tổ chức, đơn vị khác có hoặc không sử dụng ngân sách nhà nước (sau đây gọi tắt là đơn vị hành chính, sự nghiệp).</span></li>\r\n	<li><span style=\"font-size:16px;\">Đối với các đơn vị sự nghiệp công lập tự đảm bảo chi thường xuyên và chi đầu tư được áp dụng chế độ kế toán doanh nghiệp, nếu thực hiện đơn đặt hàng của Nhà nước hoặc có tiếp nhận viện trợ không hoàn lại của nước ngoài hoặc có nguồn phí được khấu trừ, để lại thì phải lập báo cáo quyết toán quy định tại Phụ lục 04 của Thông tư này.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Chương II:&nbsp;</strong><strong>QUY ĐỊNH CỤ THỂ</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 3. Quy định về chứng từ kế toán</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Các đơn vị hành chính, sự nghiệp đều phải sử dụng thống nhất mẫu chứng từ kế toán thuộc loại bắt buộc quy định trong Thông tư này. Trong quá trình thực hiện, các đơn vị không được sửa đổi biểu mẫu chứng từ thuộc loại bắt buộc.</span></li>\r\n	<li><span style=\"font-size:16px;\">Ngoài các chứng từ kế toán bắt buộc được quy định tại Thông tư này và các văn bản khác, đơn vị hành chính, sự nghiệp được tự thiết kế mẫu chứng từ để phản ánh các nghiệp vụ kinh tế phát sinh. Mẫu chứng từ tự thiết kế phải đáp ứng tối thiểu 7 nội dung quy định tại Điều 16 Luật Kế toán, phù hợp với việc ghi chép và yêu cầu quản lý của đơn vị.</span></li>\r\n	<li><span style=\"font-size:16px;\">Đối với mẫu chứng từ in sẵn phải được bảo quản cẩn thận, không được để hư hỏng, mục nát. Séc, Biên lai thu tiền và giấy tờ có giá phải được quản lý như tiền.</span></li>\r\n	<li><span style=\"font-size:16px;\">Danh mục, mẫu và giải thích phương pháp lập các chứng từ kế toán bắt buộc quy định tại Phụ lục số 01 kèm theo Thông tư này.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 4. Quy định về tài khoản kế toán</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Tài khoản kế toán phản ánh thường xuyên, liên tục, có hệ thống tình hình về tài sản, tiếp nhận và sử dụng nguồn kinh phí do ngân sách nhà nước cấp và các nguồn kinh phí khác; tình hình thu, chi hoạt động, kết quả hoạt động và các khoản khác ở các đơn vị hành chính sự nghiệp.</span></li>\r\n	<li><span style=\"font-size:16px;\">Phân loại hệ thống tài khoản kế toán:</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Các loại tài khoản trong bảng gồm tài khoản từ loại 1 đến loại 9, được hạch toán kép (hạch toán bút toán đối ứng giữa các tài khoản). Tài khoản trong bảng dùng để kế toán tình hình tài chính (gọi tắt là kế toán tài chính), áp dụng cho tất cả các đơn vị, phản ánh tình hình tài sản, công nợ, nguồn vốn, doanh thu, chi phí, thặng dư (thâm hụt) của đơn vị trong kỳ kế toán.</span></li>\r\n	<li><span style=\"font-size:16px;\">b) Loại tài khoản ngoài bảng gồm tài khoản loại 0, được hạch toán đơn (không hạch toán bút toán đối ứng giữa các tài khoản). Các tài khoản ngoài bảng liên quan đến ngân sách nhà nước hoặc có nguồn gốc ngân sách nhà nước (TK 004, 006, 008, 009, 012, 013, 014, 018) phải được phản ánh theo mục lục ngân sách nhà nước, theo niên độ (năm trước, năm nay, năm sau (nếu có)) và theo các yêu cầu quản lý khác của ngân sách nhà nước.</span></li>\r\n	<li><span style=\"font-size:16px;\">c) Trường hợp một nghiệp vụ kinh tế tài chính phát sinh liên quan đến tiếp nhận, sử dụng: nguồn ngân sách nhà nước cấp; nguồn viện trợ, vay nợ nước ngoài; nguồn phí được khấu trừ, để lại thì kế toán vừa phải hạch toán kế toán theo các tài khoản trong bảng, đồng thời hạch toán các tài khoản ngoài bảng, chi tiết theo mục lục ngân sách nhà nước và niên độ phù hợp.</span></li>\r\n	<li><span style=\"font-size:16px;\">Lựa chọn áp dụng hệ thống tài khoản:</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Các đơn vị hành chính, sự nghiệp căn cứ vào Hệ thống tài khoản kế toán ban hành tại Thông tư này để lựa chọn tài khoản kế toán áp dụng cho đơn vị.</span></li>\r\n	<li><span style=\"font-size:16px;\">b) Đơn vị được bổ sung tài khoản kế toán trong các trường hợp sau:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Được bổ sung tài khoản chi tiết cho các tài khoản đã được quy định trong danh mục hệ thống tài khoản kế toán (Phụ lục số 02) kèm theo Thông tư này để phục vụ yêu cầu quản lý của đơn vị.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Trường hợp bổ sung tài khoản ngang cấp với các tài khoản đã được quy định trong danh mục hệ thống tài khoản kế toán (Phụ lục số 02) kèm theo Thông tư này thì phải được Bộ Tài chính chấp thuận bằng văn bản trước khi thực hiện.</span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:16px;\">Danh mục hệ thống tài khoản kế toán, giải thích nội dung, kết cấu và phương pháp ghi chép tài khoản kế toán nêu tại Phụ lục số 02 kèm theo Thông tư này.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 5. Quy định về sổ kế toán</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Đơn vị hành chính, sự nghiệp phải mở sổ kế toán để ghi chép, hệ thống và lưu giữ toàn bộ các nghiệp vụ kinh tế, tài chính đã phát sinh có liên quan đến đơn vị kế toán. Việc bảo quản, lưu trữ sổ kế toán thực hiện theo quy định của pháp luật về kế toán, các văn bản có liên quan và quy định tại Thông tư này.</span></li>\r\n	<li><span style=\"font-size:16px;\">Đơn vị hành chính, sự nghiệp có tiếp nhận, sử dụng: nguồn ngân sách nhà nước cấp; nguồn viện trợ, vay nợ nước ngoài; nguồn phí được khấu trừ, để lại phải mở sổ kế toán để theo dõi riêng theo Mục lục NSNN và theo các yêu cầu khác để phục vụ cho việc lập báo cáo quyết toán với ngân sách nhà nước và các cơ quan có thẩm quyền.</span></li>\r\n	<li><span style=\"font-size:16px;\">Các loại sổ kế toán</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Mỗi đơn vị kế toán chỉ sử dụng một hệ thống sổ kế toán cho một kỳ kế toán năm, bao gồm sổ kế toán tổng hợp và sổ kế toán chi tiết.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Tùy theo hình thức kế toán đơn vị áp dụng, đơn vị phải mở đầy đủ các sổ kế toán tổng hợp, sổ kế toán chi tiết và thực hiện đầy đủ, đúng nội dung, trình tự và phương pháp ghi chép đối với từng mẫu sổ kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sổ kế toán ngân sách, phí được khấu trừ, để lại phản ánh chi tiết theo mục lục ngân sách nhà nước để theo dõi việc sử dụng nguồn ngân sách nhà nước, nguồn phí được khấu trừ để lại.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sổ kế toán theo dõi quá trình tiếp nhận và sử dụng nguồn viện trợ, vay nợ nước ngoài phản ánh chi tiết theo mục lục ngân sách nhà nước làm cơ sở lập báo cáo quyết toán theo quy định của Thông tư này và theo yêu cầu của nhà tài trợ.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Mẫu sổ kế toán tổng hợp:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Sổ Nhật ký dùng để ghi chép các nghiệp vụ kinh tế, tài chính phát sinh theo trình tự thời gian. Trường hợp cần thiết có thể kết hợp việc ghi chép theo trình tự thời gian với việc phân loại, hệ thống hóa các nghiệp vụ kinh tế, tài chính đã phát sinh theo nội dung kinh tế. Số liệu trên Sổ Nhật ký phản ảnh tổng số các hoạt động kinh tế, tài chính phát sinh trong một kỳ kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Sổ Cái dùng để ghi chép các nghiệp vụ kinh tế, tài chính phát sinh theo nội dung kinh tế (theo tài khoản kế toán). Trên Sổ Cái có thể kết hợp việc ghi chép theo trình tự thời gian phát sinh và nội dung kinh tế của nghiệp vụ kinh tế, tài chính. Số liệu trên Sổ Cái phản ánh tổng hợp tình hình tài sản, nguồn kinh phí và tình hình sử dụng nguồn kinh phí.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">c) Mẫu sổ, thẻ kế toán chi tiết:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Sổ, thẻ kế toán chi tiết dùng để ghi chi tiết các nghiệp vụ kinh tế tài chính phát sinh liên quan đến các đối tượng kế toán theo yêu cầu quản lý mà Sổ Cái chưa phản ánh chi tiết. Số liệu trên sổ kế toán chi tiết cung cấp các thông tin cụ thể phục vụ cho việc quản lý trong nội bộ đơn vị và việc tính, lập các chỉ tiêu trong báo cáo tài chính và báo cáo quyết toán ngân sách nhà nước.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Căn cứ vào yêu cầu quản lý và yêu cầu hạch toán của từng đối tượng kế toán riêng biệt, đơn vị được phép bổ sung các chỉ tiêu (cột, hàng) trên sổ, thẻ kế toán chi tiết để phục vụ lập báo cáo tài chính, báo cáo quyết toán theo yêu cầu quản lý.</span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:16px;\">Trách nhiệm của người giữ và ghi sổ kế toán</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Sổ kế toán phải được quản lý chặt chẽ, phân công rõ ràng trách nhiệm cá nhân giữ và ghi sổ. Sổ kế toán giao cho nhân viên nào thì nhân viên đó phải chịu trách nhiệm về nội dung ghi trong sổ trong suốt thời gian giữ và ghi sổ.</span></li>\r\n	<li><span style=\"font-size:16px;\">b) Khi có sự thay đổi nhân viên giữ và ghi sổ, kế toán trưởng hoặc phụ trách kế toán phải tổ chức bàn giao trách nhiệm quản lý và ghi sổ kế toán giữa nhân viên kế toán cũ với nhân viên kế toán mới. Nhân viên kế toán cũ phải chịu trách nhiệm về toàn bộ những nội dung ghi trong sổ trong suốt thời gian giữ và ghi sổ, nhân viên kế toán mới chịu trách nhiệm từ ngày nhận bàn giao. Biên bản bàn giao phải được kế toán trưởng hoặc phụ trách kế toán ký xác nhận.</span></li>\r\n	<li><span style=\"font-size:16px;\">c) Sổ kế toán phải ghi kịp thời, rõ ràng, đầy đủ theo các nội dung của sổ. Thông tin, số liệu ghi vào sổ kế toán phải chính xác, trung thực, đúng với chứng từ kế toán tương ứng dùng để ghi sổ.</span></li>\r\n	<li><span style=\"font-size:16px;\">d) Việc ghi sổ kế toán phải theo trình tự thời gian phát sinh của nghiệp vụ kinh tế, tài chính. Thông tin, số liệu ghi trên sổ kế toán của năm sau phải kế tiếp thông tin, số liệu ghi trên sổ kế toán của năm trước liền kề, đảm bảo liên tục từ khi mở sổ đến khi khóa sổ.</span></li>\r\n	<li><span style=\"font-size:16px;\">Mở sổ kế toán</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Nguyên tắc mở sổ kế toán</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Sổ kế toán phải được mở vào đầu kỳ kế toán năm hoặc ngay sau khi có quyết định thành lập và bắt đầu hoạt động của đơn vị kế toán. Sổ kế toán được mở đầu năm tài chính, ngân sách mới để chuyển số dư từ sổ kế toán năm cũ chuyển sang và ghi ngay nghiệp vụ kinh tế, tài chính mới phát sinh thuộc năm mới từ ngày 01/01 của năm tài chính, ngân sách mới.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Số liệu trên các sổ kế toán theo dõi tiếp nhận và sử dụng nguồn ngân sách nhà nước sau ngày 31/12 được chuyển từ tài khoản năm nay sang tài khoản năm trước để tiếp tục theo dõi số liệu phát sinh trong thời gian chỉnh lý quyết toán, phục vụ lập báo cáo quyết toán ngân sách nhà nước theo quy định.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị được mở thêm các sổ kế toán chi tiết theo yêu cầu quản lý của đơn vị.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Trường hợp mở sổ kế toán bằng tay (thủ công):</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị kế toán phải hoàn thiện thủ tục pháp lý của sổ kế toán như sau:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đối với sổ kế toán đóng thành quyển:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Ngoài bìa (góc trên bên trái) phải ghi tên đơn vị kế toán, giữa bìa ghi tên sổ, ngày, tháng năm lập sổ, ngày, tháng, năm khóa sổ, họ tên và chữ ký của người lập sổ, kế toán trưởng hoặc người phụ trách kế toán và thủ trưởng đơn vị ký tên, đóng dấu; ngày, tháng, năm kết thúc ghi sổ hoặc ngày chuyển giao sổ cho người khác.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Các trang sổ kế toán phải đánh số trang từ trang một (01) đến hết trang số cuối cùng, giữa hai trang sổ phải đóng dấu giáp lai của đơn vị kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Sổ kế toán sau khi làm đầy đủ các thủ tục trên mới được coi là hợp pháp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đối với sổ tờ rời:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Đầu mỗi sổ tờ rời phải ghi rõ tên đơn vị, số thứ tự của từng tờ sổ, tên sổ, tháng sử dụng, họ tên của người giữ sổ và ghi sổ kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Các sổ tờ rời trước khi sử dụng phải được Thủ trưởng đơn vị ký xác nhận, đóng dấu và ghi vào sổ đăng ký sử dụng thẻ tờ rời.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Các sổ tờ rời phải sắp xếp theo thứ tự các tài khoản kế toán và phải đảm bảo an toàn và dễ tìm.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">c) Trường hợp lập sổ kế toán trên phương tiện điện tử:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Phải đảm bảo các yếu tố của sổ kế toán theo quy định của pháp luật về kế toán. Nếu lựa chọn lưu trữ sổ kế toán trên phương tiện điện tử thì vẫn phải in sổ kế toán tổng hợp ra giấy, đóng thành quyển và phải làm đầy đủ các thủ tục quy định nêu tại điểm b, khoản 5 Điều này.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Các sổ kế toán còn lại, nếu không in ra giấy, mà thực hiện lưu trữ trên các phương tiện điện tử thì Thủ trưởng đơn vị kế toán phải chịu trách nhiệm để bảo đảm an toàn, bảo mật thông tin dữ liệu và phải bảo đảm tra cứu được trong thời hạn lưu trữ.</span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:16px;\">Ghi sổ kế toán</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Việc ghi sổ kế toán phải căn cứ vào chứng từ kế toán, mọi số liệu ghi trên sổ kế toán phải có chứng từ kế toán chứng minh; phải đảm bảo số và chữ rõ ràng, liên tục có hệ thống, không được viết tắt, không ghi chồng đè, không được bỏ cách dòng.</span></li>\r\n	<li><span style=\"font-size:16px;\">b) Trường hợp ghi sổ kế toán thủ công, phải dùng mực không phai, không dùng mực đỏ để ghi sổ kế toán. Phải thực hiện theo trình tự ghi chép và các mẫu sổ kế toán quy định tại Phụ lục số 03. Khi ghi hết trang sổ phải cộng số liệu của từng trang để mang số cộng trang trước sang đầu trang kế tiếp, không được ghi xen thêm vào phía trên hoặc phía dưới. Nếu không ghi hết trang sổ phải gạch chéo phần không ghi, không tẩy xóa, cấm dùng chất hóa học để sửa chữa.</span></li>\r\n	<li><span style=\"font-size:16px;\">Khóa sổ kế toán</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Khóa sổ kế toán là việc cộng sổ để tính ra tổng số phát sinh bên Nợ, bên Có và số dư cuối kỳ của từng tài khoản kế toán hoặc tổng số thu, chi, tồn quỹ, nhập, xuất, tồn kho.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">a) Kỳ khóa sổ</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Sổ quỹ tiền mặt phải thực hiện khóa sổ vào cuối mỗi ngày. Sau khi khóa sổ phải thực hiện đối chiếu giữa sổ tiền mặt của kế toán với sổ quỹ của thủ quỹ và tiền mặt có trong két đảm bảo chính xác, khớp đúng. Riêng ngày cuối tháng phải lập Bảng kiểm kê quỹ tiền mặt, sau khi kiểm kê, Bảng kiểm kê quỹ tiền mặt được lưu cùng với sổ kế toán tiền mặt ngày cuối cùng của tháng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Sổ tiền gửi ngân hàng, kho bạc phải khóa sổ vào cuối tháng để đối chiếu số liệu với ngân hàng, kho bạc; Bảng đối chiếu số liệu với ngân hàng, kho bạc (có xác nhận của ngân hàng, kho bạc) được lưu cùng Sổ tiền gửi ngân hàng, kho bạc hàng tháng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đơn vị kế toán phải khóa sổ kế toán tại thời điểm cuối kỳ kế toán năm, trước khi lập báo cáo tài chính.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Ngoài ra, đơn vị kế toán phải khóa sổ kế toán trong các trường hợp kiểm kê đột xuất hoặc các trường hợp khác theo quy định của pháp luật.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Trình tự khóa sổ kế toán</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">(1) Đối với ghi sổ thủ công:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Bước 1: Kiểm tra, đối chiếu trước khi khóa sổ kế toán</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Cuối kỳ kế toán, sau khi đã phản ánh hết các chứng từ kế toán phát sinh trong kỳ vào sổ kế toán, tiến hành đối chiếu giữa số liệu trên chứng từ kế toán (nếu cần) với số liệu đã ghi sổ, giữa số liệu của các sổ kế toán có liên quan với nhau để đảm bảo sự khớp đúng giữa số liệu trên chứng từ kế toán với số liệu đã ghi sổ và giữa các sổ kế toán với nhau. Tiến hành cộng số phát sinh trên Sổ Cái và các sổ kế toán chi tiết.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Từ các sổ, thẻ kế toán chi tiết lập Bảng tổng hợp chi tiết cho những tài khoản phải ghi trên nhiều sổ hoặc nhiều trang sổ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Tiến hành cộng số phát sinh Nợ, số phát sinh Có của tất cả các tài khoản trên Sổ Cái hoặc Nhật ký &ndash; Sổ Cái đảm bảo số liệu khớp đúng và bằng tổng số phát sinh. Sau đó tiến hành đối chiếu giữa số liệu trên Sổ Cái với số liệu trên sổ kế toán chi tiết hoặc Bảng tổng hợp chi tiết, giữa số liệu của kế toán với số liệu của thủ quỹ, thủ kho. Sau khi đảm bảo sự khớp đúng sẽ tiến hành khóa sổ kế toán. Trường hợp có chênh lệch phải xác định nguyên nhân và xử lý số chênh lệch cho đến khi khớp đúng.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Bước 2: Khóa sổ</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Khi khóa sổ phải kẻ một đường ngang dưới dòng ghi nghiệp vụ cuối cùng của kỳ kế toán. Sau đó ghi &ldquo;Cộng số phát sinh trong tháng&rdquo; phía dưới dòng đã kẻ;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Ghi tiếp dòng &ldquo;Số dư cuối kỳ&rdquo; (tháng, quý, năm);</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Ghi tiếp dòng &ldquo;Cộng số phát sinh lũy kế các tháng trước&rdquo; từ đầu quý;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Ghi tiếp dòng &ldquo;Tổng cộng số phát sinh lũy kế từ đầu năm&rdquo;;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>Dòng &ldquo;Số dư cuối kỳ&rdquo; tính như sau:</em></span></p>\r\n\r\n<table width=\"100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td width=\"21%\"><span style=\"font-size:16px;\"><em>Số dư Nợ cuối kỳ</em></span></td>\r\n			<td width=\"6%\"><span style=\"font-size:16px;\"><em>=</em></span></td>\r\n			<td width=\"21%\"><span style=\"font-size:16px;\"><em>Số dư Nợ đầu kỳ</em></span></td>\r\n			<td width=\"6%\"><span style=\"font-size:16px;\"><em>+</em></span></td>\r\n			<td width=\"18%\"><span style=\"font-size:16px;\"><em>Số phát sinh Nợ trong kỳ</em></span></td>\r\n			<td width=\"6%\"><span style=\"font-size:16px;\"><em>&ndash;</em></span></td>\r\n			<td width=\"19%\"><span style=\"font-size:16px;\"><em>Số phát sinh Có trong kỳ</em></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td width=\"21%\"><span style=\"font-size:16px;\"><em>Số dư Có cuối kỳ</em></span></td>\r\n			<td width=\"6%\"><span style=\"font-size:16px;\"><em>=</em></span></td>\r\n			<td width=\"21%\"><span style=\"font-size:16px;\"><em>Số dư Có đầu kỳ</em></span></td>\r\n			<td width=\"6%\"><span style=\"font-size:16px;\"><em>+</em></span></td>\r\n			<td width=\"18%\"><span style=\"font-size:16px;\"><em>Số phát sinh Có trong kỳ</em></span></td>\r\n			<td width=\"6%\"><span style=\"font-size:16px;\"><em>&ndash;</em></span></td>\r\n			<td width=\"19%\"><span style=\"font-size:16px;\"><em>Số phát sinh Nợ trong kỳ</em></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi tính được số dư của từng tài khoản, tài khoản nào dư Nợ thì ghi vào cột Nợ, tài khoản nào dư Có thì ghi vào cột Có.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Cuối cùng kẻ 2 đường kẻ liền nhau kết thúc việc khóa sổ.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Riêng một số sổ chi tiết có kết cấu các cột phát sinh Nợ, phát sinh Có và cột &ldquo;Số dư&rdquo; (hoặc nhập, xuất, &ldquo;còn lại&rdquo; hay thu, chi, &ldquo;tồn quỹ&rdquo;&hellip;) thì số liệu cột số dư (còn lại hay tồn) ghi vào dòng &ldquo;Số dư cuối kỳ&rdquo; của cột &ldquo;Số dư&rdquo; hoặc cột &ldquo;Tồn quỹ&rdquo;, hay cột &ldquo;Còn lại&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi khóa sổ kế toán, người ghi sổ phải ký dưới 2 đường kẻ, kế toán trưởng hoặc người phụ trách kế toán kiểm tra đảm bảo sự chính xác, cân đối sẽ ký xác nhận. Sau đó trình Thủ trưởng đơn vị kiểm tra và ký duyệt để xác nhận tính pháp lý của số liệu khóa sổ kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">(2) Đối với ghi sổ trên máy vi tính:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Việc thiết lập quy trình khóa sổ kế toán trên phần mềm kế toán cần đảm bảo và thể hiện các nguyên tắc khóa sổ đối với trường hợp ghi sổ kế toán thủ công.</span></p>\r\n\r\n<ol start=\"8\">\r\n	<li><span style=\"font-size:16px;\">Sửa chữa sổ kế toán</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Phương pháp sửa chữa sổ kế toán: Thực hiện theo quy định tại khoản 1, khoản 4 Điều 27 Luật Kế toán.</span></li>\r\n	<li><span style=\"font-size:16px;\">b) Các tình huống sửa chữa sổ kế toán đối với một (01) năm tài chính</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Đối với các bút toán đã ghi sổ thuộc năm tài chính, ngân sách năm N, trường hợp có sai sót hoặc có yêu cầu của cơ quan có thẩm quyền, việc điều chỉnh số liệu được quy định như sau:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>(</em><em>1</em><em>) Từ ngày 01/01 năm N đến ngày 31/12 năm N:</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trong thời gian từ ngày 01/01 năm N đến trước khi khóa sổ kế toán vào ngày 31/12 năm N, kế toán sửa chữa sổ kế toán tài chính năm hiện tại theo phương pháp quy định tại tiết a khoản 8 Điều này.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đối với các bút toán liên quan đến quyết toán ngân sách nhà nước, đồng thời điều chỉnh các thông tin trên sổ kế toán chi tiết theo dõi ngân sách phù hợp với các bút toán tài chính đã sửa chữa.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>(2) Từ ngày 01/01 năm N + 1 đến trước khi nộp báo cáo tài chính cho cơ quan nhà nước có th</em><em>ẩ</em><em>m quyền:</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trong thời gian từ ngày 01/01 năm N + 1 đến trước khi nộp báo cáo tài chính cho cơ quan nhà nước có thẩm quyền, kế toán sửa chữa sổ kế toán tài chính năm báo cáo theo phương pháp quy định tại tiết a khoản 8 Điều này.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đối với các bút toán liên quan đến quyết toán ngân sách nhà nước, đồng thời điều chỉnh các thông tin trên sổ kế toán chi tiết theo dõi ngân sách năm báo cáo phù hợp với các bút toán tài chính đã sửa chữa.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><em>(3) Sau khi nộp báo cáo tài chính cho cơ quan nhà nước có thẩm quyền:</em></span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Sau khi đã nộp báo cáo tài chính, đối với sổ kế toán tài chính, kế toán sửa chữa sổ kế toán tài chính năm phát hiện theo phương pháp quy định tại tiết a khoản 8 Điều này, đồng thời thuyết minh trên báo cáo tài chính.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đối với các bút toán liên quan đến quyết toán ngân sách nhà nước:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Trường hợp báo cáo quyết toán ngân sách nhà nước chưa được duyệt, đồng thời điều chỉnh các thông tin trên sổ kế toán chi tiết theo dõi ngân sách năm báo cáo.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Trường hợp báo cáo quyết toán ngân sách nhà nước đã được duyệt, đồng thời điều chỉnh các thông tin trên sổ kế toán chi tiết theo dõi ngân sách năm phát hiện và thuyết minh trên báo cáo quyết toán ngân sách nhà nước.</span></p>\r\n\r\n<ol start=\"9\">\r\n	<li><span style=\"font-size:16px;\">Danh mục sổ kế toán, mẫu sổ, hướng dẫn lập sổ kế toán nêu tại Phụ lục số 03 kèm theo Thông tư này.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 6. Báo cáo quyết toán</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Đối tượng lập báo cáo quyết toán</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị hành chính, sự nghiệp có sử dụng ngân sách nhà nước phải lập báo cáo quyết toán ngân sách đối với phần kinh phí do ngân sách nhà nước cấp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trường hợp đơn vị hành chính, sự nghiệp có phát sinh các khoản thu, chi từ nguồn khác, nếu có quy định phải quyết toán như nguồn ngân sách nhà nước cấp với cơ quan có thẩm quyền thì phải lập báo cáo quyết toán đối với các nguồn này.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:16px;\">Mục đích của báo cáo quyết toán</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo quyết toán ngân sách nhà nước dùng để tổng hợp tình hình tiếp nhận và sử dụng nguồn kinh phí ngân sách nhà nước của đơn vị hành chính, sự nghiệp, được trình bày chi tiết theo mục lục ngân sách nhà nước để cung cấp cho cơ quan cấp trên, cơ quan tài chính và cơ quan có thẩm quyền khác. Thông tin trên Báo cáo quyết toán ngân sách nhà nước phục vụ cho việc đánh giá tình hình tuân thủ, chấp hành quy định của pháp luật về ngân sách nhà nước và các cơ chế tài chính khác mà đơn vị chịu trách nhiệm thực hiện, là căn cứ quan trọng giúp cơ quan nhà nước, đơn vị cấp trên và lãnh đạo đơn vị kiểm tra, đánh giá, giám sát và điều hành hoạt động tài chính, ngân sách của đơn vị.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo quyết toán nguồn khác phản ánh tình hình thu &ndash; chi các nguồn khác (ngoài nguồn ngân sách nhà nước) của đơn vị hành chính, sự nghiệp, theo quy định của pháp luật phải thực hiện quyết toán với cơ quan cấp trên, cơ quan tài chính và cơ quan có thẩm quyền khác. Thông tin trên Báo cáo quyết toán nguồn khác phục vụ cho việc đánh giá tình hình thực hiện cơ chế tài chính mà đơn vị áp dụng, là căn cứ quan trọng giúp cơ quan cấp trên, cơ quan tài chính, cơ quan có thẩm quyền khác và lãnh đạo đơn vị đánh giá hiệu quả của các cơ chế, chính sách áp dụng cho đơn vị.</span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:16px;\">Nguyên tắc, yêu cầu lập và trình bày báo cáo quyết toán</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Nguyên tắc:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Việc lập báo cáo quyết toán phải căn cứ vào số liệu sau khi khóa sổ kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đối với báo cáo quyết toán ngân sách nhà nước:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Số quyết toán ngân sách nhà nước bao gồm số kinh phí đơn vị đã nhận và sử dụng từ nguồn ngân sách nhà nước cấp trong năm, bao gồm cả số liệu phát sinh trong thời gian chỉnh lý quyết toán theo quy định của pháp luật về ngân sách nhà nước.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Số liệu quyết toán ngân sách nhà nước của đơn vị phải được đối chiếu, có xác nhận của Kho bạc nhà nước nơi giao dịch.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">+ Số quyết toán chi ngân sách nhà nước là số đã thực chi, có đầy đủ hồ sơ chứng từ, riêng khoản chi thuộc nguồn phải ghi thu ghi chi ngân sách nhà nước thì chỉ quyết toán khi đã có thủ tục xác nhận ghi thu &ndash; ghi chi vào ngân sách nhà nước của cơ quan có thẩm quyền.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đối với báo cáo quyết toán nguồn khác: Số liệu quyết toán bao gồm số thu, chi từ nguồn khác không thuộc ngân sách nhà nước mà đơn vị đã thực hiện từ đầu năm đến hết ngày 31/12 hàng năm.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Yêu cầu:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Việc lập báo cáo quyết toán phải bảo đảm sự trung thực, khách quan, đầy đủ, kịp thời, phản ánh đúng tình hình thu, chi đối với từng nguồn kinh phí của đơn vị hành chính, sự nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo quyết toán phải được lập đúng nội dung, phương pháp theo quy định và được trình bày nhất quán giữa các kỳ báo cáo. Hệ thống chỉ tiêu của báo cáo quyết toán ngân sách nhà nước phải phù hợp và thống nhất với chỉ tiêu dự toán năm được cơ quan có thẩm quyền giao và mục lục ngân sách nhà nước, đảm bảo có thể so sánh được giữa số thực hiện với số dự toán và giữa các kỳ kế toán với nhau.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trường hợp báo cáo quyết toán ngân sách được lập có nội dung và phương pháp trình bày khác với các chỉ tiêu trong dự toán hoặc khác với báo cáo kỳ kế toán năm trước thì phải giải trình trong phần thuyết minh báo cáo quyết toán năm.</span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:16px;\">Kỳ báo cáo:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo quyết toán ngân sách nhà nước, báo cáo quyết toán nguồn khác được lập báo cáo theo kỳ kế toán năm.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Số liệu lập báo cáo quyết toán ngân sách nhà nước hàng năm là số liệu thu, chi thuộc năm ngân sách của đơn vị hành chính, sự nghiệp, được tính đến hết thời gian chỉnh lý quyết toán ngân sách nhà nước (ngày 31/01 năm sau) theo quy định của pháp luật về ngân sách nhà nước.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Số liệu lập báo cáo quyết toán là số thu, chi thuộc nguồn khác của đơn vị hành chính, sự nghiệp, được tính đến ngày kết thúc kỳ kế toán năm (ngày 31/12).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Trường hợp pháp luật có quy định lập thêm báo cáo quyết toán theo kỳ kế toán khác thì ngoài báo cáo quyết toán năm đơn vị phải lập cả báo cáo theo kỳ kế toán đó.</span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:16px;\">Trách nhiệm của các đơn vị trong việc lập, nộp báo cáo quyết toán</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Trách nhiệm của đơn vị:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị hành chính, sự nghiệp phải lập và nộp báo cáo quyết toán ngân sách nhà nước, ngoài các mẫu biểu báo cáo quyết toán ngân sách nhà nước quy định tại Thông tư này, còn phải lập các mẫu báo cáo phục vụ công tác quyết toán ngân sách nhà nước, các yêu cầu khác về quản lý ngân sách nhà nước theo quy định của pháp luật về ngân sách nhà nước.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị hành chính, sự nghiệp có phát sinh thu &ndash; chi nguồn khác không thuộc ngân sách nhà nước theo quy định phải quyết toán với cơ quan cấp trên, cơ quan tài chính và cơ quan có thẩm quyền khác thì đơn vị phải lập và nộp báo cáo quyết toán kinh phí nguồn khác theo quy định tại Thông tư này.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị hành chính, sự nghiệp là đơn vị cấp trên phải tổng hợp báo cáo quyết toán năm của các đơn vị cấp dưới trực thuộc theo quy định hiện hành.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Trách nhiệm của cơ quan Tài chính, Kho bạc Nhà nước:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Các cơ quan Tài chính, Kho bạc Nhà nước và các đơn vị khác có liên quan, có trách nhiệm thực hiện và phối hợp trong việc kiểm tra, đối chiếu, điều chỉnh, cung cấp và khai thác số liệu về kinh phí và sử dụng kinh phí, quản lý và sử dụng tài sản và các hoạt động khác có liên quan đến tình hình thu, chi ngân sách nhà nước và các hoạt động nghiệp vụ chuyên môn của đơn vị hành chính, sự nghiệp.</span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:16px;\">Nội dung, thời hạn nộp báo cáo quyết toán năm</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Nội dung:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị hành chính, sự nghiệp nộp báo cáo cho đơn vị dự toán cấp trên hoặc cơ quan tài chính cùng cấp (trong trường hợp không có đơn vị dự toán cấp trên), gồm:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Các báo cáo quyết toán năm quy định tại Thông tư này.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Các mẫu biểu đối chiếu dự toán ngân sách cấp theo hình thức rút dự toán tại Kho bạc nhà nước, tình hình tạm ứng và thanh toán tạm ứng kinh phí ngân sách tại Kho bạc nhà nước, bảng đối chiếu số dư tài khoản tiền gửi tại Kho bạc nhà nước theo quy định tại Thông tư 61/2014/TT-BTC ngày 12/5/2014 của Bộ Tài chính và các văn bản sửa đổi, bổ sung, thay thế có liên quan (nếu có).</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Báo cáo khác theo quy định của cơ quan có thẩm quyền phục vụ công tác quyết toán ngân sách nhà nước.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Thời hạn nộp Báo cáo quyết toán năm của đơn vị hành chính, sự nghiệp có sử dụng kinh phí ngân sách nhà nước thực hiện theo quy định của Luật ngân sách nhà nước và các văn bản hướng dẫn pháp luật về ngân sách nhà nước.</span></li>\r\n	<li><span style=\"font-size:16px;\">Danh mục báo cáo, mẫu báo cáo, giải thích phương pháp lập báo cáo quyết toán ngân sách nêu tại Phụ lục số 04, kèm theo Thông tư này.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 7. Báo cáo tài chính</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Đối tượng lập báo cáo tài chính</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Sau khi kết thúc kỳ kế toán năm, các đơn vị hành chính, sự nghiệp phải khóa sổ và lập báo cáo tài chính để gửi cơ quan có thẩm quyền và các đơn vị có liên quan theo quy định.</span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:16px;\">Mục đích của báo cáo tài chính</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo tài chính dùng để cung cấp thông tin về tình hình tài chính, kết quả hoạt động tài chính và các luồng tiền từ hoạt động của đơn vị, cung cấp cho những người có liên quan để xem xét và đưa ra các quyết định về các hoạt động tài chính, ngân sách của đơn vị. Thông tin báo cáo tài chính giúp cho việc nâng cao trách nhiệm giải trình của đơn vị về việc tiếp nhận và sử dụng các nguồn lực theo quy định của pháp luật.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Thông tin báo cáo tài chính của đơn vị hành chính, sự nghiệp là thông tin cơ sở để hợp nhất báo cáo tài chính của đơn vị cấp trên.</span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:16px;\">Nguyên tắc, yêu cầu lập báo cáo tài chính</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Nguyên tắc:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Việc lập báo cáo tài chính phải được căn cứ vào số liệu kế toán sau khi khóa sổ kế toán. Báo cáo tài chính phải được lập đúng nguyên tắc, nội dung, phương pháp theo quy định và được trình bày nhất quán giữa các kỳ kế toán, trường hợp báo cáo tài chính trình bày khác nhau giữa các kỳ kế toán thì phải thuyết minh rõ lý do.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo tài chính phải có chữ ký của người lập, kế toán trưởng và thủ trưởng của đơn vị kế toán. Người ký báo cáo tài chính phải chịu trách nhiệm về nội dung của báo cáo.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Yêu cầu:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo tài chính phải được phản ánh một cách trung thực, khách quan về nội dung và giá trị các chỉ tiêu báo cáo; trình bày theo một cấu trúc chặt chẽ, có hệ thống về tình hình tài chính, kết quả hoạt động và các luồng tiền từ hoạt động của đơn vị.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo tài chính phải được lập kịp thời, đúng thời gian quy định đối với từng loại hình đơn vị, trình bày rõ ràng, dễ hiểu, chính xác thông tin, số liệu kế toán.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">Thông tin, số liệu báo cáo phải được phản ánh liên tục, số liệu của kỳ này phải kế tiếp số liệu của kỳ trước.</span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:16px;\">Kỳ lập báo cáo</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị phải lập báo cáo tài chính vào cuối kỳ kế toán năm theo quy định của Luật Kế toán.</span></p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:16px;\">Trách nhiệm của các đơn vị trong việc lập báo cáo tài chính</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Các đơn vị hành chính, sự nghiệp phải lập báo cáo tài chính năm theo mẫu biểu ban hành tại Thông tư này; trường hợp đơn vị hành chính, sự nghiệp có hoạt động đặc thù được trình bày báo cáo theo chế độ kế toán do Bộ Tài chính ban hành cụ thể hoặc đồng ý chấp thuận.</span></li>\r\n	<li><span style=\"font-size:16px;\">b) Các đơn vị hành chính, sự nghiệp lập báo cáo tài chính theo biểu mẫu đầy đủ, trừ các đơn vị kế toán dưới đây có thể lựa chọn để lập báo cáo tài chính đơn giản:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">(1) Đối với cơ quan nhà nước thỏa mãn các điều kiện:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Phòng, cơ quan tương đương phòng thuộc Ủy ban nhân dân cấp huyện, chỉ được giao dự toán chi ngân sách nhà nước chi thường xuyên;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Không được giao dự toán chi ngân sách nhà nước chi đầu tư phát triển, chi từ vốn ngoài nước; không được giao dự toán thu, chi phí hoặc lệ phí;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Không có cơ quan, đơn vị trực thuộc.</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">(2) Đối với đơn vị sự nghiệp công lập thỏa mãn các điều kiện:</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Đơn vị sự nghiệp công lập được cấp có thẩm quyền phân loại là đơn vị sự nghiệp do ngân sách nhà nước đảm bảo toàn bộ chi thường xuyên (theo chức năng, nhiệm vụ được cấp có thẩm quyền giao, không có nguồn thu, hoặc nguồn thu thấp);</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Không được bố trí dự toán chi ngân sách nhà nước chi đầu tư phát triển, chi từ vốn ngoài nước; không được giao dự toán thu, chi phí hoặc lệ phí;</span></p>\r\n\r\n<p><span style=\"font-size:16px;\">&ndash; Không có đơn vị trực thuộc.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">c) Đơn vị kế toán cấp trên có các đơn vị cấp dưới không phải là đơn vị kế toán phải lập báo cáo tài chính tổng hợp, bao gồm số liệu của đơn vị mình và toàn bộ thông tin tài chính của các đơn vị cấp dưới, đảm bảo đã loại trừ tất cả số liệu phát sinh từ các giao dịch nội bộ giữa đơn vị cấp trên và đơn vị cấp dưới và giữa các đơn vị cấp dưới với nhau (các đơn vị cấp dưới trong quan hệ thanh toán nội bộ này là các đơn vị hạch toán phụ thuộc và chỉ lập báo cáo tài chính gửi cho cơ quan cấp trên để tổng hợp (hợp nhất) số liệu, không phải gửi báo cáo tài chính cho các cơ quan bên ngoài).</span></li>\r\n	<li><span style=\"font-size:16px;\">Nội dung và thời hạn nộp báo cáo tài chính</span></li>\r\n	<li><span style=\"font-size:16px;\">a) Nội dung:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Đơn vị hành chính, sự nghiệp nộp báo cáo tài chính cho đơn vị kế toán cấp trên hoặc cơ quan nhà nước có thẩm quyền các báo cáo tài chính năm theo quy định tại Thông tư này.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">b) Thời hạn nộp báo cáo tài chính:</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo tài chính năm của đơn vị hành chính, sự nghiệp phải được nộp cho cơ quan nhà nước có thẩm quyền hoặc đơn vị cấp trên trong thời hạn 90 ngày, kể từ ngày kết thúc kỳ kế toán năm theo quy định của pháp luật.</span></p>\r\n\r\n<ol start=\"7\">\r\n	<li><span style=\"font-size:16px;\">Công khai báo cáo tài chính</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\">Báo cáo tài chính được công khai theo quy định của pháp luật về kế toán và các văn bản có liên quan.</span></p>\r\n\r\n<ol start=\"8\">\r\n	<li><span style=\"font-size:16px;\">Danh mục báo cáo, mẫu báo cáo, giải thích phương pháp lập báo cáo tài chính đơn giản, báo cáo tài chính đầy đủ nêu tại phụ lục số 04, kèm theo Thông tư này.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Chương III:&nbsp;</strong><strong>TỔ CHỨC THỰC HIỆN</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 8. Hiệu lực thi hành</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Thông tư này có hiệu lực sau 45 ngày, kể từ ngày ký và áp dụng từ ngày 01/01/2018.</span></li>\r\n	<li><span style=\"font-size:16px;\">Thông tư này thay thế Quyết định số 19/2006/QĐ-BTC ngày 30/3/2006 của Bộ Tài chính về việc ban hành chế độ kế toán Hành chính sự nghiệp và Thông tư số 185/2010/TT-BTC ngày 15/11/2010 của Bộ Tài chính hướng dẫn sửa đổi, bổ sung Chế độ kế toán Hành chính sự nghiệp ban hành kèm theo Quyết định số 19/2006/QĐ-BTC.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:16px;\"><strong>Điều 9. Tổ chức thực hiện</strong></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px;\">Các Bộ, ngành, Ủy ban nhân dân tỉnh, thành phố trực thuộc Trung ương chịu trách nhiệm chỉ đạo, triển khai thực hiện Thông tư này tới các đơn vị hành chính sự nghiệp thuộc phạm vi phụ trách hoặc quản lý.</span></li>\r\n	<li><span style=\"font-size:16px;\">Vụ trưởng Vụ Chế độ kế toán và kiểm toán, Vụ trưởng Vụ Hành chính sự nghiệp, Vụ trưởng Vụ Ngân sách Nhà nước, Chánh văn phòng Bộ và Thủ trưởng các đơn vị liên quan thuộc Bộ Tài chính chịu trách nhiệm phổ biến, hướng dẫn, kiểm tra và thi hành Thông tư này./.</span></li>\r\n</ol>', NULL, 1, 1, NULL, 1, '2018-11-26 08:15:16', '2018-11-26 08:15:16', NULL);
