(32, 'Bơm Lưu Lượng (hàng xịn)', 'bom-luu-luong-hang-xin', 1, 'images/uploads/images/techrumi/techrumi_6.jpg;images/uploads/images/techrumi/techrumi_6.jpg', '', '<p>\r\n	Bơm Lưu Lượng (hàng xịn)\r\n</p>', '<p>\r\n	<span style=\"font-size: large;\"><strong>ÁP DỤNG:</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm lưu lượng tải ống lớn, máy chạy êm vì bơm dùng cánh thau, thích hợp dùng cho tươi tưới và thủy lợi trong gia dụng và công nghiệp.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Mẫu sản phẩm dây đồng cánh đồng, bơm lưu lượng được thiết kế chuyên dùng cho tưới cây, trồng trọc.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: large;\"><strong>ĐẶC TÍNH:</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Đầu bơm đúc bằng gang</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Dây điện quấn bằng đồng</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Cánh bơm đồng thau</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Trục bơm bằng thép 45#</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Phốt dùng bằng chất liệu Graphite to ceramic hoặc graphite to SIS</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bạc đạn NSK hoặc HCH</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Độ bảo vệ IP44</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Ống dân nước từ 60*60, 90*90, 114*114</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>XUẤT SỨ:&nbsp;CHINA</strong></span>\r\n</p>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>CÔNG NGHỆ: ITALY</strong></span>\r\n</p>', NULL, 'Bơm Lưu Lượng (hàng xịn)', 'Bơm Lưu Lượng (hàng xịn)', 'Bơm Lưu Lượng (hàng xịn)', '0', 0, '0', 0, 1, 1, 65, '2018-06-04 04:15:42', '2018-06-06 07:32:49'),
(33, 'Bơm Chìm Nhôm Tĩnh Điện', 'bom-chim-nhom-tinh-dien', 1, 'images/uploads/images/techrumi/techrumi_7.jpg;images/uploads/images/techrumi/techrumi_7.jpg', '', '<p>\r\n	Bơm Chìm Nhôm Tĩnh Điện\r\n</p>', '<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: large;\"><strong>ÁP DỤNG:</strong></span>\r\n</p>\r\n\r\n<ul style=\"text-align: justify; margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm&nbsp;chìm được sản xuất bằng dây đồng, thân nhôm tĩnh điện, dùng trong môi trường cấp thoát nước và nước thải nhẹ, có phao tự ngắt khi hết nước.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Mẫu sản phẩm dây đồng cánh phíp hoặc gang, bơm thả chìm nhôm tĩnh điện được thiết kế chuyên dùng cho công nghiệp và nông nghiệp, khách sạn, công viên, nhà máy vv.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: large;\"><strong>ĐẶC TÍNH:</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size: medium;\">Đầu bơm nhôm tĩnh điện</span>\r\n	</li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size: medium;\">Thân bằng nhôm tĩnh điện</span>\r\n	</li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size: medium;\">Dây điện quấn bằng đồng</span>\r\n	</li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size: medium;\">Cánh bơm bằng phíp hoặc gang</span>\r\n	</li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size: medium;\">Trục bơm bằng inox</span>\r\n	</li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size: medium;\">Phốt dùng bằng chất liệu Graphite to ceramic hoặc graphite to SIS</span>\r\n	</li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size: medium;\">Bạc đạn NSK</span>\r\n	</li>\r\n	<li style=\"text-align: justify;\">\r\n		<span style=\"font-size: medium;\">Độ bảo vệ IP68</span>\r\n	</li>\r\n	<li style=