(53, 'Bơm CM Công Suất Lớn (cáng gang)', 'bom-cm-cong-suat-lon-cang-gang', 1, 'images/uploads/images/mepcatocn/mapcato_cn_1.jpg;images/uploads/images/mepcatocn/mapcato_cn_1.jpg', '', '<p>\r\n	Bơm CM Công Suất Lớn (cáng gang)\r\n</p>', '<p>\r\n	<span style=\"font-size: large;\"><strong>ÁP DỤNG:</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm cung cấp nước trong ngành Công nghiệp và Nông nghiệp.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm hệ thống xử lý nước sạch cho các khu công nghiệp và chung cư.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm hệ thống tưới tiêu thủy lợi.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm cứu hoả, phòng cháy chữa cháy.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm nước sinh hoạt cho nhà cao tầng.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: large;\"><strong>ĐẶC TÍNH:</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Đầu bơm đúc bằng gang</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Dây điện quấn bằng đồng</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Cánh bơm đồng thau</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Trục bơm bằng inox 304 hoặc 316</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Phốt Italy</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bạc đạn Nhật Nachi</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>XUẤT XỨ: LIÊN DOANH </strong></span>\r\n</p>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>CÔNG NGHỆ: ITALY</strong></span>\r\n</p>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong><img alt=\"\" src=\"http://localhost:8080/maybomnuoc/images/uploads/images/tintuc/BOSINI.jpg\" style=\"width: 100%; height: 100%;\"></strong></span>