(58, 'Bơm Công Nghiệp Trục Ngang Inox ZS', 'bom-cong-nghiep-truc-ngang-inox-zs', 1, 'images/uploads/images/cnp/cnp_5.jpg;images/uploads/images/cnp/cnp_5.jpg', '', '<p>\r\n	Bơm Công Nghiệp Trục Ngang Inox ZS\r\n</p>', '<p>\r\n	<span style=\"font-size: medium;\"><strong>GIỚI THIỆU</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm ly tâm đơn tầng trục ngang được làm bằng kỹ thuật tiên tiến như hàn dập các tấm thép kháng mòn. Đây là dòng bơm ly tâm thế hệ mới khởi đầu ở Trung Quốc và có thể thay thế loại bơm IS truyền thống và các bơm kháng mòn thông thường. Nó có ngoại hình đẹp, nhẹ, gọn, hiệu suất cao, tiết kiệm điện, bền , kháng mòn, ít gây tiếng ồn &hellip;..</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>ÁP DỤNG</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm ly tâm đơn tầng trục ngang ZS là loại đa năng, có dải áp dụng rộng. Nó có thể chuyển tải các chất lỏng khác nhau bao gồm cả nước, chất lỏng công nghiệp và thích hợp cho nhiệt độ, lưu lượng và cột áp khác nhau. Áp dụng tiêu biểu chính bao gồm các lãnh vực sau :</span>\r\n	</li>\r\n</ul>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">- Cung cấp nước, lọc nước, tăng áp lực nước</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">- Tăng áp lực trong công nghiệp : hệ thống làm sạch</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">- Chuyển chất lỏng công nghiệp : cung cấp nước cho nồi hơi, hệ thống condensed, hệ thống làm mát, điều hoà nhiệt độ, chuyển các chất lỏng chứa acid, alkali nhẹ.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">- Xử lý nước : hệ thống nước chưng cất, hồ bơi &hellip;..</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">- Tưới đất nông nghiệp, công nghiệp hoá chất, thuốc men, vệ sinh&nbsp; &hellip;&hellip;</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>CÁC YÊU CẦU LẮP ĐẶT</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Bơm ly tâm đơn tầng trục ngang ZS nối trực tiếp qua trục bao gồm bơm, trục bơm và motor tiêu chuẩn.</span>\r\n	</li>\r\n</ul>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Máy bơm phải được lắp đặt ở nơi thông thoáng, không đông lạnh</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Việc lắp đặt phải đảm bảo máy bơm không bị ảnh hưởng bởi sức căng của đường ống.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Nếu lắp đặt ngoài trời, máy bơm phải có đồ che chắn để tránh nước mưa, sương chạm vào motor, dây điện .</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Để dễ dàng kiểm tra và bảo trì, phải có khoảng trống xung quanh nhóm máy bơm.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Thiết bị điện phải đảm bảo máy bơm không bị hư hại bởi mất pha, điện áp không ổn định, hở mạch và quá tải.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Máy bơm phải được đặt trên một bệ đỡ nằm ngang. Hướng nằm ngang là cửa vào của bơm và hướng thẳng đứng là cửa ra của bơm.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>ĐIỀU KIỆN HOẠT ĐỘNG</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Các chất lỏng sạch, không gây cháy, nổ, không chứa các hạt rắn và chất xơ sợi.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Nhiệt độ chất lỏng : - 20<sup>0</sup>C đến + 100<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Nhiệt độ môi trường : lên đến + 40<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Độ cao : lên đến 1000 m</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Áp lực tối đa của hệ thống là 10 bar.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>MOTOR</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">TEFC motor, 2 cực</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Lớp bảo vệ : IP 55</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Lớp cách điện : class F</span>\r\n	</li>\r\n	<li>\r\n		<span><span><span style=\"font-size: medium;\">Điện áp tiêu chuẩn : 50 Hz&nbsp; 1 x 220V</span><br>\r\n		3 x 380V</span></span>\r\n	</li>\r\n</ul>', NULL, 'Bơm Công Nghiệp Trục Ngang Inox ZS', 'Bơm Công Nghiệp Trục Ngang Inox ZS', 'Bơm Công Nghiệp Trục Ngang Inox ZS', '0', 0, '0', 0, 1, 1, 69, '2018-06-04 04:15:42', '2018-06-06 08:32:59'),
(59, 'Bơm Quả Tiễn Inox 4 SJ', 'bom-qua-tien-inox-4-sj', 1, 'images/uploads/images/cnp/cnp_6.jpg;images/uploads/images/cnp/cnp_6.jpg', '', '<p>\r\n	Bơm Quả Tiễn Inox 4 SJ\r\n</p>', '<p>\r\n	<span style=\"font-size: medium;\"><strong>ÁP DỤNG</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Hệ thống tưới&nbsp; và cung cấp nước.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Nước ngầm</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Tăng áp</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Các sử dụng công nghiệp khác</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>ĐIỀU KIỆN HOẠT ĐỘNG</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Các chất lỏng không ăn mòn , sạch, không chứa các hạt rắn và chất xơ sợi</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Nhiệt độ chất lỏng tối đa : 35<sup>0</sup>C</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Áp lực làm việc tối đa : 30 bar</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Độ PH : 6.5 đến 8.5</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Chiều sâu nhúng bơm trong nước tối đa : &le; 50 m</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>PHẠM VI ÁP DỤNG</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">Serie này được áp dụng cho việc luân chuyển và tạo áp lực cho nước sạch và nước đã làm mềm.</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">Serie này được làm bằng thép không gỉ và&nbsp; có tính kháng mòn tốt để tải nước lạnh chứa một lượng nhỏ oxides. Nó cũng được dùng để tải nước chứa một ít alkaline và nước được lược khoáng chất. Bơm được làm bằng thép không gỉ 316 và FPM được sử dụng để tải các chất lỏng có độ ăn mòn cao.</span>\r\n	</li>\r\n</ul>\r\n\r\n<p>\r\n	<span style=\"font-size: medium;\"><strong>NEMA MOTOR</strong></span>\r\n</p>\r\n\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		<span style=\"font-size: medium;\">4&rdquo; motor, 1 pha, công suất tối đa lên đến 1.5 kW</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">4&rdquo; motor, 3 pha, công suất tối đa lên đến 7.5 kW</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"font-size: medium;\">6&rdquo; motor, 3 pha, công suất từ 4.0 kW đến 37 kW</span>\r\n	</li>\r\n</ul>', NULL, 'Bơm Quả Tiễn Inox 4 SJ', 'Bơm Quả Tiễn Inox 4 SJ', 'Bơm Quả Tiễn Inox 4 SJ', '0', 0, '0', 0, 1, 1, 69, '2018-06-04 04:15:42', '2018-06-06 08:33:40'),
(60, 'Bơm Nước Thải WQ', 'bom-nuoc-thai-wq', 1, 'images/uploads/images/cnp/cnp_7.jpg;images/uploads/images/cnp/cnp_7.jpg', '', '<p>\r\n	Bơm Nước Thải WQ\r\n</p>', '<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\"><strong>*</strong>WQ, JYWQ cải tiến loại bơm tương tự ở trong nước và hải ngoại. Nó tối ưu hoá kỹ thuật phốt, thuỷ lực, cấu trúc, bảo vệ. Việc đổi mới làm bơm được tin cậy hơn, an toàn hơn. Dễ sử dụng, ít hư hỏng, công suất thoát nước tốt. Cả series bơm dễ dàng cho sự chọn lựa.</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">*Công suất tối ưu hoá, hai cánh loại channel, sử dụng từ 2 đến 3 phốt cơ khí, phốt cơ khí được bôi trơn, máy bơm được làm mát tốt hơn, chạy ổn định mà không bị nghẹt.</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">*Sử dụng motor có cấp IPX8, làm mát tốt, nhiệt độ gia tăng chậm hơn motor bình thường, bền, class F, ít hư.</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\"><strong>Áp dụng</strong></span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\"><strong>*</strong><em>Cho building, bệnh viện, khu vực dân cư,các dự án đô thị, đường xá,nước thải nhà máy, xử lý nước thải nhỏ..vv.. Chất lỏng được bơm là nước mưa, nước thải có lẫn các hạt rắn, các xơ sợi dài.</em></span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\"><strong>Điều kiện hoạt động</strong></span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">*Nguồn điện cung cấp : 50Hz, 3 pha, 380V</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">*Nhiệt độ chất lỏng nhỏ hơn 40<sup>0</sup>C, PH từ 4 &ndash; 10, tỷ trọng nhỏ hơn 1200kg/m<sup>3</sup>, tỉ lệ chất lỏng và hạt rắn nhỏ hơn 2%.</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">*Máy bơm thì không thích hợp cho chất lỏng ăn mòn mạnh hoặc chất rắn.</span>\r\n</p>', NULL, 'Bơm Nước Thải WQ', 'Bơm Nước Thải WQ', 'Bơm Nước Thải WQ', '0', 0, '0', 0, 1, 1, 69, '2018-06-04 04:15:42', '2018-06-06 08:34:34'),
(61, 'Bơm Đường Ống TD', 'bom-duong-ong-td', 1, 'images/uploads/images/cnp/cnp_8.jpg;images/uploads/images/cnp/cnp_8.jpg', '', '<p>\r\n	Bơm Đường Ống TD\r\n</p>', '<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\"><strong>GIỚI THIỆU</strong>&nbsp;</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">Bơm TD là loại bơm ly tâm, một tầng, in-line ( cửa vào và cửa ra nằm thẳng hàng ), trang bị với motor chuẩn và phốt cơ khí. So sánh với các bơm khác có cấu trúc tương tự, bơm này tiếp cận kém với các chất lỏng không sạch.</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\"><strong>ÁP DỤNG</strong></span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">Máy bơm được sử dụng cho chất lỏng sạch, không ăn mòn vật liệu, không gây cháy nổ, không chứa các chất rắn, chất xơ sợi có thể gây hại cho máy bơm .&nbsp; Nếu độ nhớt hoặc độ đậm đặc của chất lỏng vượt mức yêu cầu, công suất của bơm sẽ giảm và lượng tiêu thụ điện sẽ tăng.</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">Nhiệt độ chất lỏng : - 15<sup>0</sup>C &ndash; 110<sup>0</sup>C.</span>\r\n</p>\r\n\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-size: medium;\">Áp lực tối đa : loại thông thường : 12 bar, loại đặc biệt : 16 bar.</span>\r\n</p>', NULL, 'Bơm Đường Ống TD', 'Bơm Đường Ống TD', 'Bơm Đường Ống TD', '0', 0, '0', 0, 1, 1, 69, '2018-06-04 04:15:42', '2018-06-06 08:35:07');
