(63, 'Bơm Chìm Nhựa BPS100', 'bom-chim-nhua-bps100', 1, 'images/uploads/images/apppump/app_pump_2.jpg;images/uploads/images/apppump/app_pump_2.jpg', '', '<p>\r\n	Bơm Chìm Nhựa BPS100\r\n</p>', '<table border=\"0\" cellpadding=\"2\" cellspacing=\"1\" style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\">\r\n				<p>\r\n					<em>* <strong><span style=\"text-decoration: underline;\">Các đặc điểm kỹ thuật</span></strong>:</em>\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gắn liền vào bộ bảo vệ motor khi nhiệt độ quá nóng.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Vỏ bọc motor và cánh quạt bằng sợi thủy tinh dẻo dai cao không rỉ sét.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trong bộ bảo vệ motor khi nhiệt độ quá nóng.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Giá thành thấp, thiết kế motor PSC bằng thép, vị trí phót thấp dễ bị nước bao quanh để giảm nhiệt độ và tăng thêm tính bền lâu của máy.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mức độ bơm nước thặng dư thấp nhất 3mm.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hiệu suất cao đối với chi phí có hiệu quả hoạt động bơm.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; BPS-100A trang bị với công tắc phao, thích hợp với hố hầm nhỏ.\r\n				</p>\r\n\r\n				<p>\r\n					<em>* <strong><span style=\"text-decoration: underline;\">Các ứng dụng</span></strong>:</em>\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mức độ bơm nước có thể đạt đến tối tiểu 3mm, dễ dàng rửa bồn chứa và bể cá.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thích hợp dùng trong hố hầm nước thải, vườn nghệ thuật thác nước, vườn phun.\r\n				</p>\r\n\r\n				<p>\r\n					&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Phục hồi nước bồn tắm, phun tưới vườn và rửa xe.\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				OUTPUT\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				1/6 HP , 100 W\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				OUTLET\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				25 mm , 1\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				RATED\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				H 4 (M), Q 35 (l/m)\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				MAXIMUM\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				H 4 (M), Q 70 (l/m)\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				DIMENSION\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				155 L X 155 W X 240 H (mm)\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				WEIGHT\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				2.9 KGS\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', NULL, 'Bơm Chìm Nhựa BPS100', 'Bơm Chìm Nhựa BPS100', 'Bơm Chìm Nhựa BPS100', '0', 0, '0', 0, 1, 1, 70, '2018-06-04 04:15:42', '2018-06-06 09:01:03'),
(64, 'Bơm Chìm Nhựa BPS200', 'bom-chim-nhua-bps200', 1, 'images/uploads/images/apppump/app_pump_3.jpg;images/uploads/images/apppump/app_pump_3.jpg', '', '<p>\r\n	Bơm Chìm Nhựa BPS200\r\n</p>', '<table border=\"0\" cellpadding=\"2\" cellspacing=\"1\" style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\">\r\n				<p>\r\n					<em>* <strong><span style=\"text-decoration: underline;\">Các đặc điểm kỹ thuật</span></strong>:</em>\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Vỏ bọc motor và cánh quạt bằng chất xơ thủy tinh nhựa không rỉ sét.<br>\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đông cơ hiệu suất cao, bảo vệ IP68, loại F cách điện và bảo vệ motor khi nhiệt độ quá nóng.<br>\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đôi phót có tính đáng tin cậy cao để phòng chống motor bị ẩm ướt.<br>\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nhẹ cân và cấu trúc đơn giản.<br>\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thiết kế tráng kiện đối với thời gian phục vụ lâu dài.<br>\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hiệu suất cao đối với hiệu quả về hoạt động bơm và giá thành thấp.<br>\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trang bị với công tắc phao hoạt động tự động quá.\r\n				</p>\r\n\r\n				<p>\r\n					<em>* <strong><span style=\"text-decoration: underline;\">Các ứng dụng</span></strong>:</em>\r\n				</p>\r\n\r\n				<p>\r\n					&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Hố hầm, phun sương hoa viên, thác nước , nhà vệ sinh, nhà giặt ủi, nhà rửa xe, tưới tiêu và nhiều dạng bơm nước di dời khác.\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				OUTPUT\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				1/3 - 1/2 HP , 250 - 400 W\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				OUTLET\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				40 - 50 mm , 1-1/2\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				RATED\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				H 5 - 6 (M), Q 80 - 120 (l/m)\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				MAXIMUM\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				H 7 - 9 (M), Q 170 - 220(l/m)\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				DIMENSION\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				196 L X 196 W X 365 H(mm)\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				WEIGHT\r\n			</td>\r\n			<td class=\"lineDotB2\">\r\n				7 - 8 KGS\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', NULL, 'Bơm Chìm Nhựa BPS200', 'Bơm Chìm Nhựa BPS200', 'Bơm Chìm Nhựa BPS200', '0', 0, '0', 0, 1, 1, 70, '2018-06-04 04:15:42', '2018-06-06 09:01:30'),
(65, 'BAS', 'bas', 1, 'images/uploads/images/apppump/app_pump_4.jpg;images/uploads/images/apppump/app_pump_4.jpg', '', '<p>\r\n	BAS\r\n</p>', '<table border=\"0\" cellpadding=\"2\" cellspacing=\"1\" style=\"width: 100%;\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\">\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					<strong><em>* <span style=\"text-decoration: underline;\">Các đặc điểm kỹ thuật:</span></em></strong>\r\n				</p>\r\n\r\n				<p style=\"margin-left: 36.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cánh quạt hình xoáy không gây trở ngại.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 36.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bộ bảo vệ motor khi nhiệt độ quá nóng.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 36.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Vỏ bọc được làm bằng Inox.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 36.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Căn cứ theo mã lực khác nhau, vật rắn có thể thông qua đường kính với phạm vi 5 ~ 10 mm.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 36.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đặc tính tốt, giá thành thấp, hiệu suất cao.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					<strong><em>* <span style=\"text-decoration: underline;\">Các ứng dụng:</span></em></strong>\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp; Tuần hoàn ao hoặc hồ cá và thải ra nước mưa.\r\n				</p>\r\n\r\n				<p style=\"margin-left: 18.0pt;\">\r\n					-&nbsp;&nbsp;&nbsp; Thác nước, suối nước, vườn phun, hố hầm ứ nước thải, tống nước thải.\r\n				</p>\r\n\r\n				<p>\r\n					&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp;&nbsp;&nbsp; Nhiều loại công trình xử lý nước.\r\n				</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td align=\"right\" bgcolor=\"#139CB9\" class=\"T10wB\" width=\"25%\">\r\n				OUTPUT\r\n			</td>\r\n